	text-align: center;
	white-space: nowrap;
	line-height: 1;
	height: 1em;
	vertical-align: text-bottom;
}

.rt {
	display: table-header-group;
	font-size: .6em;
	line-height: 1.1;
	text-align: center;
	white-space: nowrap;
}

.keycap {
	border: 1px solid;
	border-bottom-width: 2px;
	-moz-border-radius: 3px;
	-webkit-border-radius: 3px;
	border-radius: 3px;
	background-color: #f9f9f9;
	font-family: inherit;
	font-size: .85em;
	white-space: nowrap;
	border-color: #ddd #bbb #bbb #ddd;
	padding: 1px 3px;
}

.tags {
	display: inline-block;
	height: 13px;
	line-height: 13px;
	font-size: 11px;
	background: #666;
	color: #fff;
	text-decoration: none;
	-moz-border-radius-bottomright: 4px;
	-webkit-border-bottom-right-radius: 4px;
	border-bottom-right-radius: 4px;
	-moz-border-radius-topright: 4px;
	-webkit-border-top-right-radius: 4px;
	border-top-right-radius: 4px;
	margin: 0 0 0 5px;
	padding: 3px 5px 3px 0;
}

.tags:before {
	content: "";
	float: left;
	position: relative;
	top: -3px;
	left: -10px;
	width: 0;
	height: 0;
	border-color: transparent #666 transparent transparent;
	border-style: solid;
	border-width: 8px 8px 8px 0;
	padding: 0 1px 3px;
}

.tags:after {
	content: "";
	position: relative;
	top: 4.5px;
	left: -8px;
	float: left;
	width: 4px;
	height: 4px;
	-moz-border-radius: 2px;
	-webkit-border-radius: 2px;
	border-radius: 2px;
	background: #fff;
	-moz-box-shadow: -1px -1px 2px #004977;
	-webkit-box-shadow: -1px -1px 2px #333;
	box-shadow: -1px -1px 2px #333;
}

.bblock:hover {
	background-color: #000;
	color: #060;
	text-decoration: none;
}

.dblock:hover {
	background-color: transparent;
	text-decoration: none;
}

.emph {
	text-emphasis-style: dot;
	-webkit-text-emphasis-style: dot;
}

.page-source {
	word-break: break-all;
}

#edit-page-comments {
	width: 100%;
}

.visuallyhidden {
	border: 0;
	clip: rect(0000);
	height: 1px;
	overflow: hidden;
	position: absolute;
	width: 1px;
	white-space: nowrap;
	margin: -1px;
	padding: 0;
}

.visuallyhidden.focusable:active,
.visuallyhidden.focusable:focus {
	clip: auto;
	height: auto;
	overflow: visible;
	position: static;
	width: auto;
	white-space: inherit;
	margin: 0;
}

.invisible {
	visibility: hidden;
}

.clearfix:before,
.clearfix:after {
	content: " ";
	display: table;
}

.clearfix:after {
	clear: both;
}

::-moz-selection,
::selection {
	background: #fcb3b3;
	text-shadow: none;
}

div#container-wrap {
	background: none;
}

text-area,
.yui-navset li {
	line-height: normal;
}

.title.modal-header,
.owindow {
	background-color: #fff;
}

.signature,
.hidden {
	display: none !important;
}

.bblock,
.dblock {
	color: #000;
	background-color: #000;
	transition: 2s;
	text-decoration: none;
}

#page-content div,
#page-content div table {
	max-width: 100%;
}

/* 1% Height Firefox Fixes */
.footnotes-footer,
.page-options-bottom,
.owindow .title,
.math-equation,
.bibitem,
.thread-container .post .long .options {
	height: auto !important;
}

/* Hiding Hidden Tags */
#main-content .page-tags a[href^="/system:page-tags/tag/_"] {
	display: none
}
  
@media only screen and (max-width: 768px) {
	#page-content {
		font-size: .9em;
	}

	#main-content {
		max-width: 90vw;
		-webkit-transition: .5s ease-in-out .1s;
		-moz-transition: .5s ease-in-out .1s;
		-ms-transition: .5s ease-in-out .1s;
		-o-transition: .5s ease-in-out .1s;
		transition: .5s ease-in-out .1s;
		margin-left: 5%;
		margin-right: 5%;
		padding: 0;
	}

	.mobile-top-bar > div > p {
		display: block;
	}

	.page-options-bottom {
		height: initial;
	}

	.page-options-bottom a {
		padding: 0 4px;
	}

	blockquote {
		margin: 1em 0;
	}

	.license-area {
		font-size: .8em;
	}

	.page-history tbody tr td:last-child {
		width: 35%;
		font-size: 1.5rem;
	}

	.owindow {
		min-width: 80%;
		max-width: 99%;
	}

	.modal-body .table,
	.modal-body .table ~ div {
		float: left;
	}

	.owindow .button-bar {
		float: right;
	}

	.owindow div .btn-primary {
		width: 100%;
		float: left;
	}

	.yui-navset {
		z-index: 1;
	}

	table.form td,
	table.form th {
		float: left;
		padding: 0;
	}

	td.name {
		width: 15em;
	}

	#edit-page-title {
		font-family: var(--title-font);
		max-width: 90%;
	}

	.content-panel.left-column,
	.content-panel.right-column {
		width: 99%;
		float: left;
	}

	#page-content div,
	#page-content div table {
		clear: both;
	}

	#page-content div.title {
		font-family: var(--title-font);
		word-break: keep-all;
	}

	#content-wrap {
		margin: 2.875em auto 0;
	}

	#footer {
		width: auto;
		bottom: 1rem;
	}

	#license-area {
		font-size: 1em;
		bottom: -1.5em;
		width: 90%;
		margin: 0 auto;
		line-height: 1;
		height: 2rem;
	}

	#search-top-box-input,
	#navi-bar,
	#navi-bar-shadow {
		display: none;
	}

	#recent-posts-category,
	.owindow div .btn-primary ~ div {
		width: 100%;
	}
}

@media print {

	*,
	:before,
	:after {
		background: transparent !important;
		color: #000 !important;
		-webkit-box-shadow: none !important;
		box-shadow: none !important;
		text-shadow: none !important;
	}

	a,
	a:visited {
		text-decoration: underline;
	}

	a[href]:after {
		content: " ("attr(href) ")";
	}

	abbr[title]:after {
		content: " ("attr(title) ")";
	}

	a[href^="#"]:after,
	a[href^="javascript:"]:after {
		content: "";
	}

	pre {
		white-space: pre-wrap !important;
	}

	pre,
	blockquote {
		border: 1px solid #999;
		page-break-inside: avoid;
	}

	thead {
		display: table-header-group;
	}

	tr,
	img {
		page-break-inside: avoid;
	}

	p,
	h2,
	h3 {
		orphans: 3;
		widows: 3;
	}

	h2,
	h3 {
		page-break-after: avoid;
	}
}
	
/* Sliding Sidebar Beta */
@media only screen and (min-width: 769px) {
	#side-bar::after {
		content: "\25c0 \25c0 \25c0";
		font-family: "Lato", san-serif;
		font-size: 1em;
		color: rgba(var(--swatch-menutxt-light-color));
		font-weight: 700;
		position: fixed;
		display: flex;
		align-items: center;
		justify-content: center;
		width: 1rem;
		height: 15rem;
		border-radius: 0 0.5em 0.5em 0;
		background-color: rgba(var(--swatch-topmenu-bg-color));
		top: calc(50vh - 7.5rem);
		left: calc(((var(--base-font-size) * .5625) * 0.5) + var(--scrollbar-width));
		transition: all 300ms cubic-bezier(0.4, 0.0, 0.2, 1);
		writing-mode: tb-rl;
		box-shadow: var(--box-border-all);
	}

	@supports (-moz-appearance:meterbar) {
		#side-bar::after {
			left: calc(var(--base-font-size) * 0.625);
		}
	}

	#side-bar {
		margin: 0 0 0 -16.5rem;
		transition:
			margin 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
			background 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
			padding 300ms cubic-bezier(0.4, 0.0, 0.2, 1);
		padding-right: 1rem;
		background: rgba(var(--swatch-menubg-color), 0);
		top: 0;
		min-height: 100vh;
	}

	#side-bar:hover,
	#side-bar:active,
	#side-bar:focus-within {
		margin: 0 0 0 -0.99vw;
		background-color: rgba(var(--swatch-menubg-color), 1);
		padding-right: 0;
	}

	#side-bar:hover::after,
	#side-bar:active::after,
	#side-bar:focus-within::after {
		left: 14.316rem;
		opacity: 0;
		width: 0rem;
		font-size: 0em;
	}

	#side-bar .side-block {
		margin-top: 0;
		filter: brightness(25%);
		transition:
			filter 300ms cubic-bezier(0.4, 0.0, 0.2, 1);
	}

	#side-bar:hover .side-block,
	#side-bar:active .side-block,
	#side-bar:focus-within .side-block {
		filter: brightness(100%);
	}
}

@media only screen and (max-width: 768px) {
	#side-bar {
		left: calc(-17rem - 0.5625rem);
	}

	#side-bar:target {
		overflow-x: hidden;
	}

	#top-bar > div.mobile-top-bar > div.open-menu > p > a {
		color: rgba(var(--swatch-menutxt-dark-color)) !important;
	}

	#skrollr-body:target #side-bar {
		left: 0;
	}

	#side-bar:target .close-menu {
		margin-left: calc(15.5rem + 2 * 0.5625rem + 0.125rem);
	}

	#skrollr-body:target #container {
		transform: none;
	}

	#skrollr-body:target #container-wrap-wrap {
		overflow: hidden;
	}
}	
}

@-moz-document url("http://www.scp-wiki.net/") {
#page-content {
	display: flex;
	flex-direction: column;
}

#page-content > p {
