style.css:
@-moz-document domain("tweakers.net") {
#menubar {
    background-image: none;
    box-shadow: none;
    border-bottom: none;
    padding: 6px 0;
    -webkit-box-shadow: -2px 2px 24px 0px rgba(0,0,0,0.1);
    -moz-box-shadow: -2px 2px 24px 0px rgba(0,0,0,0.1);
    box-shadow: -2px 2px 24px 0px rgba(0,0,0,0.1);
    z-index: 999999;
}

#menuwrapper {
    height: 56px;
}


#categoryBar,
.icon.display.flag,
#search .fancyButton.abo,
html.js body.hasTracker #tracker,
#search #true,
#menubottombar {
    display: none;
}

#news .newsSubmit,
.atLabel {
    opacity: .25;
    color: black;
}

#adv {
    border: none;
    background: #F6F6F6;
    padding: 16px;
}

#adv .advimg {
    background: none;
    border: none;
}

#adv .advimg img {
    border-radius: 2px
}

html.js body.hasTracker {
    margin-left: 0;
}

#top,
#searchbar {
    border-radius: 0;
    background: transparent;
}

.newNotificationsCount {
    border-radius: 8px;
    opacity: .85;
}

#menu .sitename+ul {
    margin-left: 180px;
}

#search .keywordSearch {
    width: 520px;
}

#search {
    padding: 16px 0;
}

#contentArea {
    margin-top: 6px;
}

input.text {
    padding: 8px 12px;
}

.keywordSearch {
    height: 100%;
}

#menu {
    width: calc(100% - 80px)
}

#categoryBar h2 {
    margin-bottom: 24px;
}

.fpAnkeiler.fullWidth,
.leaderBoard {
    margin: 18px auto 32px;
}

#b_ad, #as_normal {
    margin-bottom: 24px;
}

.billBoard > div,
.billBoard {
    width: 100% !important;
    text-align: left !important;
}


body.customColor #contentArea {
    border-radius: 6px;
    border: none;
    -webkit-box-shadow: -10px 10px 60px 0px rgba(0,0,0,0.06);
    -moz-box-shadow: -10px 10px 60px 0px rgba(0,0,0,0.06);
    box-shadow: -10px 10px 60px 0px rgba(0,0,0,0.06);
}

.mainColumn.frontpage,
#fpPromo .item {
    border: 0;
}


table.highlights td {
    padding: 10px 0;
}

table.highlights .type {
    width: 6px;
}

table.highlights .type .contentIcon {
    margin: 0;
    background: none;
    width: 0;
}

table.highlights td,
#recentlyTested li {
    border-bottom: 2px solid #F6F6F6;
}



table.highlights .title {
    padding: 0;
}

table.highlights .title a {
    padding: 8px 12px;
}

.commentCount {
    background: none;
    text-align: right;
    opacity: .6;
}

table.highlights,
#recentlyTested ul {
    border-top: 2px solid #F6F6F6;
}

.inBetweenContent > td > div {
    margin: 20px 0;
}

.fancyButton.grey {
    border: none;
    background-image: none;
    padding: 0 42px 0 18px;
    height: 36px;
    background-color: #EEEEEE;
    font-size: 13px;
}

a.fancyButton, span.fancyButton {
    line-height: 36px;
}

.fancyButton.clickOut::after {
    top: 12px;
    right: 20px;
    opacity: .3;
}

.fpAnkeiler>a.commentCount {
    color: white !important;
    opacity: 1;
    font-size: 14px !important;
    margin: 0;
    background: rgba(0, 0, 0, .1);
    padding: 6px 10px;
    height: auto; width: auto;
}
}
