style.css:
@-moz-document url-prefix("https://mod.reddit.com") {
@font-face {
    font-family: Whitney;
    font-weight: 300;
    src: url(https://discordapp.com/assets/6c6374bad0b0b6d204d8d6dc4a18d820.woff) format("woff")
}

@font-face {
    font-family: Whitney;
    font-weight: 400;
    src: url(https://discordapp.com/assets/e8acd7d9bf6207f99350ca9f9e23b168.woff) format("woff")
}

@font-face {
    font-family: Whitney;
    font-weight: 500;
    src: url(https://discordapp.com/assets/3bdef1251a424500c1b3a78dea9b7e57.woff) format("woff")
}

@font-face {
    font-family: Whitney;
    font-weight: 600;
    src: url(https://discordapp.com/assets/be0060dafb7a0e31d2a1ca17c0708636.woff) format("woff")
}

@font-face {
    font-family: Whitney;
    font-weight: 700;
    src: url(https://discordapp.com/assets/8e12fb4f14d9c4592eb8ec9f22337b04.woff) format("woff")
}

* {
    font-family: Whitney!important;
    border: 0px solid!important;
}

.App__sidebar,
.App__sidebarFooter,
.ThreadViewer__infobarContainer,
.ReportForm,
.AccordionSection__sectionHead {
    background: #2f3136;
}

/* logo */
.Header__icon {
    width: 130px;
    background: url(https://i.imgur.com/vSwW5OW.png);
    background-repeat: no-repeat;
    background-size: 115px;
    margin-left: 10px;
}

.Header__icon img {
    display: none;
}

/* sidebar folders */
.SidebarNav__item.m-selected.m-all .SidebarNav__icon,
.SidebarNav__item.m-selected.m-all .SidebarNav__title,
.SidebarNav__item.m-selected.m-archived .SidebarNav__icon,
.SidebarNav__item.m-selected.m-archived .SidebarNav__title,
.SidebarNav__item.m-selected.m-inprogress .SidebarNav__icon,
.SidebarNav__item.m-selected.m-inprogress .SidebarNav__title,
.SidebarNav__item.m-selected.m-new .SidebarNav__icon,
.SidebarNav__item.m-selected.m-new .SidebarNav__title,
.SidebarNav__item:hover.m-all .SidebarNav__icon,
.SidebarNav__item:hover.m-all .SidebarNav__title,
.SidebarNav__item:hover.m-archived .SidebarNav__icon,
.SidebarNav__item:hover.m-archived .SidebarNav__title,
.SidebarNav__item:hover.m-inprogress .SidebarNav__icon,
.SidebarNav__item:hover.m-inprogress .SidebarNav__title,
.SidebarNav__item:hover.m-new .SidebarNav__icon,
.SidebarNav__item:hover.m-new .SidebarNav__title {
    color: #dcddde!important;
}

/* subreddit list */
.SidebarCommunity__name {
    color: #72767d;
}
.SidebarCommunity__name.m-selected {
    color: #dcddde;
}

.SidebarCommunity__selectedIcon {
    color: #7289da;
}

.SidebarCommunity__blankIcon,
.SidebarCommunity__selectedIcon {
    position: relative;
    top: 5px;
}

.Sidebar__body::-webkit-scrollbar {
    width: 0 !important
}

/* misc */
.ThreadPreviewViewerHeader__iconLabel {
    color: #fff;
}

.ThreadPreview__divider,
.NewThread__communityTitle {
    color: #949494
}


.SidebarNav__title,
.SidebarNav__item.m-selected .SidebarNav__title {
    font-weight: 500;
    font-size: 16px;
}

.App,
.App__header,
.ThreadViewer__header,
.ThreadViewer,
.ThreadPreviewViewer__header,
.ThreadListing__threads,
.AccordionSection__articleWrap {
    background: #36393f;
}

.StyledHtml,
.AccordionSection__sectionHead,
.ChildReasons__radio .Radio__label,
.AccordionSection__section {
    font-size: 0.9375rem;
    color: #dcddde;
}

.ThreadTitle__community,
.ThreadTitle__title {
    color: #fff;
    font-weight: 600;
    font-size: 18px;
}

.Sidebar__titleMessage {
    font-size: 16px;
    color: #e4e4e4;
}

.Thread__message ~ .Thread__message,
.Thread__message ~ .Thread__modAction,
.Thread__modAction ~ .Thread__message,
.Thread__modAction ~ .Thread__modAction {
    z-index: 50;
}

.SidebarNav__item,
.SidebarNav__item:visited,
.Message__date,
.Message__date:visited,
.Message__quote,
.Message__report,
.InfoBar__age,
.InfoBar__karmaDisplay,
.InfoBar__recentsTitle {
    color: #929292;
}

/* sidebar notices  */
.InfoBar__shadowBan {
    background: #522a2e;
}

.InfoBar__banText,
.InfoBar__shadowBan {
    color: #ff6e87;
}

.InfoBar__recentsNone {
    color: #757575;
}

/* radio circles */
.Radio__input:checked + .Radio__circle:before {
    color: #7289da;
}
.Radio__input:checked + .Radio__circle + .Radio__label,
.Checkbox__input:checked + .Checkbox__circle + .Checkbox__label {
    color: #fff;
}

/* mod notes */
.Thread__messageIsMod {
    width: 100%;
    background: #46d1601c;
    /*pointer-events: none;*/
}

.Message {
    position: relative;
    z-index: 99999;
}

/* mod actions */
.Thread__modAction {
    background: #202225
}
.ModAction__text {
    color: #b0b0b0;
}

/* grouped thread actions */
.Thread__grouped,
.Thread__groupedText {
    background: #202225;
}

.Thread__groupedTextActions,
.Thread__groupedTextMessages {
    color: #f6f6f7
}

.Thread__groupedTextAnd {
    color: #ccccca;
}

/* new thread */
.NewThread__cancelText {
    color: #ccccca;
}

.NewThread__form {
    background: transparent;
    box-shadow: none;
}

/* dropdowns */
.FancySelect__value.m-nocolor,
.FancySelect__value.m-nocolor .FancySelect__valueIcon,
.FancySelect__value.m-nocolor .FancySelect__valueText {
    color: #f6f6f7;
}

.FancySelect__tooltip {
    background: #36393f!important;
    border-radius: 3px;
    box-shadow: 0 0 0 1px rgba(32, 34, 37, .6), 0 1px 5px 0 rgba(0, 0, 0, .3);
}

.FancySelect__option {
    color: #f6f6f7;
}

.FancySelect__option:hover {
    background: #0000001a;
}

/* toast notifications */
.Toaster {
    box-shadow: none;
}

/* thread list */
.ThreadListing__thread {
    background: #494d53;
}

.ThreadListing__thread.m-read {
    background: #36393f;
    box-shadow: none;
}

/* footer & mute buttons, thread preview reply count, timestamps and more */
.App__sidebarFooterText,
.InfoBar__controlText,
.ThreadPreview__time,
.ThreadPreview__time:visited,
.ThreadPreview__replyCount,
.Message__internalNote,
.ThreadViewerHeader__back,
.ThreadViewerHeader__back:visited,
.ThreadViewerHeader__controlText,
.ThreadViewerHeader__controlText:visited {
    color: #ccccca;
}

/* links */
a,
.AccordionSection__sectionHead.open {
    color: #00b0f4;
}
a::visited {
    color: #0096cf;
}

/* quotes */
.StyledHtml blockquote,
.StyledHtml code,
.StyledHtml pre,
.StyledHtml pre code {
    background: #494d53;
}

.StyledHtml blockquote {
    width: fit-content;
    padding: 0px 10px;
    border-radius: 3px;
}

/* thread authors */
.InfoBar__username,
.InfoBar__username:visited {
    color: #f1f1f1;
    font-weight: 600;
    font-size: 20px;
}

.Message__author,
.Message__author:visited,
.ThreadPreview__author,
.ThreadPreview__author:visited {
    color: #f1f1f1;
    font-size: 0.85rem;
    font-weight: 500;
    text-decoration: none!important;
}

.ThreadPreview__authors {
    color: #949494;
}

/* hide logout, useless captions and formatting help */
.Header__profile,
.ThreadViewerReplyForm__formattingHelp {
    display: none;
}

.ThreadViewerHeader__controlText {
    font-size: 0px;
}

/* search */
.Search {
    width: 280px;
    border-radius: 3px;
    left: auto;
    right: 0px;
}

.Search__inputContainer,
.Search__button {
    background: #ffffff1a!important;
}

.Search__input {
    color: white;
}

/* textboxes */
.Textarea,
.Textarea:focus,
.NewThread__subject,
.Input,
.Input:focus {
    background: #72767d4d;
    color: #fbfbfb;
    border-radius: 3px;
}

.ThreadViewerReplyForm__replyText {
    height: 50px;
}

/* notification badges */
.SidebarNav__count,
.SidebarNav__item.m-selected.m-mod .SidebarNav__count,
.SidebarNav__item:hover.m-mod .SidebarNav__count,
.SidebarNav__item.m-selected.m-notifications .SidebarNav__count,
.SidebarNav__item:hover.m-notifications .SidebarNav__count {
    background-color: #f04747;
    font-weight: 500;
    box-shadow: 0 1px 0 rgba(0, 0, 0, .25), inset 0 1px 0 hsla(0, 0%, 100%, .15);
    color: #fff;
    padding: 3px 6px;
    font-size: 12px;
    line-height: 12px;
    border-radius: 15px;
    height: auto;
    margin-top: 0px;
    top: 3px;
}

.SidebarNav__item.m-selected.m-all .SidebarNav__count,
.SidebarNav__item.m-selected.m-archived .SidebarNav__count,
.SidebarNav__item.m-selected.m-inprogress .SidebarNav__count,
.SidebarNav__item.m-selected.m-new .SidebarNav__count,
.SidebarNav__item:hover.m-all .SidebarNav__count,
.SidebarNav__item:hover.m-archived .SidebarNav__count,
.SidebarNav__item:hover.m-inprogress .SidebarNav__count,
.SidebarNav__item:hover.m-new .SidebarNav__count {
    background-color: #f04747;
}

/* icons */
.icon-arrowdown:before {
    content: " ";
    background: url(https://discordapp.com/assets/f58cf3b8fc79e9d671ab649ab37651a9.svg) 50% no-repeat;
    display: inline-block;
    width: 10px;
    height: 7px;
}

.ThreadTitle__divider {
    font-size: 0px;
}
.ThreadTitle__divider::after {
    content: " ";
    background: url(https://discordapp.com/assets/f58cf3b8fc79e9d671ab649ab37651a9.svg) 50% no-repeat;
    display: inline-block;
    width: 10px;
    height: 8px;
    transform: rotate(270deg);
}

.ThreadListing__thread .ThreadTitle__divider::after {
    position: relative;
    top: 4px;
}

.icon-check:before {
    content: " ";
    display: inline-block;
    width: 18px;
    height: 18px;
    border-radius: 3px;
    background: transparent;
    background-image: url("data:image/svg+xml;base64,PHN2ZyBuYW1lPSJDaGVja21hcmsiIHdpZHRoPSIxOCIgaGVpZ2h0PSIxOCIgdmlld0JveD0iMCA wIDE4IDE4IiB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciPjxnIGZpbGw9Im5vbm UiIGZpbGwtcnVsZT0iZXZlbm9kZCI+PHBvbHlsaW5lIHN0cm9rZT0iIzcyODlkYSIgc3Ryb2tlL XdpZHRoPSIyIiBwb2ludHM9IjMuNSA5LjUgNyAxMyAxNSA1Ij48L3BvbHlsaW5lPjwvZz48L3N2 Zz4=")
}

.icon-checkbox:before {
    content: " ";
    display: inline-block;
    width: 18px;
    height: 18px;
    border-radius: 3px;
    background: transparent;
}

/* top bar */
.ThreadPreviewViewer__listing,
.ThreadViewer__threadContainer,
.ThreadViewer__infobarContainer {
    top: 0px;
}

.ThreadPreviewViewer__header,
.ThreadViewer__header {
    position: fixed;
    width: calc(100% - 600px);
    top: 5px;
    left: 280px;
}

/* dropdown fixes */
.ThreadPreviewViewer__header {
    z-index: 10;
}

/* toast notifications */
.Toaster {
    z-index: 100;
}

/* reporting */
.ReportForm__title {
    color: #fff;
}

.Common__inputHeader,
.ChildReasons__radioOptionsSectionHeader {
    color: #8a8a8a;
}

/* extension specific fixes */
body.mod-toolbox-rd .tb-bracket-button.tb-search-help {
    background: #878a8c;
    color: #4a4d53;
    font-size: 13px;
    border-radius: 50%;
    font-weight: 600;
    margin-left: 8px;
    top: 24px;
    height: 14px;
    width: 14px;
    line-height: 14px;
}

.material-icons {
    font-family: "Material Icons"!important;
}
}
