options.value:
.nowcoder-header{
    position:relative;
    margin-bottom:10px;
}
.nk-main{
    padding-top:0;
}
.discuss-title.js-fixed-title.fixed-title{
    top:0px !important;
    width:688px;
    box-sizing:border-box;
}

/*修正回复框长度*/
.reply-input.reply-input-textarea.js-ipt{
    width:550px !important;
}
/* 拉伸 1440宽屏 */
@media screen and (min-width: 1440px) {
  .nk-main{
        width:1440px;
    }
    .mini-banner{
        display:flex;
        justify-content:center;
    }
    .mini-banner a{
        width:960px;
        flex:0 0 auto;
    }
    .nk-content{
        width:960px;
    }
    .nk-bar{
        width:470px;
    }
    .nk-content .discuss-detail{
        width:830px;
    }
    .discuss-main{
        font-size:16px;
    }
    
/*允许通知展开长度*/
    .letter-brief{
        width:800px;
    }
    /*调整 搜索框错位*/
    #search{
        width:774px;
    }
    /*顶部标题长度*/
    .discuss-title.js-fixed-title.fixed-title{
        width:958px ;
    }
    
    /*修正回复框长度*/
    .reply-input.reply-input-textarea.js-ipt{
        width:821px !important;
    }    
    /*修正其他页面宽度*/
    .paper-filter-box{
        width:1440px;
    }
    .content-item-box{
        width:1440px;
    }
    .course-wrap {
        margin-left:240px;
    }
    .test-center-page .nk-content{
        width:1220px;
    }
}

a.identity-banner{
    /*身份认证*/
    display:none;
}
#jsSideTopicList{
    /*商业广告*/
    display:none;
}
.mini-banner{
    /*顶部banner*/
    display:none;
}

/*名字对比度提升*/

a[class*="level-color"]{
    filter:grayscale(20%) brightness(0.7);
    font-size:14px;
}

.reply-box{
    padding: 0 10px;
}
.reply-list-item{
    margin-top:5px !important;
    padding-top:5px !important;
}
.reply-main{
    margin-bottom:0px;
}
.reply-info{
    margin-top:0px;
}
.reply-action{
    margin-top:5px !important;
}
.reply-editbox{
    margin-top:5px !important;
    padding:0 !important;
}

.reply-content img[data-card-emoji]{
    width:30px;
    height:30px;
    transition:height 0.25s,width 0.25s;
}
.reply-content img[data-card-emoji]:hover{
    width:60px;
    height:60px;
}

.answer-brief.nc-post-content img{
    max-width:120px;
    transition:max-width 0.25s;
}
.answer-brief.nc-post-content img:hover{
    max-width:240px;
}

.answer-brief.nc-post-content img[data-card-emoji]{
    max-width:60px;
    transition:max-width 0.25s ;
}
.answer-brief.nc-post-content img[data-card-emoji]:hover{
    max-width:100px;
}
