style.css:
@-moz-document url-prefix("https://aternos.org/start/") {
/* Main */
::-webkit-scrollbar {
	display: none !important;
}

.alert header {
	background: rgba(7, 14, 20, 0.8) !important;
    color: #009BC6 !important;
}

.alert header .fas {
	color: #BF4660 !important;
}

.alert main {
	background: rgba(7, 14, 20, 0.8) !important;
    color: #AF0FCD !important;
    font-weight: bold !important
}

body {
	background-image: url(https://i2.wp.com/background4free.com/download/purple_violet_magenta_1297045874.jpg?q=100) !important;
    background-size: cover !important;
    background-repeat: no-repeat !important;
}

/* Header */
.header {
	background: rgba(0,0,0, 0.8) !important;
}

header .logo > img {
display: none;
}

header .logo {
	content: url(https://i.imgur.com/uZysbTy.png);
}

header .social .fab {
	color: #009BC6 !important;
}

header .social .fab:hover {
	color: #3680C1 !important;
}

header .statustext .statusinfo {
	color: #AF0FCD !important;
}

header .friend-access-nav .btn-white {
	background: #FCFAFA !important;
}

header .friend-access-nav .btn-white:hover {
	background: #E2E2E2 !important;
}

header .friend-access-nav .btn-blue {
	background: #009BC6 !important;
}

header .friend-access-nav .btn-blue:hover {
	background: #3680C1 !important;
}

header .friend-access-dropdown {
	background: rgba(0,0,0, 0.8) !important;
    border: none !important;
}

header .friend-access-dropdown .friend-access-switch {
    border-bottom: 1px solid #161925;
}

header .friend-access-dropdown .friend-access-switch-ip {
	color: #009BC6 !important;
}

header .friend-access-dropdown .friend-access-switch-user {
	color: #AF0FCD !important;
    font-weight: bold !important;
}

header .friend-access-by {
	color: #009BC6 !important;
}

header .user .username a {
	color: #009BC6 !important;
    font-weight: bold !important;
}

header .user .usericon {
	color: #009BC6 !important;
}

header .user .username:hover {
	color: #3680C1 !important;
}

header .user .usericon:hover {
	color: #3680C1 !important;
}

header .user .userlinks a, header .user .userlinks span {
	color: #AF0FCD !important;
}

header .user .userlinks a:hover, header .user .userlinks span:hover {
	color: #8E2CB2 !important;
}

header .help .btn-blue {
	background: #009BC6 !important;
}

header .help .btn-blue:hover {
	background: #3680C1 !important;
}

/* Sidebar */
.sidebar {
	background: rgba(0,0,0, 0.6) !important;
}

/* Navigation */
.navigation {
	background: rgba(0,0,0, 0.8) !important;
    border: none !important;
}

.navigation .item {
    border: none !important;
    color: #009BC6 !important;
}

.navigation .item:hover {
    border: none !important;
    color: #009BC6 !important;
    background: rgba(0,0,0, 0.6) !important;
}

.navigation .item::after {
    color: #AF0FCD !important;
}

.navigation .item .fas, .navigation .item .far {
	font-weight: 10 !important;
    color: #AF0FCD !important;
}

.navigation .navigation-toggle {
	color: #AF0FCD !important;
}

.navigation span.navigation-log-problems-badge {
	color: white !important
}

/* Content */
.content {
	background: rgba(0,0,0, 0.6) !important;
}

.content h1 {
	color: #009BC6 !important;
}

.content h2 {
	color: #AF0FCD !important;
}

.start-activity {
	background: #05060a !important;
    color: #AF0FCD !important;
    text-shadow: 0 0 2.5px black !important;
}

.start-activity:hover {
	background: #2d2d2d !important;
}

.start-activity-icon .fas, .start-activity-icon .fab, .start-activity-icon .far {
	font-weight: 10 !important;
    color: #009BC6 !important;
}

.start-activity-help .start-activity {
	background: #161925 !important;
}

.start-activity-help .start-activity:hover {
	background: #2d2d2d !important;
}

.tutorials .tutorial {
    background: rgba(0,0,0, 0.6) !important;
	border: none !important;
    -webkit-border-radius: 15px !important;
}

.tutorial-text .tutorial-darker {
	color: #009BC6 !important;
}

.tutorial-text {
	color: #AF0FCD !important;
}

