*{
font-family: 'Roboto', sans-serif;
}

.numCounter > b:before {
    transition: 0.4s ease-out;
}
.numCounter > b {
    width: 25px;
    background: #000;
    box-shadow: none;
}
.numCounter > span {
    opacity: 1;
    font-size: 47px;
}
.card {
    background-color: #000;
}
body {
    background-color: #000;
    color: #ddd;
}
.numCounter {
    /*height: 40px;
