/* Thema */

*{font-family:Roboto;}

.header .logo {
    font-size: 16px;
}
body {
    background-color: #333333;
}
.menu > ul {
    border-top: 8px solid #333333;
}
.head-bar {
    background-color: #333333;
}
.head-bar div > a {
    border-left: 1px solid #333333;
}
#cijfers-leerling .last-grade {
    background-color: #333333;
    border-radius: 50px;
    animation-name: cijferout;
    animation-duration: 0.5s;
    animation-fill-mode: forwards;
}
#cijfers-leerling .last-grade:hover {
    animation-name: cijfer;
    animation-duration: 0.5s;
    animation-fill-mode: forwards;
}
@keyframes cijfer {
    from {
        border-radius: 50px;
    }
    to {
        border-radius: 15px;
    }
}
@keyframes cijferout {
    from {
        border-radius: 15px;
    }
    to {
        border-radius: 50px;
    }
}
.block h3 {
    color: #333333;
}
.menu li.active,
.menu li.highlight-menu.active,
.menu li.active > a:hover {
    background: #333333;
}
.menu li.active,
.menu li.highlight-menu.active {
    background-color: #333333;
    transform: scale(1.1);
    transition: all .2s;
}
.content-container {
    background-color: #1f1f1f;
    border: 1px solid #333333;
    border-radius: 5px;
    animation-name: colorchange;
    animation-duration: 0.75s;
}
@keyframes colorchange {
    from {
        background-color: #333333;
    }
    to {
        background-color: #1f1f1f;
    }
}
.endlink a {
    color: #1f1f1f;
}
.menu a {
    border-bottom: 1px solid #333333;
}
aside .trigger-btn {
    border-left: 1px solid #333333;
}
aside .tabs li.active a {
    background-color: #333333;
}
aside .tabs li.active:after {
    border-color: #333333 rgba(255, 255, 255, 0) rgba(255, 255, 255, 0) rgba(255, 255, 255, 0);
}
aside .tabs li:hover a {
    color: #888888;
}
#afsprakenlijst-container .today {
    color: #333333;
    font-size: 12px;
    font-weight: bold;
}
.head-bar div > a:hover {
    background: #4d4d4d;
    background: -moz-linear-gradient(top, #005c86 0, #0096db 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0, #4d4d4d), color-stop(100%, #4d4d4d));
    background: -webkit-linear-gradient(top, #4d4d4d 0, #4d4d4d 100%);
    background: -o-linear-gradient(top, #005c86 0, #0096db 100%);
    background: -ms-linear-gradient(top, #005c86 0, #0096db 100%);
    background: linear-gradient(to bottom, #4d4d4d 0, #4d4d4d 100%);
}
.header div.logoff:hover {
    background-color: #2c2c2c;
}
.header-user img {
    border-radius: 3px;
}
.header > div + div {
    border-left: 1px solid #333333;
}
.header .header-user:hover {
    background-color: #2c2c2c;
}
.header .icon-settings {
    float: right;
    margin: 0 4px 0 4px;
    animation-name: settingspinout;
    animation-duration: 1.5s;
}
.header .icon-settings:hover {
    float: right;
    margin: 0 4px 0 4px;
    animation-name: settingspin;
    animation-duration: 1s;
}
@keyframes settingspin {
    from {
        transform: rotate(0deg);
    }
    to {
        transform: rotate(360deg);
    }
}
@keyframes settingspinout {
    from {
        transform: rotate(360deg);
    }
    to {
        transform: rotate(0deg);
    }
}
.menu .navTrigger {
    border-bottom: 1px solid #333333;
}
.menu a:hover,
.menu .navTrigger:hover {
    background-color: #333333;
}
.header .icon-logo:before {
    content: "i";
}
.header .logo {
    animation-name: logoslide;
    animation-duration: 1.5s;
}
@keyframes logoslide {
    from {
        transform: scale(0, 0.25);
    }
    to {
        transform: scale(1, 1);
    }
}
.card .content .content-title {
    color: #333333;
}
.attachment-bar.editable:before {
    color: #ffffff;
}
.msg-list .unread {
    background-color: #e6e6e6;
}
.details h3 {
    color: #333333;
}
.head-bar .foldout li + li {
    position: relative;
    border-top: 1px solid #333333;
}
.head-bar .foldout {
    -moz-box-shadow: 3px 3px 5px -2px rgba(0, 0, 0, 0.5);
    -webkit-box-shadow: 3px 3px 5px -2px rgba(0, 0, 0, 0.5);
    box-shadow: 3px 3px 5px -2px rgba(0, 0, 0, 0.5);
    border: 1px solid #333333;
    background-color: #333333;
}
.head-bar .foldout li a:hover {
    background-color: #404040;
}
.block.search-field .glyph.icon-search {
    border: 1px solid #333333;
}
.btn-row .btn-pri,
.block.search-field .glyph.icon-search,
.endlink .btn-pri {
    background: linear-gradient(to bottom, #333333 0, #333333 100%);
}
.tabs {
    border-left: 1px solid #333333;
}
.photo-high .photo-placeholder,
.photo.photo-high img {
    height: auto;
    width: 85px;
    border-radius: 50px;
    animation-name: avatar;
    animation-duration: 1.5s;
}
@keyframes avatar {
    from {
        transform: rotate(0deg);
        width: 0px;
    }
    to {
        transform: rotate(360deg);
        width: 85px;
    }
}
.primary-button {
    background-color: #333333;
}
.primary-button:hover {
    color: #333333;
}
.primary-button:hover .primary-button__icon {
    fill: #333333;
}
div.image-carrousel {
    visibility: hidden;
}
.podium {
    display: block;
    margin-left: auto;
    margin-right: auto;
    width: 40%;
}
.col-sm-6 {
    width: 100%;
}
.password__eye-icon {
    width: 20px;
    height: 20px;
    fill: #333333;
}
.text-icon[icon-type="information"] {
    background-color: #333333;
}
.is-focused .placeholder,
.is-dirty .placeholder {
    color: #333333;
}
.header .logo:hover:after {
    display: inline-block;
    animation-name: versie;
    animation-duration: 0.5s;
    animation-fill-mode: forwards;
}
@keyframes versie {
    0% {
        transform: scale(0, 1);
        margin-left: -25px;
    }
    100% {
        transform: scale(1, 1);
        margin-left: 0px;
    }
}
.endlink a:hover {
    color: #787878;
}
.alert {
    border-left: 4px solid #333333;
    background-color: #dcdcdc !important;
}
.alert span.nrblock {
    background-color: #333333;
}
.widget .agenda-list li.alert span.time:before {
    color: #333333;
}
.widget .agenda-list li.alert span.time:after {
    color: #333333 !important;
}
.alert a:hover {
    background-color: #c5c5c5 !important;
}
.primary-button {
    background: linear-gradient(to bottom, #333333 0, #333333 100%);
}
.primary-button {
    border: 1px solid #333333;
}
.dialog .title {
    color: #333333;
}
.sidecolumn aside .head-bar .asideTrigger {
    border: 1px solid #1f1f1f;
    border-bottom: none;
    background-color: #1f1f1f;
    border-radius: 12px;
}
.info-toast:before {
    background-color: #333333;
    animation-name: toast;
    animation-duration: 2s;
    animation-iteration-count: infinite;
}
.info-toast {
    animation-name: toastborder;
    animation-duration: 2s;
    animation-iteration-count: infinite;
}
@keyframes toast {
    0% {
        background-color: #333333;
    }
    50% {
        background-color: #1f1f1f;
    }
    100% {
        background-color: #333333;
    }
}
@keyframes toastborder {
    0% {
        border-color: #333333;
    }
    50% {
        border-color: #1f1f1f;
    }
    100% {
        border-color: #333333;
    }
}
.info-toast span {
    color: #333333;
}
.alert-toast:before {
    animation-name: toast;
    animation-duration: 2s;
    animation-iteration-count: infinite;
}
.alert-toast {
    animation-name: toastborder;
    animation-duration: 2s;
    animation-iteration-count: infinite;
}
.alert-toast span {
    color: #333333;
}
.attachments a {
    color: #333333;
}
#berichten-detail-container .new-message-block li label {
    color: #ffffff;
}
.attachment-bar.editable a:hover {
    color: #8c8c8c;
}
.cijfers-k-grid.k-grid .grade.insufficient {
    font-weight: bold;
    font-size: 14px;
    animation-name: onvoldoende;
    animation-duration: 2s;
    animation-iteration-count: infinite;
}
@keyframes onvoldoende {
    0% {
        color: #333333;
    }
    50% {
        color: #c3000c;
    }
    100% {
        color: #333333;
    }
}
.attachment-bar div a {
    color: #333333;
}
div.loading-overlay > div > img {
    visibility: hidden;
}
div.loading-overlay {
    visibility: hidden;
}
div.loading-overlay > div > img + p {
    visibility: hidden;
}
.loading-image {
    visibility: hidden;
}
.info-toast i {
    animation-name: toastborder;
    animation-duration: 2s;
    animation-iteration-count: infinite;
}
.alert-toast i {
    animation-name: toastborder;
    animation-duration: 2s;
    animation-iteration-count: infinite;
}
.msg-list .unread {
    border-left: 4px solid #333333;
}
#cijfers-leerling .no-data .last-grade {
    background-color: #878787;
    font-style: normal;
}
@keyframes fadeIn {
    from {
        opacity: 0
    }
    to {
        opacity: 1
    }
}
@keyframes colorError {
    0% {
        fill: #fff;
    }
    50% {
        fill: #c32929;
    }
    100% {
        fill: #fff;
    }
}
.fade-in {
    opacity: 0;
    animation: fadeIn ease-in 1;
    animation-fill-mode: forwards;
    animation-duration: 5s;
    animation-delay: 1.1s
}
.color {
    animation: colorError 12s;
    animation-delay: 5.1s;
    animation-iteration-count: infinite;
}
.header .header-user em, .header .user em {
    font-weight: bold;
}
.new-appointment-block label {
    color: #fff;
}
div.modal-dialog:after {
    background: #2a2a2a;
}
div.modal-dialog h1 {
    color: #2c2c2c;
}
div.modal-dialog > nav > ul > li.highlight > a {
    background: #595959;
}
div.modal-dialog button {
    background: linear-gradient(to bottom, #373f42 0, #272727 100%);
}
div.modal-dialog footer ul.carrousel-indicator li.active {
    color: #000000;
}
div.modal-dialog {
    border: 0px solid #000000;
}
div.modal-dialog footer div.footer-navigation > a {
    color: #1c1c1c;
}
}
