style.css:
@-moz-document domain("elchapuzasinformatico.com") {
.layout-box .site-content .wrap, .post-item.sticky,   #toc_container.toc_light_blue{
 background-color: #333;   
}

h1.post-title,  h1, h2, h3, h4, h5, h6, .entry-title a, a:hover {
    color: #ddd;
}

body, .vote-up .count,  p.post-excerpt {
 color: #ccc;   
}

 .entry-meta, .related-item h3 a, .small-post .title a, .small-post .comment-text a{
 color: #bbb;   
}

table td a {
    color:#439fdc!important;
}

/* Titulo seccion dentro de noticial, la de fondo azul */

 .blue-bar {
    background-color: #0799fb;
}

 .blue-bar h1 {
    color: #000;
}
/* citas */
blockquote {
    color: #8fbdd0;
}


/* Tablas y valoraciones */

.TReview1nombre,  .valoracionhead {
    background-color: #003371;
    color: #eee;
}

 .TReview2nombre{
    background-color: #1454a2;
    color: #eee;
}

.TReview2 {
    background-color: #bbbbbb;
    color: #000;
}

 .TReview1 {
    background-color: #4c4c4c;
    color: #cecece;
}

.TReview3,  .TReview4, .TReview5{
    background-color: #444;
    color: #ddd;
}

table {
    background: #555;
}

tbody tr:nth-child(2n-1) {
    background-color: #444;
}

tbody tr:hover {
    background-color: rgba(101, 142, 216, 0.3);
}

/* Valoraciones, fondo */
 .valoracionpositivo { /* + */
    background-color: #21a700;
}

 .valoracionnegativo { /* - */
    background-color: #d00000;
}

  .valoracionotros { /* * */
    background-color: #d4a807;
}
}
