style.css:
@-moz-document domain("duckduckgo.com") {
html.dark-bg {
  background: #323639;
}

.nav-menu__theme-color-top[style="background-color:#444;"] {
  background-color: #323639 !important;
}

.nav-menu__theme-color-bot[style="background-color:#222;"] {
  background-color: #212225 !important;
}

.set-thumbnails > :nth-child(4) > .set-thumbnail__label:after {
  content: "DarkGo";
}

.dark-bg .header-wrap {
  background: #323639;
  box-shadow: none;
  border-bottom: 1px solid #2d2f30;
}
.dark-bg .zcm__link:hover {
  color: #9aa0a6;
  background: none !important;
}
.dark-bg .zcm__link.is-active {
  color: white !important;
  border-bottom-color: #508ff2;
}
.dark-bg .tile {
  border: 1px solid #282c30;
  background: #232528;
}
.dark-bg .tile:hover {
  border-color: #323639;
}
.dark-bg .header__button--menu {
  border-radius: 100%;
  color: #9aa0a6;
  background: rgba(40, 44, 48, 0);
  transition: background 0.1s ease, color 0.1s ease;
  will-change: background, color;
}
.dark-bg .header__button--menu:hover {
  background: #282c30;
  color: #c2c2c2;
}
.dark-bg .header--aside.is-hidden {
  display: block !important;
}
.dark-bg .nav-menu--slideout {
  background: #292a2d;
  border: 1px solid #1a1c1e;
  height: auto !important;
  border-radius: 10px;
  margin: 5px;
}
.dark-bg .search__button,
.dark-bg .search__clear,
.dark-bg .footer,
.dark-bg .js-header-logo,
.dark-bg .js-header-aside > *:not(.header__button--menu),
.dark-bg .js-serp-top-right,
.dark-bg .js-serp-bottom-right,
.dark-bg .search-filters-wrap:before,
.dark-bg .search-filters-wrap:after,
.dark-bg .result__icon,
.dark-bg .result__check,
.dark-bg .zci__more-at__icon,
.dark-bg .metabar__dropdowns-wrap::before,
.dark-bg .metabar__dropdowns-wrap::after {
  display: none !important;
}
.dark-bg .site-wrapper {
  background: #212225;
}
.dark-bg .body--home {
  background: #323639;
}
.dark-bg .zci,
.dark-bg .metabar {
  background: #1c1d20;
}
.dark-bg .search--adv {
  border-radius: 10px !important;
  background: #282c30;
  box-shadow: none !important;
  transition: background 0.3s ease;
  will-change: background;
  color: #c2c2c2 !important;
}
.dark-bg .search--adv:hover {
  background: #2f3336;
}
.dark-bg .search--adv.search--focus,
.dark-bg .search--adv.has-suggestions {
  background: #3c4043;
  transition: none;
}
.dark-bg .search--adv.has-suggestions {
  border-bottom-left-radius: 0 !important;
  border-bottom-right-radius: 0 !important;
}
.dark-bg .search--adv {
  padding-right: 11px !important;
}
.dark-bg .acp--highlight {
  background: #585b5e;
}
.dark-bg .search__autocomplete {
  margin-top: -44px !important;
  padding-top: 44px;
}
.dark-bg .search--adv.search--focus.has-text:not(.has-suggestions)::after {
  content: "Press enter to search with DuckDuckGo";
  position: absolute;
  width: calc(100% - 22px);
  height: 44px;
  background: #3c4043;
  padding-top: 44px;
  top: 0;
  margin-left: -12px;
  padding-left: 11px;
  padding-right: 11px;
  line-height: 44px;
  color: #9aa0a6 !important;
  z-index: 2;
  border-radius: 10px;
  box-shadow: 0 10px 10px 0 #00000033 !important;
  font-family: "DDG_ProximaNova", "DDG_ProximaNova_UI_0", "DDG_ProximaNova_UI_1", "DDG_ProximaNova_UI_2", "DDG_ProximaNova_UI_3", "DDG_ProximaNova_UI_4", "DDG_ProximaNova_UI_5", "DDG_ProximaNova_UI_6", "Proxima Nova", "Helvetica Neue", "Helvetica", "Segoe UI", "Nimbus Sans L", "Liberation Sans", "Open Sans", FreeSans, Arial, sans-serif !important;
}
.dark-bg .search__autocomplete,
.dark-bg .acp-wrap {
  box-shadow: 0 10px 10px 0 #00000033;
  margin-left: 0;
  border: none;
  border-bottom-left-radius: 10px !important;
  border-bottom-right-radius: 10px !important;
}
.dark-bg .acp-wrap {
  background: #3c4043;
}
.dark-bg .search--adv {
  border: none;
}
.dark-bg .search__input--adv {
  z-index: 26;
}
.dark-bg .result {
  border-radius: 10px;
}
.dark-bg .result.highlight {
  background: #232528;
  border-color: #323639;
}
.dark-bg .result__image, .dark-bg .tile {
  background: #282c30;
}
.dark-bg .result__image__plc {
  background: #323639;
}
.dark-bg .feedback-prompt__link {
  opacity: 0.2;
  will-change: opacity;
  transition: opacity 0.2s ease;
}
.dark-bg .dark-bg .feedback-prompt__link:hover {
  opacity: 0.7;
}
.dark-bg .module,
.dark-bg .module--news__item {
  border-color: #323639 !important;
  border-radius: 10px !important;
}
.dark-bg .module--news__image-wrapper {
  background: #282c30;
}
.dark-bg .module--news__item,
.dark-bg .module--about,
.dark-bg .module--news__placeholder {
  background: #232528;
}
.dark-bg .module__clickable-header {
  border-bottom: 1px solid #323639;
}
.dark-bg .module__toggle {
  border-top: 1px solid #323639;
}
.dark-bg .module__toggle--more::after {
  background: linear-gradient(rgba(51, 51, 51, 0), #232528);
}
.dark-bg .modal__box {
  background: #292a2d;
  border-color: #1a1c1e !important;
  border-width: 1px !important;
  border-radius: 10px;
  box-shadow: 0 5px 9px 3px #00000020 !important;
}
.dark-bg .modal__list li a:hover {
  background: #444547;
  border-radius: 4px;
}
.dark-bg .about-profiles__img[src="/assets/icons/thirdparty/wikipedia.svg"] {
  filter: invert(1);
}
.dark-bg .metabar__grid-btn,
.dark-bg .btn,
.dark-bg .tile-nav,
.dark-bg .tile-nav::after,
.dark-bg .tile-nav--sm,
.dark-bg .module--news__right,
.dark-bg .module--news__left,
.dark-bg .btn--icon {
  background: #282c30 !important;
  border: 1px solid #323639 !important;
  color: #9aa0a6;
  transition: all 0.1s ease;
}
.dark-bg .metabar__grid-btn:hover,
.dark-bg .btn:hover,
.dark-bg .tile-nav:hover,
.dark-bg .tile-nav:hover::after,
.dark-bg .tile-nav--sm:hover,
.dark-bg .module--news__right:hover,
.dark-bg .module--news__left:hover,
.dark-bg .btn--icon:hover {
  background: #323639 !important;
  color: #c2c2c2 !important;
}
.dark-bg .switch.is-on {
  background: #508ff2;
}
.dark-bg pre,
.dark-bg code {
  background: #1c1d20 !important;
  border-radius: 4px;
}
.dark-bg code {
  color: #c2c2c2;
  padding: 3px;
}
.dark-bg :visited,
.dark-bg a:focus {
  color: #9aa0a6 !important;
}

/*# sourceMappingURL=darkdarkgo.css.map */

}
