style.css:
@-moz-document domain(semana.com)
{


.widgetMundialosky { height: 105px; } /* Modificable según el contenido, si se vuelve muy monotono de actualizar se puede eliminar esta línea */

body, 
.row.apertura, 
.row.content, 
.main-section > .row, 
.titleContainer .small-12.columns { background-color: #222; }

.panel, 
.ver-mas, 
.mas-visto .tabs, 
.opinion .tabs, 
.mas-visto .tabs .widget-header, 
.opinion .tabs .widget-header, 
.titleContainer.fixed .progress .meter, .alert-box, 
.suggestedArticles.wp2.ui.left.floated.fadeInUp, .panel.col-central, .side-nav.related, .side-nav.related li a { background-color: #333; color: #BBB !important; border-color:#333 }

.titleContainer .progress { background-color: #000 !important; }

.article-footer, .contenedor-recomendados { background-color: #1B1B1B; }

.linksTop li, 
.linksTop li a, 
.tittleTagsOpinion, 
.panel p, .article p, p, .articles-row-header .panel-h, 
.panel .articles-row-header .panel-h, 
.side-nav li .related-link, 
.related-link, 
.side-nav li a.related-link,
.newArticle .intro, 
label#labelComentarios, 
.btWidgetVideo { color: #999 !important; }

label#labelComentarios, a.btWidgetVideo { border-color:#1C1C1C !important; border-width: 3px; }
label#labelComentarios:hover, a.btWidgetVideo:hover { background-color: #333; }

.article-h-link, 
.confidenciales .article-link, 
.article-h, 
.titleContainer .small-12.columns, 
h1,
h2,
h3,
h4,
h5,
h6, 
.panelUltimasNoticias h3 a {  color: #BBB !important; }

.icon-facebook2, 
.icon-twitter2, 
.icon-googleplus, 
.icon-linkedin2  { background-color: #222 !important; }

.semana-header, .tagsOpinion { border-bottom-width: 0px !important;}

.destacado p, 
.panel .panel-h, .panel.callout .panel-h, .widget-header .panel-h
{ color: #fcd9d9 !important; }

.ac-container article p, 
.comentarios, .row.volcado.article-list { background-color: #333; } 

ul#rb-grid, .button.secondary, .alert-box.secondary.column { background-color: #222;  }

.alert-box.secondary.column { border-color: #222;  }

.button.secondary { background-color: #222; color:#999 }

.button.secondary:hover { background-color: #444; color: #999 }

#searchBox, #comment, .discusiones_input, #searchButton { background-color: #666 !important; border-color: #666 }

.comentarios hr, hr.lineconfidential { border-color: #222; }

.article-footer, .side-nav.related + .article-tags { border-color: #252525 !important; }

.row.volcado.article-list, 
.article-h.suggestedMore, 
.suggestedArticles h3, 
.panelUltimasNoticias, 
.btn.btn-3.btn-3e.icon-text2, 
.article-header.widget-body, .widget-body, .article-list { border-width: 0px !important; }

.ver-mas.text-right {box-shadow:  0 0px 0 rgba(255,255,255) inset;}

  
}
