        font-weight: 300;
    }

    .im-replied--author-link
    {
        font-size: 0.95em;
        font-weight: 400;
        letter-spacing: 1px;
    }

    .im_fwd_log_wrap, .im_wall_log_wrap
    {
        font-size: 0.8em;
    }

    .post_author
    {
        padding-right: 0;
    }

    .post_header_info, .wall_module .copy_post_header_info
    {
        display: flex;
        flex-direction: column;
        align-items: center;
        padding-right: 40px;
    }

    .reply .reply_content .reply_author a
    {
        font-size: 1.2em;
        font-weight: 400;
        letter-spacing: 1px;
    }

    .im-mess-stack .im-mess-stack--content .im-mess-stack--pname > a
    {
        font-size: 1.2em;
        line-height: 15px;
        font-weight: 400;
        letter-spacing: 1px;
    }

    .im-mess .im-mess--text
    {
        font-size: 1.2em;
        font-weight: 300;
    }

    .reply .reply_content .reply_text
    {
        font-size: 1.15em;
        font-weight: 300;
    }

    .reply .reply_content .reply_text a, .nim-dialog .nim-dialog--preview-attach, .im-page-pinned--media
    {
        color: #7c64c4;
    }

    .page_block
    {
        border-radius: 8px !important;
    }

    #feed_new_posts
    {
        margin-bottom: 8px;
    }

    .page_block .wall_post_text
    {
        font-size: 1.2em;
        font-weight: 300;
        letter-spacing: 1px;
    }

    .feed_new_posts
    {
        border: none;
    }

    /* SCROLL */
    ::-webkit-scrollbar
    {
        width: 8px;
    }

    ::-webkit-scrollbar-track
    {
        background-color: black;
    }

    ::-webkit-scrollbar-track:hover
    {
        background-color: #070509;
    }

    ::-webkit-scrollbar-thumb
    {
        -webkit-border-radius: 0px;
        border-radius: 2px;
        background-color: #302a49;
    }

    ::-webkit-scrollbar-thumb:hover
    {
        background-color: #4e4574;
    }
}
