style.css:
@-moz-document domain("weloma.art"), domain("welovemanga.one"), domain("klz9.com"), domain("manga1000.top"), domain("rawinu.com"), domain("klto9.com"), domain("nicomanga.com") {
#aniBox,
	#pop_manga,
	#rd-side_icon,
	#id-custom_banner,
	.chapter-content-top br,
	.chapter-content br,
	.chapter-content center,
	.end,
	.bidadx-div,
	.adsbygoogle,
	.mCustomScrollbar>.container>center,
	.spolecznoscinet,
	.amp-flying-carpet-wrapper,
	[id^="protag"],
	[id^="div-gpt-ad-"],
	body>div:not([class]),
	body>br,
	center>br,
	.container.manga>br,
	.popup-wrap,
	ins,
	img[src*="image_5f0ecf23aed2e.png"],
	a[href="https://nicoscan.com/"],
	#modal-1,
	.modal-backdrop {
		display: none !important;
		position: absolute !important;
		right: 100vw !important;
	}

	::-webkit-scrollbar {
		max-width: 1mm;
		max-height: 1mm;
		background: #333
	}

	::-webkit-scrollbar-thumb {
		background-color: #e51;
	}

	html,
	body {
		background-color: #555;
	}

	body {
		height: auto;
		min-height: 100vh;
		font: 400 12pt sans-serif;
		overflow-x: hidden;
		background-image: radial-gradient(circle at 50% 30%,
				#43474a 10vw,
				#313537 70vw);
	}

	body,
	p {
		color: #ccc
	}

	img.lazyload {
		object-fit: scale-down;
	}

	img {
		object-fit: scale-down;
	}
	iframe {
		user-select: none;
		-webkit-user-select: none;
	}
	.rd_sidebar {
		position: fixed;
		width: 50%;
		min-width: 500px;
		margin: 0 min(25%, calc(50vw - 250px));
	}

	.rd_sidebar form,
	.rd_sidebar input,
	.rd_sidebar .ss-box {
		width: 100% !important;
		float: none !important;
	}

	.rd_sidebar main {
		max-height: 100vh;
		padding: 3mm 10mm;
		background: rgba(255, 255, 255, .2);
		backdrop-filter: blur(3mm);
		-webkit-backdrop-filter: blur(3mm);
		zoom: 1.2;
	}

	.ss-wrap {
		padding: 0;
		margin: 0 10mm 0 0;
	}

	ul#search-suggestions {
		position: static;
		max-width: none;
		max-height: calc(100vh - 60px);
	}

	ul.ss-box li.ss-header {
		height: auto;
	}

	ul.ss-box li.ss-result {
		padding: 0;
		zoom: 1.2;
	}

	#header,
	#sticky-navbar {
		position: sticky !important;
		display: block !important;
		top: -30px;
		left: 0 !important;
		width: 100% !important;
		height: 50px !important;
		padding: 0;
		margin: 0;
		overflow: visible !important;
		opacity: .2;
		z-index: 5;
		box-shadow: 0 0 3mm #555;
		transform: none !important;
		transition: .8s 0.6s, width 0s !important;
		backdrop-filter: blur(3mm);
		-webkit-backdrop-filter: blur(3mm);
	}

	#header:hover,
	#sticky-navbar:hover {
		top: 0;
		opacity: 1;
		transition: .2s, width 0s !important;
	}

	.container_header {
		display: flex;
	}

	.navbar-header {
		display: inline-block;
		float: none;
	}

	.navbar-form {
		right: 0;
	}

	.navbar-brand,
	.navbar-nav>li>a {
		line-height: 28px;
		padding: 10px;
	}

	.navbar-collapse.collapse {
		display: flex !important;
	}

	.navbar-nav {
		flex: 1 1 auto;
	}

	.navbar-right {
		flex: 0 0 auto;
	}

	.container {
		max-width: none;
	}

	.container.cp {
		padding-top: 0;
	}

	.chapter-before .chapter-select {
		position: fixed;
		left: 0;
		bottom: 0;
		display: flex;
		width: 100vw;
		padding: 1mm 14mm;
		background-color: rgba(80, 80, 80, .8);
		white-space: nowrap;
		opacity: .3;
		z-index: 2;
		box-shadow: 0 0 1mm rgba(0, 0, 0, .5);
		transition: .8s .4s, width 0s, padding 0s;
		backdrop-filter: blur(4mm);
		-webkit-backdrop-filter: blur(4mm);
	}

	.chapter-before .chapter-select:hover {
		background-color: rgba(135, 140, 140, .6);
		opacity: 1;
		transition: .2s, width 0s, padding 0s;
	}

	.chapter-select .select-chapter {
		width: 40mm;
		padding: 0;
		margin: 0 auto;
	}

	.form-control {
		font-size: 12pt;
		height: 2.2em;
		line-height: 1.4;
		padding: 0 1em;
		color: #aaa;
		background: #000;
	}

	.chapter-select a {
		flex: 0 0 auto;
		line-height: 1.4;
		padding: .4em 1em;
		margin: 0;
	}

	.btn-search {
		position: static;
		float: right;
		width: 30px;
		height: 30px;
		background: #000;
		padding: 0;
		border-radius: 4px;
		box-shadow: inset 0 0 0 1px #888;
		filter: brightness(1.5);
	}

	body>.container,
	.mCustomScrollbar>.container {
		position: relative;
		min-width: 670px;
		margin: 0 auto;
	}

	.mCustomScrollbar>.container {
		max-width: 100%;
		overflow: hidden;
	}

	.owl-carousel {
		max-width: 1600px;
		margin: 0 auto;
	}

	#contentstory,
	.backgroundhide {
		margin: 0 0 3mm;
	}

	.chapter-content-top {
		margin-top: 0;
		top: 1em;
		overflow: visible;
	}

	.carousel-inner>img {
		height: 100px;
		overflow: hidden
	}

	.media-object {
		object-fit: cover;
	}

	.mt-5rem {
		margin: 0;
	}

	#content,
	.chapter-content {
		font-size: 0;
		width: auto !important;
		max-width: 100vw;
		text-align: center;
		direction: rtl;
		padding: 3mm 0 1mm;
		margin: -8mm auto 2mm !important;
		border-radius: 3mm;
	}

	.chapter-content p {
		display: inline
	}

	.chapter-content a.label {
		font-size: 11pt;
	}

	#notis,
	#chapters,
	.chapter-select {
		direction: ltr;
	}

	.list-chapters .list-wrap p {
		margin: 0;
	}

	.list-chapters .title {
		display: inline-block;
		line-height: 2;
		width: calc(100% - 260px);
	}

	.swiper-wrapper .white-slide {
		background-color: rgba(255, 255, 255, .4);
	}

	.mrb10 {
		position: absolute;
		top: 12mm;
		right: 6mm;
		background: rgba(0, 0, 0, .2);
		text-shadow: 0 0 1mm #000;
		padding: 1mm 3mm;
		border-radius: 1em;
		opacity: .3;
		transition: .3s;
	}

	.mrb10:hover {
		background: rgba(0, 0, 0, .6);
		opacity: 1;
	}

	.chapter-pagination {
		top: 0;
	}

	.full-image {
		display: inline !important;
	}

	#content>img,
	.chapter-content img {
		user-select: auto;
		-webkit-user-select: auto;
		min-width: 60vh;
		max-width: 100%;
		height: calc(100vh - 12mm);
		vertical-align: top;
		object-fit: contain;
		background: radial-gradient(circle at 50% 50%,
				rgba(255, 255, 255, .9) 60px,
				rgba(255, 255, 255, .7) 30%,
				rgba(255, 255, 255, 0) 110%);
		border: none;
		border-radius: 1mm;
		margin: 0 auto 6.7mm;
		transition: width .3s;
	}

	.chapter-content .chapter-img {
		object-fit: contain;
	}

	.well {
		color: #fff;
		background: #555;
	}

	.summary-more.more-state {
		background: linear-gradient(180deg,
				transparent,
				#555 75%);
	}

	.chapter-content .well {
		width: 80vw;
		min-width: 500px !important;
		max-width: 900px;
		margin: 0 auto;
		padding: 0 1em;
		border-radius: 3mm;
	}

	.page-header {
		margin: 0;
		padding: 0;
	}

	.container.list {
		padding: 5mm;
		max-width: 100vw;
		height: auto !important;
		min-height: calc(100vh - 5mm);
		overflow-x: hidden;
	}

	.badge-info {
		background: rgba(100, 200, 230, .75);
		text-shadow: 0 0 1mm #000;
	}

	.btn {
		font-size: 11pt;
		margin: 1px 2px 1px 0;
	}

	.btn-xs {
		color: #ddd;
		line-height: 1.4;
		border: none;
		padding: 0 .4em;
		border-radius: 4px;
	}

	.btn-xs:hover {
		color: #fff;
	}

	.hinhnen1 {
		padding: 3mm;
	}

	.pagination-wrap,
	.chapter-content .input-group {
		position: fixed;
		width: 100%;
		bottom: 0;
		left: 0;
		padding: 0;
		text-align: right;
		direction: ltr;
		z-index: 2;
		background: rgba(110, 115, 125, .6);
		opacity: .2;
		backdrop-filter: blur(3mm);
		-webkit-backdrop-filter: blur(3mm);
		transition: .3s;
	}

	.pagination-wrap:hover,
	.chapter-content .input-group:hover {
		opacity: 1;
	}

	.pagination-wrap>.pagination-v4,
	.pagination-wrap>.pagination,
	.chapter-content .input-group {
		display: inline-flex;
		width: 100%;
		padding: 2mm 1mm;
	}

	.pagination-v4>li>a,
	.pagination-v4>li>span,
	.chapter-content .input-group a {
		margin: 0 2pt;
	}

	.pagination>li:first-child,
	.chapter-content .input-group div:first-child {
		flex: 0 0 auto;
		min-width: 8em;
		margin: 0 auto 0 13mm;
	}

	.pagination>li:last-child,
	.chapter-content .input-group div:last-child {
		flex: 0 0 auto;
		min-width: 8em;
		margin: 0 13mm 0 auto;
	}

	.pagination>li:first-child>a,
	.pagination>li:last-child>a {
		display: flex;
		align-items: center;
		font-size: 0;
		width: 100%;
		background: #c70;
		text-align: center;
		vertical-align: middle;
		box-shadow: inset 0 0 0 1px #777, 0 0 0 1px #850;
	}

	.pagination>li:first-child>a:not(.disabled)::after,
	.pagination>li:last-child>a:not(.disabled)::after {
		font-size: calc(9pt + .3vw);
		content: "▲︎︎";
		line-height: 1.4rem;
		margin: 0 auto;
		padding: .3em;
		color: #fff;
		transform: rotate(-90deg);
	}

	.pagination>li:last-child>a:not(.disabled)::after {
		transform: rotate(90deg);
	}

	.pagination>li:first-child>a:hover,
	.pagination>li:last-child>a:hover {
		background: #f93 !important;
	}

	.pagination>li:first-child>a:hover::after,
	.pagination>li:last-child>a:hover::after {
		color: #000;

	}

	.col-lg-8,
	.info-manga {
		min-width: 600px;
		margin: 0 !important;
	}

	body>.col-md-8.centered {
		margin-bottom: 12mm;
	}

	.col-lg-4>div {
		width: auto;
		display: block;
	}

	ul.filter-type {
		column-count: 2;
	}

	.col-lg-4 .panel-body .pull-left,
	.col-lg-4 .panel-body .pull-right {
		float: none !important;
		display: inline;
	}

	.breadcrumb {
		position: absolute;
		top: 2mm;
		display: inline-flex;
		max-width: 70%;
		text-align: left;
		font-size: 0;
		padding: .2rem 1rem .2rem .2rem;
		margin: 1mm 0;
		border-radius: 2mm 6mm 6mm 2mm;
		background: rgba(0, 0, 0, .2);
		box-shadow: 0 0 1mm 1px #725;
		white-space: nowrap;
		z-index: 2;
		overflow: hidden;
		opacity: .3;
	}

	.breadcrumb:hover {
		background: rgba(0, 0, 0, .7);
		opacity: 1;
	}

	.breadcrumb>li {
		position: relative;
		font-size: 12pt;
		flex: 0 0 auto;
		overflow: hidden;
		text-overflow: ellipsis;
	}

	.breadcrumb>li a {
		max-width: 100%;
	}

	.breadcrumb>li:nth-of-type(3) {
		flex: 1 1 auto;
	}

	.breadcrumb>li+li::before {
		color: #c85;
		content: "▸";
		padding: 0 0 0 2pt;
	}

	.breadcrumb a {
		display: inline-block;
		padding: .1em .5em;
		border-radius: 1.5em;
		text-decoration: none;
	}

	.breadcrumb li:first-child a {
		padding: .1em .5em .1em .4em;
		border-radius: 1mm 1.5em 1.5em 1mm;
	}

	ol.breadcrumb li a {
		color: #bbb;
	}

	.col-lg-4 .panel-body a {
		display: inline-block;
		padding: 2px 2mm;
		margin: 2px 0;
		line-height: 1;
		border-radius: 1em;
		text-decoration: none;
	}

	.breadcrumb a:hover,
	.col-lg-4 .panel-body a:hover {
		color: #fff;
		background-color: rgba(0, 0, 0, .4);
	}

	.xt {
		clear: both;
	}

	.btn-info {
		margin: 0 0 1.5mm;
	}

	#commentbox {
		padding-bottom: 12mm
	}

	#commentbox img {
		min-width: 0;
		height: auto;
	}

	.pagination {
		display: flex;
		margin: 1mm 0 0 !important;
	}

	.pagination-v4>li>a {
		min-width: 2em;
		font-size: calc(9pt + .3vw);
		text-align: center;
		padding: 1mm .4em;
		margin: 0 1.5mm;
		border: none;
		background: #456;
		box-shadow: 0 0 0 1px #222;
	}

	.pagination-v4>li>span.disabled,
	.pagination-v4>li>a.disabled,
	.pagination-v4>li>span.disabled:focus,
	.pagination-v4>li>a.disabled:hover,
	.pagination-v4>li>a.disabled:focus {
		background: none !important;
	}

	.title_doctruyen .centertext a.link_right {
		font-size: 18pt;
		padding: 0 25%;
		margin: 0 0;
		display: block;
	}

	.container.manga {
		padding-top: 6mm;
	}

	.cmt>.col-md-8 {
		max-width: 1280px;
		float: none;
		margin: 0 auto;
	}

	.fb_iframe_widget {
		white-space: nowrap;
		width: auto !important;
		min-width: 0 !important;
	}

	#myTabContent {
		padding: 0 5mm;
	}

	.tieude {
		margin: 0 auto !important;
	}

	img[src="/images/download1.gif"] {
		display: inline-block;
		width: auto;
		height: 7mm;
	}

	.end2 {
		margin-top: 2mm;
	}

	.chapter-content img,
	.chapter-content p {
		display: inline-block;
	}

	#list-imga:hover>p:first-of-type,
	#chapImgslist:hover>img:first-of-type,
	.chapter-content:hover>img:first-of-type,
	.chapter-content:hover>p:first-of-type,
	.chapter-content:hover img.chapter-img:first-of-type {
		display: block;
	}

	a.comment-user {
		color: #39d;
	}

	ul.list-chapters a:hover {
		color: #fc5 !important;
		text-decoration: none;
	}

	.owl-carousel .owl-stage-outer {
		padding: 2mm 0;
	}

	div[style="background-color: aliceblue;"] {
		background: none !important;
	}

	.bodythumb .title-thumb {
		color: #d85;
	}

	.topAll .nav-tabs .nav-link {
		color: #888;
	}

	.topAll .nav-tabs .nav-link:hover {
		color: #cdf;
	}

	.topAll .nav-item.active .nav-link,
	.topAll .nav-item.active .nav-link:hover {
		color: #eee;
		background: none;
	}

	@media screen {
		select {
			font: 12pt sans-serif;
			line-height: 1.2;
		}

		.mCustomScrollbar {
			padding-top: 0;
		}

		.row-last-update {
			background: none;
		}

		#contentstory,
		.topday {
			background: rgba(0, 0, 0, .3);
		}

		.popular-thumb-item,
		.modal-content,
		.thumb-item-flow {
			padding: 6px !important;
			margin: 0;
			border-radius: 1mm;
			transition: .2s, width 0s;
		}

		.modal-content {
			background: #444;
		}

		.owl-item {
			padding: 2mm !important;
		}

		.thumb-item-flow .thumb-detail {
			padding: 3mm 2mm 1mm;
			background: linear-gradient(0deg,
					rgba(0, 0, 0, .8) 0,
					rgba(0, 0, 0, .4) 60%,
					rgba(0, 0, 0, 0) 100%);
		}

		.popular-thumb-item:hover,
		.thumb-item-flow:hover {
			background: rgba(255, 255, 255, .4);
			box-shadow: 0 0 5px 1mm #f0a;
			opacity: 1;
			transform: scale(1.01);
			transition: .2s;
		}

		.thumb-item-flow a:hover {
			color: #6fa;
			text-decoration: none;
		}

		.thumb-item-flow .img-in-ratio {
			background-size: cover;
			background-position: 50% 20%;
		}

		.search-box .input-group {
			width: 300px;
		}

		.suggestsearch {
			border: none;
		}

		.media:first-child {
			padding: 0;
		}

		.sidebar-content {
			clear: both;
			height: 140px;
			padding: 0 0 3mm;
		}

		.sidebar-content>a {
			width: 100px;
			height: 120px;
			padding: 0;
		}

		.sidebar-content>a img {
			width: 100%;
			height: 100%;
			margin: 0 auto;
			object-fit: cover;
		}

		.sidebar-content>h2>a {
			display: -webkit-box;
			-webkit-box-orient: vertical;
			-webkit-line-clamp: 3;
			height: auto;
			line-height: 1.4;
			overflow: hidden;
		}

		.modal-open {
			overflow: auto;
		}

		.suggestsearch {
			width: 370px;
		}

		img.myimg,
		.doreamon .cover img,
		img.media-object.img-thumb {
			height: 13.2vh;
			min-height: 160px;
		}

		.btn-success {
			background: #188;
		}

		.btn-warning {
			background: #972;
		}

		ul.list-chapters {
			max-height: none;
		}

		.card-body {
			padding: 0 2mm 2mm;
		}

		.info-manga .card-body {
			max-height: 46em;
			transition: .7s .3s;
			overflow: auto;
		}

		ul.list-chapters a li .chapter-name,
		ul.list-chapters a li .chapter-time {
			font-size: 13pt;
		}

		.rows-cmt {
			max-height: 200vh;
		}
	}

	@media screen and (max-width: 992px) {

		.list>.col-md-8,
		.row>.col-md-8:first-child,
		.col-lg-8,
		.info-manga {
			width: 100%;
			z-index: 1;
		}

		.container.list .col-md-4,
		.col-sm-8+.col-sm-4,
		.col-md-8+.col-md-4,
		.col-md-8 .col-md-4 {
			width: 100%;
		}
	}

	@media screen and (min-width: 992px) {
		.container.list {
			position: relative;
			max-width: 100%;
			overflow: hidden;
		}

		.col-md-8 {
			flex: 0 0 95%;
			max-width: none;
		}

		.list>.col-md-8,
		.row>.col-md-8:first-child,
		.col-lg-8,
		.col-lg-9,
		.info-manga {
			width: max(95%, calc(100% - 400px));
		}

		.container.list .col-md-4,
		.col-md-8+.col-md-4,
		.col-sm-8+.col-sm-4 {
			position: absolute;
			right: calc(5% - 440px);
			width: 440px;
			max-height: calc(100% - 90px);
			padding: 1em;
			margin: 0;
			background-color: rgba(140, 140, 140, .5);
			overflow: auto;
			border-radius: 1em 0 0 1em;
			box-shadow: 0 0 1mm #666;
			opacity: .5;
			z-index: 3;
			transition: all .8s;
		}

		.container.list .col-md-4:hover,
		.col-md-8+.col-md-4:hover,
		.col-sm-8+.col-sm-4:hover {
			right: 0;
			opacity: 1;
			box-shadow: 0 0 2mm #444;
			transition: all .3s .2s;
			backdrop-filter: blur(3mm);
			-webkit-backdrop-filter: blur(3mm);
		}
	}

	@media screen and (max-width: 650pt) {
		.thumb-item-flow {
			flex: 1 1 30%;
			max-width: none;
		}

		.a6-ratio {
			max-height: 20em;
		}
	}

	@media screen and (min-width: 650pt) {
		.thumb-item-flow {
			flex: 1 1 13em;
			max-width: 20em;
