style.css:
@-moz-document url("https://www.faceflow.com/app/") {

::-webkit-scrollbar {



      width: 10px;



      height: 10px;



      -webkit-border-radius: 10px;



      -moz-border-radius: 10px;



      border-radius: 0;



  }



  ::-webkit-scrollbar-button {



      width: 0;



      height: 0;



  }



  ::-webkit-scrollbar-thumb {



      background: #141719;



      border: 0 none #ffffff;



      border-radius: 0;



  }



  ::-webkit-scrollbar-thumb:hover {



      background: #141719;



  }



  ::-webkit-scrollbar-thumb:active {



      background: #141719;



  }



  ::-webkit-scrollbar-track {



      background: transparent;



      border: 0 solid transparent;



  }



  ::-webkit-scrollbar-track:hover {



      background: transparent;



  }



  ::-webkit-scrollbar-track:active {



      background: transparent;



  }



  ::-webkit-scrollbar-corner {



      background: transparent;



  }



  a {



      color: #6fb0bb;



  }



  #participantsListsContainer {



      background-color: #1f2123;



      border-right: 1px #141719 solid;



  }



  .chatWindow {



      background-color: #2f3136;



  }



  .chat-from {



      color: #c3c5cc;



  }



  #chat-input {



      background-color: #1f2123;



      border-top: 1px #1f2123 solid;



  }



  #enterMessage {



      background-color: #1f2123;



      color: #c3c5cc;



  }



  .message-wrap {



      background-color: #1f2123;



      color: #c3c5cc;



  }



  .you .message-wrap {



      background-color: #415263;



  }



  #chat-send {



      border-radius: 0 !important;



      background-color: #415263 !important;



      color: #c3c5cc !important;



      border: 2px #415263 solid !important;



  }



  .participants-container {



      background-color: #1f2123;



      border-right: 1px solid #1f2123;



  }



  .participants-container .profile-pic-container.you-participant span {



      background-color: #415263;



      color: #c3c5cc;



      border-color: #415263;



  }



  .participants-container .profile-pic-container span {



      background-color: #1f2123;



      color: #c3c5cc;



  }



  #participantsListsContainer .participant {



      border-bottom: 0;



      color: #c3c5cc;



  }



  #participantsListsContainer .male {



      color: #a9e8f1 !important;



  }



  #participantsListsContainer .female {



      color: #e2a0cd !important;



  }



  #participantsListsContainer .gender-none {

      color: #9dd4a6  !important;



  }


  .conversation-options {



      background-color: #141719;



      color: #c3c5cc;



  }



  .conversation-options a {



      color: #c3c5cc !important;



  }



  .close-conversation-main-window {



      display: none;



  }



  #communication-list {



      border-bottom: 2px solid #2f3136;



  }



  #communication-list .contact {



      background-color: #1f2123;



      color: #c3c5cc;



  }



  #communication-list {



      background-color: #1f2123;



      border-color: #1f2123;



  }



  #myDashboardBtn {



      background-color: #415263;



  }



  #communication-list .list-title {



      background-color: #415263;



      color: #c3c5cc;



  }


	#communication-list .contact.conversation-active, 
	#communication-list .contact:hover {
		background-color: #415263 !important;
		color: aliceblue;
  }

  #contacts-container .contact {



      border-top: 0;



  }

#contacts-list-filter {
	background-color: #1f2123;
    color: #c3c5cc;
    border: 0;
}


  header {



      background-color: #415263;



      border-bottom: 1px solid #141719;



  }



  a.btn, a.btn:hover {



      background-color: #415263;



      border-radius: 0;



  }



  body {



      background: black;



  }
}
