}

.fold-comments-sticky {
    background: rgba(34, 34, 34, 0.9) !important;
    box-shadow: 0 0 4px 8px rgba(34, 34, 34, 0.9) !important
}

.preview-expand {
    padding-bottom: 8px !important
}

.comment-icon i.fa-inverse {
    display: none !important
}

