style.css:
@-moz-document domain(wilxite.jumpjet.co.uk) {
#body {
    color: #eceef1;
}
input.headerTitle {
    color: #FFFFFF;
}
input.headerTitle:focus {
    color: #303031;
}
.background {
    background-color: #26272C!important;
}
.loadOverlay {
    background-color: rgba(0, 0, 0, 0);
}
.loader {
    width: 40px;
}
a {
    color: #14c9e4
}
a:hover {
    color: #00deff;
}
#user {
    background-color: #454a5f;
    border-bottom: none;
    box-shadow: 2px 2px 4px 1px rgba(5, 10, 10, 0.2);
}
#user > div a {
    color: #e0e0e0;
}
.widgets .widget {
    background-color: #3A3C44;
    border-bottom: none;
    box-shadow: 1px 1px 2px 1px rgba(5, 10, 10, 0.1);
}
.widgets .tabs .tabsTitle > div {
    background-color:#51535a
}
.widgets .tabs .tabsTitle > div.active {
    background-color: #3A3C44;
}
.ticketHeader {
    color:#000000;
}
.mce-tinymce, .ticketContent, .editable.mce-content-body {
    background-color: #FFFFFF;
    padding:10px !important;
    box-shadow: inset 1px 1px 2px 0px rgba(0, 0, 0, 0.5);
}
.editable.mce-content-body {
    color: #191818;
}
table.list thead tr td {
    background-color: #2E3037;
    text-shadow: none;
}
table.list thead td, table.list tfoot td {
    border-top: 1px solid #4c4d50;
    border-bottom: 1px solid #4c4d50;
}
table.list.alternateRows tbody tr:nth-of-type(2n+1) td {
    background-color: #2E3037;
    border-top: 1px solid #4c4d50;
    border-bottom: 1px solid #4c4d50;
}
table.list.alternateRows tbody tr:nth-of-type(2n) td {
    background-color: #2b2d33;
    border-top: 1px solid #4c4d50;
    border-bottom: 1px solid #4c4d50;
}
table.list td:first-child {
    border-left: 1px solid #4c4d50;
}
table.list td:last-child {
    border-right: 1px solid #4c4d50;
}
.actionsBar {
    background-color: #454a5f;
    border-top:none;
    box-shadow: -2px -2px 4px 1px rgba(5, 10, 10, 0.2);
}
.filters {
    border: 1px solid #4c4d50;
    background-color: #323335;
    box-shadow: 1px 2px 2px 0px rgba(0, 0, 0, 0.2);
}
table.list.alternateRows tbody tr.statusReadytoclose td {
    background-color: #326732;
}
table.list.alternateRows tbody tr.statusReadytoclose:hover td {
    background-color: #498a49;
}
table.list.alternateRows tbody tr.statusDraft td {
    background-color: #676767;
}
table.list.alternateRows tbody tr.statusDraft:hover td {
    background-color: #78797b;
}
table.list tbody tr:nth-of-type(n):hover td, table.list tbody tr.hover:nth-of-type(n) td {
    background-color: #5d6069;
}
.dropzone {
    background-color: #34353D;
}
.dropzone .dz-preview.dz-image-preview {
    background-color: #34353D;
}
#taskList li div {
    background-color: #202021;
    border: 1px solid #4c4d50;
}
#taskList li div:hover {
    background-color: #2f2f2f;
}
#taskList li.complete div {
    background-color: #326732;
}
.buttonBG {
    background-color: #8806ef !important;
    color: #FFFFFF !important;
    border-top: 1px solid #9053de;
    border-bottom: 1px solid #8738d0;
    text-shadow: 0px 1px 0px #862498;
    transition: all 0.1s ease;
}
.buttonBG:hover {
    background-color: #780ece !important;
}
.buttonBG:active {
    background-color: #6910b1 !important;
    border-top: 1px solid #9053de;
    border-bottom: 1px solid #8738d0;
}
#actions {
    background-color: #292933;
    box-shadow: 0px 0 2px 2px rgba(0, 0, 0, 0.4);
}
#actions #actionsButton::after {
    background-color: #292933;
}
.permissionsLink li {
    border: 1px solid #585858;
    background-color: #2c303a;
}
.dashboardWidget {
    background-color: #3A3C44 !important;
    border: 1px solid #525252 !important;
    border-bottom: 2px solid #444343 !important;
}
.dashboardWidget .taskList {
    background-color: #3A3C44 !important;
    border: 1px solid #4c4d50;
}
.actionBox {
    padding: 10px 12px;
    background-color: #232429;
    margin-bottom: 8px;
    box-shadow: 1px 1px 2px 0px rgba(0, 0, 0, 0.05);
    border: 1px solid #4c4d50;
    transition: 0.2s;
}
.actionDetail {
    background-color: #444444;
}
}
