style.css:
/* i really want this to be global */

th.checkbox input[type=checkbox], td.checkbox input[type=checkbox] {
    transform: scale(1.5);

}

.js-status {
   /*transform: scale(1.125);*/
}

td.img, .scroll-auto, .header {
    display: none;
}

.grid-container-sub {
    padding-left: 0.25em;
    padding-right: 0;
    padding-top: 0.25em;
	padding-bottom: 0.25em;
    margin: 0 0;
    box-shadow: rgba(23, 43, 77, 0.32) 1px 1px 4px -1px inset; 

}

.grid.selectable {
    margin-top: 0;
    margin-bottom: 0;
	margin: 10;
    border-width: 1px;
    border-color: black;
    box-shadow: rgba(23, 43, 77, 0.32) 0px 4px 8px -2px
}

.grid-title {
	margin: 0 0;
    box-shadow: rgba(23, 43, 77, 0.32) 0px 4px 8px -2px; 
    letter-spacing: 0.1em;
    display: block;
    border-width: 1px;
    border-color: #f9f9f9;
    background: #1f497d;
	color: white;
    border-style: solid;
}
.title.pull-left { margin: 3px 4px;  padding: 0; font-size: 0.8em; }
.title.pull-left .text-secondary { display: none; }

.grid-chart.pull-left.link-tooltip, .chart-minibar-custom, .chart-minibar, .column.column-valign.chart-minibar-column-graph {
    display: flex;
    flex: 1;
    width: 100%;
    padding: 0px 0px;
    display: none;
}

.grid-container {
    margin-bottom: 0;
    display: block;
    border-width: 2px;
    border-color:  #f1f1f1;
    border-style: solid;
}

.grid tr.oddSelected  { background: #e0ffff !important; }
.grid tr.evenSelected { background: #e0ffff !important; }
.grid tr.highlighted  { background: #e7f2e3!important; }
/*.odd    { background: #fff !important; }
.even   { background: #eee !important; }*/
tr:nth-child(odd) {
    background-color: #f1f1f1 !important;
}
tr:nth-child(even) {
    background-color: #f7f7f7 !important;
}
