@-moz-document domain("vk.com") {
body {
    background: white;
}
#page_header_cont .back {
    background: #ffffff;
    border-bottom: 1px solid #c5d0db;
}
.top_nav_link:hover {
    background: #00000011;
    text-decoration: none;
}
.top_nav_link.active {
    background: #00000011;
    text-decoration: none;
}
.top_home_link .top_home_logo {
    background: #618ab8;
    -webkit-mask-image: url(https://vk.com/images/svg_icons/ic_head_logo.svg);
}
.top_nav_link {
    color: #618ab8;
}
.top_nav_link.top_profile_link {
    padding-right: 8px;
}
.top_profile_arrow {
    display: none;
}
input.text.ts_input {
    border: 0;
    box-sizing: border-box;
    padding: 6px 6px 6px 19px;
    margin: 7px 0;
    height: 28px;
    line-height: 16px;
    width: 230px;
    border-radius: 14px;
    -o-transition: background-color 0.3s, color 0.05s;
    transition: background-color 0.3s, color 0.05s;
    background: #00000011 url(https://vk.com/images/svg_icons/ic_head_loupe.svg) no-repeat;
    padding-left: 19px;
    border-left: 8px solid transparent;
    background-position: left 7px;
    color: #ffffff;
}
.input_back {
    position: absolute;
    color: #618ab8;
    z-index: 90;
}
.ts_wrap .input_back_content {
    font-size: 13px;
    color: #618ab8;
    line-height: 16px;
}
.top_nav_btn#top_notify_btn .top_nav_btn_icon {
    -webkit-mask-image: url(https://vk.com/images/svg_icons/ic_head_notify.svg);
    background: #618ab8;
}
.top_nav_btn:hover {
