.im-page_classic .im-page--chat-header {
    position: fixed;
    top: 57px;
    z-index: 3;
    margin-top: -15px;
    margin-left: -1px;
    width: 550px;
    border: solid 1px #edeef0;
    border-top: none;
    border-left: solid 1px #edeef0;
    border-radius: 4px 4px 0 0;
}
.audio_layer_container .audio_page_player_wrap {
    z-index: 2;
    background-color: #ffffff;
    border-bottom: 1px solid #e7e8ec!important;
}
#audio_layer_tt {
    box-shadow: 0px 40px 40px 0 rgba(0, 0, 0, .3);
    border-top: 0;
    border-radius: 0 0 4px 4px;
    -webkit-perspective: none;
    top: -1px !important;
}
.eltt.eltt_bottom {
    -o-transform: translateY(-5px);
    transform: translateY(-5px);
}
.page_name, h2.page_name {
    font-size: 21px;
    line-height: 25px;
    font-weight: 400;
    -webkit-font-smoothing: subpixel-antialiased;
    -moz-osx-font-smoothing: auto;
    margin: -1px 0 -1px -1px;
    overflow: hidden;
    text-overflow: ellipsis;
    padding-left: 1px;
    color: #618ab8;
}
