style.css:
@-moz-document domain("wiselwisel.com") {
/* ---  */
#wrapper #content .post, #wrapper #sidebar #top, .bloque_lateral, #wrapper #content .post .notecontainer, .publi_dcha_300, .publi_izda_300, ul#menu_sup a  {
    background: #222;
}

#wrapper #sidebar .search form .query  {
    background: #bbb;
}

#header #header_right .fondo_menu_arriba_movil, #scrollToTop, #wrapper #content .fondo_post_abajo, #wrapper #content .fondo_post_arriba, #wrapper #sidebar .fondo_bloque_abajo, #wrapper #sidebar .fondo_bloque_arriba, #wrapper #sidebar .search form .submit, .social_buttons span.social_icon, .social_inner a.boton_lateral, .social_inner div.boton_online, ul#menu_sup li span.fondo_menu_abajo, ul#menu_sup li span.fondo_menu_arriba {
    /*background: url(https://i.imgur.com/JPUmEMn.png) no-repeat;*/
}

body, #wrapper #content .post .next_post a, #wrapper #content .post .previous_post a, .related_posts h2, #wrapper #content .post .strip-text-content a, .NA .strip-rec-link-title, #wrapper #content .post .title a, #wrapper #content .post .quote {
    color: #eee;
}
.publi_dcha_300, .publi_izda_300{
    color: #ddd;
}

#wrapper #content .post .copy, #wrapper #content .post .asker_container, #wrapper #sidebar, #wrapper #content .post .quotebg  {
    opacity: 1;
    color: #ccc;
}

#wrapper #content .post .copy a, #wrapper #sidebar #recentcomments a, #wrapper #content .post .volver_principal a, #wrapper #content .post a {
    color: #bd2d25;
}


#wrapper #content .post .footer, #wrapper #sidebar .bloque_lateral .heading, #wrapper #sidebar #top .page_mas a.mas  {
    border-color: #333;
    color: #ddd;
}

#wrapper #content .post .question, .video_error {
    color: #ccc;
    background: #444;
}


#wrapper #content .post .question .nipple {
    border-top: 12px solid #444;
}

.dsq-widget a.dsq-widget-user {
    color: #ddd!important;
}


#wrapper #content #navigation a, #wrapper #content #navigation.jump_pagination .current_page {
    background-color: #bbb;
    border: 2px solid #aaa;
}
}

@-moz-document url-prefix("https://wiselwisel.com/post/") {
@media screen and (min-width : 1310px) {
.fondo_post_arriba{
        margin: auto;
    }
#wrapper {
    width: 100%;
    max-width: 1300px;
}
#wrapper #content {
    width: 100%;
    max-width: 950px;
}
#wrapper #content .post {
    width: 100%;
}

#wrapper #content .post .post_wrap {
    max-width: 600px;
    margin: auto;
}
}
}
