a.minibutton code,
.milestone,
.keyboard-mappings th,
.api #header-wrapper .nav,
.marketing-nav a.selected,
#graph_data .tabs,
.org-nav-item.selected,
.edit-team-member:hover,
tr.commit,
.release-timeline .js-details-container,
