style.css:
@-moz-document domain("typing.com") {
body {
    background: #212121;
    min-height: 100% !important;
}
.wrapper {
    background: #212121;
    border-right: 5px solid #212121;
    border-left: 5px solid #212121;
    box-shadow: 0 0 25px rgba(0,0,0,.1);
    display: flex;
    flex-flow: column nowrap;
    max-width: 1800px;
    margin: 0 auto;
    min-height: 100vh;
    position: relative !important;
    }
.header {
    background-color: #212121;
    border-bottom: 1px solid #212122 !important;
}
html {
    color: #ffffff;
    font-family: museo-sans-rounded,sans-serif;
    font-size: 16px;
    font-weight: 500;
    line-height: 1.5 !important;
}
.logo--dk {
    background-image: url(https://www.typing.com/dist/student/images/logo-typing.svg) !important;
}
.fixedNav {
    background: #212121;
    box-shadow: 0 10px 30px rgba(0,0,0,.1);
    box-sizing: border-box;
    left: 0;
    position: fixed;
    top: 0;
    transition: all .25s ease-in-out;
    transform: translateY(-200%);
    width: 100%;
    z-index: 9998 !important;
}
.navLink.is-active.navLink--b {
    color: #ffffff !important;
}
.navLink--b {
    color: #ffffff !important;
}
.dropdown--b {
    border-radius: 10px 10px 0 0;
    border: 1px solid transparent;
    color: #ffffff;
    padding: 5px !important;
}
.hero {
    background-image: linear-gradient(to bottom,#000,#212121);
    background-color: #3295db;
    background-size: auto 100%;
    background-repeat: no-repeat;
    background-position: center !important;
}
.path {
    background: #2f2f2f !important;
}
.path.is-active {
    background-color: #3295db !important;
    border-radius: 10px 0 0 10px;
    border-color: transparent;
    box-shadow: 0 10px 30px rgba(0,0,0,.1);
    margin-left: -10px;
    margin-right: -20px;
    padding-right: 50px;
    position: relative;
    z-index: 2 !important;
}
.progress--a .progress-lt {
    background: #3295db;
    color: #fff;
    transition: all .25s ease-in-out !important;
}
.progress--a {
    background: #2f2f2f;
    border: 1px solid #d5d5d5;
    transition: all .25s ease-in-out !important;
}
.lesson {
    background: #383838 !important;
    border-radius: 10px;
    border: 1px solid #383838 !important;
    counter-increment: lesson;
    margin-bottom: 20px;
    position: relative;
    padding: 0 20px 20px !important;
}
.btn--b {
    background: #3295db;
    border-radius: 5px;
    color: #fff !important;
}
.btn--a {
    background: #fff !important;
    border-radius: 5px;
    color: #000 !important;
}
.chunk {
    background: #383838;
    flex: 1;
    margin-right: 2px;
    transition: all .25s ease-in-out;
    text-align: center;
    width: 100% !important;
}
.chunk.is-complete {
    background: #3295db !important;
}


.keyboard--a {
    background-color: transparent;
    border: 1px solid transparent !important;
}
.key--a {
    background-color: #0e0e0e;
    border-bottom: 1px solid #000;
    border-top: 1px solid #000;
    color: #ffffff !important;
}
.key--special .key-label {
    color: #fff !important;
}
.screen--card {
    background: #2a2a2a !important;
}
.screenBasic-lines {
    background-color: #2a2a2a !important;
}
.screenBasic-lines {
    align-items: center;
    display: flex;
    flex-wrap: wrap;
    width: 100%;
    will-change: transform;
    background-color: #2a2a2a;
    background-size: 100% 43px !important;
}
.screenBasic-letter {
    border-bottom: 3px solid transparent;
    color: #fafafa;
    display: inline-block;
    line-height: 1.5;
    margin: 5px 2px 5px 0;
    min-width: 14px !important;
}
}
