style.css:
@-moz-document url-prefix("https://app.glip.com") {
body {
  color: white!important;
}
.page-header, .stream-header.stream-header, .postslist, .round-button, .compose {
  background-color: rgb(54,57,62)!important;
}
.page-header {
  border-bottom: 1px rgb(37,40,44) solid!important;
}
.item {
  padding: 6px!important;
}
.calls-to-action-widgets, .item {
  background-color: rgb(50,54,59)!important;
  border-radius: 3px!important;
  border-style: solid!important;
  border-width: 1px!important;
  border-color: rgb(47,50,55)!important;
  border-left-style: solid!important;
  border-left-color: red!important;
  border-left-width: 4px!important;
}
.calls-to-action-widgets {
  margin: 10px!important;
}
.post_text, .calls-to-action-welcome-title, .plain {
  color: #dcddde!important;
  font-size: 0.9375rem!important;
  font-weight: 400!important;
}
#header-text {
  color: white!important;
  text-transform: lowercase!important;
  font-size: 18px!important;
  font-weight: 600!important;
  font-family: Whitney, "Helvetica Neue", Helvetica, Arial, sans-serif!important;
  background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='16' height='16' viewBox='0 0 16 16' class='channelIcon-MsmKOO'><path fill='rgb(138, 142, 148)' d='M2.27333333,12 L2.74666667,9.33333333 L0.08,9.33333333 L0.313333333,8 L2.98,8 L3.68666667,4 L1.02,4 L1.25333333,2.66666667 L3.92,2.66666667 L4.39333333,0 L5.72666667,0 L5.25333333,2.66666667 L9.25333333,2.66666667 L9.72666667,0 L11.06,0 L10.5866667,2.66666667 L13.2533333,2.66666667 L13.02,4 L10.3533333,4 L9.64666667,8 L12.3133333,8 L12.08,9.33333333 L9.41333333,9.33333333 L8.94,12 L7.60666667,12 L8.08,9.33333333 L4.08,9.33333333 L3.60666667,12 L2.27333333,12 L2.27333333,12 Z M5.02,4 L4.31333333,8 L8.31333333,8 L9.02,4 L5.02,4 L5.02,4 Z' transform='translate(1.333 2)'></path></svg>")!important;
    background-repeat: no-repeat;
    background-position: left center;
    padding-left: 18px;
}
.post-body {
  border-top: 1px solid rgb(75,73,81)!important;
}
.post.merged .post-body, .post.last-merged .post-body {
  border-top: 0!important;
  border-top-width: 0px!important;
  border-top-style: initial!important;
  border-top-color: initial!important;
}
.rightrail {
  border-left: 1px solid #222!important;
  background-color: rgb(47,49,54)!important;
}
.mentions {
  background-color: #333!important;
}
.at_mention_post.at_mention_post.at_mention_post {
  color: rgb(114,137,218)!important;
  padding-left: 12px!important;
  font-weight: 400!important;
}
.at_mention_post::before {
  content:"@"!important;
}
a.at_mention_compose.at_mention_compose::before {
  background-color: transparent!important;
  border: none!important;
}
.chat-textarea.chat-textarea.chat-textarea {
  color: white!important;
  background-color: rgb(72,76,82)!important;
  border: 1px solid transparent!important;
  padding-top: 12px!important;
  padding-bottom: 12px!important;
}
.compose_type.compose_type {
  height: 44px!important;
  background-color: rgb(72,76,82)!important;
  border-width: 0px!important;
}
.relative {
  margin-top: 4px!important;
  margin-bottom: 4px!important;
  border-width: 0px 1px 0px 0px!important;
  border-color: rgb(91,94,100)!important;
  border-style: solid!important;
  height: 36px!important;
}
.compose_chat {
  padding: 0 0 4px 39px!important;
}
.compose_type.compose_type.compose_type.compose_type {
  border: none!important;
}
.dropdown {
  background-color: rgb(47,49,54)!important;
}
.rollover {
  color: white!important;
}
.progress_bar {
  border-radius: 0px 8px 8px 0px!important;
}
.sep label {
  color: white!important;
  border: none!important;
  background-color: rgb(54,57,62)!important;
  min-width: 0px!important;
  top: 0px!important;
}
.post {
  z-index: 1!important;
  border-radius: 0!important;
}
.sep {
  border: none!important;
  margin: 0px!important;
}
ol.activity {
  display: inline!important;
  padding-right: 3px!important;
}
.timestamp.timestamp.timestamp {
  display: inline!important;
  position: unset!important;
  font-size: 0.75rem!important;
  color: hsla(0,0%,100%,.2)!important;
  font-weight: 400!important;
}
.post .activity {
  font-size: 1rem!important;
  font-weight: 500!important;
  color: white!important;
}
a {
  color: #0096cf!important;
}
.topbar-search-input-container input {
  background-color: rgb(72,76,82)!important;
}
#topbar {
  background-color: rgb(54,57,62)!important;
  border-bottom: 1px rgb(37,40,44) solid!important;
}
.merged .post-body .timestamp, .last-merged .post-body .timestamp {
  display: none!important;
}
}
