style.css:
@-moz-document domain(baseconnect.in) {
  body {
    background: #2e3e4e
  }
  
  header.header {
   background: #222
  }
  
  h1.heading,
  .top-nav .username,
  .form-control,
  .Select-control,
  h4.col-md-12,
  .table>tbody>tr>td.panel__big a,
  .table>tbody>tr>td,
  h2.small_heading.hit-result-bar__heading,
  h2.no-margin-top
  .editor_heading_top_time,
  .clocking__totalBox h4,
  .clocking__sumOfDetailsBox h4
  h4.bs-panel-text
  {
    color: #fefefe
  }
  
  .top-nav .username {
    color: #fefefe !important
  }
  table.bs-panel,
  .ReactTabs__TabPanel,
  .admin__whiteBox
  .bs-panel
  {
    background: #444;
    border: none
  }
  .table>tbody>tr>td,
  .linkBox__link,
  #history_fields table,
  #history_fields table tbody tr
  {
    border-color: #5f5f5f
  }
  
  .linkBox__link {
    background: #444
  }
  
  .sk-panel .sk-panel__content .sk-select select,
  .sk-panel .sk-panel__content .sk-input-filter form,
  .sk-panel .sk-panel__content .sk-input-filter form input,
  .sk-action-bar-row .sk-panel .sk-input-filter .Select .Select-control .Select-placeholder,
  .sk-panel .sk-panel-content .Suggest .react-autosuggest__container input
  {
    background: #2e3e4e !important
  }
  
  .sk-tabs-option.is-active {
    border-color: #00a699;
    color: #00a699;
    background: none
  }
  
  .sk-tabs {
    border-color: #00a699
  }
  
  .form-control,
  .Select-control
  {
    background-color: #444
  }
}
