	background: linear-gradient(to bottom, #da3a04 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}


.favorites-container .favorite-list li,
.recents-tile-list li,
.slick-track li
{
    box-shadow: 21px 24px 23px #000!important;
}






}

@-moz-document domain("siriusxm.com") {
/*__________________________________________________________________________________*/

#hp-2016 .sxmrm-module-packages .container .package-col,
.center-buttons-list__item,
.favorites-container .favorite-list li,
.recents-tile-list li,
.slick-track li
{
