style.css:
@-moz-document domain("blog.csdn.net") {
/* 默认展开文章内容 */
#article_content {
    height: auto !important;
}
div.hide-article-box {
    display: none;
}
/*----------------*/
}

@-moz-document domain("blog.csdn.net") {
/* 去广告、去登录提示 */ 
.pulllog-box, 
#dmp_ad_58,
#asideFooter,
#kp_box_476,
#adContent, 
.recommend-ad-box, 
.csdn-tracking-statistics.mb8.box-shadow {
    display: none !important; 
}
/*----------------*/

/* 去除部分按钮 */
.app-btn, 
.vip-caise, 
.gitChat.baidu-app-btn {
    display: none !important; 
}
/*----------------*/

}

@-moz-document domain("blog.csdn.net") {
/* 宽屏调整 */
/* Header调整 */
.csdn-toolbar {
    box-sizing: border-box;
}
body .csdn-toolbar .container {
    margin: 0;
    width: auto !important;
}


/* 内容界面调整 */
#mainBox {
    width: auto !important;
    padding: 12px;
    box-sizing: border-box;
}
.container main {
    margin-right: 50px;
}

/* Toolbar调整 */
.tool-box {
    width: auto !important;
}

/* 
aside {
    position: static !important;
    width: 300px !important;
}
*/

/* 下方推荐调整 */
.recommend-box .recommend-item-box.recommend-download-box .content {
    max-width: none !important;
}
/*----------------*/
}

@-moz-document domain("blog.csdn.net") {
/* 侧栏 */
aside {
    display: flex;
    flex-direction: column;
    position: fixed !important;
    z-index: 9999;
    width: 300px !important;
    top: 50px !important;
    background: #F2F2F2;
    height: calc(100% - 100px);
    min-height: 0;
    max-height: calc(100% - 100px);
    overflow: hidden;
    border-radius: 2px;
    box-shadow: 0 0 4px rgba(0,0,0,0.4);
}

#asideProfile {
    flex-shrink: 0;
    flex-grow: 0;
}
.flexible-box .text-center {
    display: none;
}
.aside-box {
    min-height: 0;
    margin-bottom: 4px !important;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.15) !important;
}
.aside-box:hover {
    flex-grow: 1;
    overflow: auto;
}
.aside-content {
    display: none;
    min-height: 0;
    max-height: max-content !important;
    padding: 8px 16px !important;
    overflow: hidden !important;
}
.aside-box:hover .aside-title {
    position: absolute;
    z-index: 999;
    width: 100%; 
}
.aside-box:hover .aside-content {
    margin-top: 35px;
    display: block;
}
/*----------------*/
}

@-moz-document domain("blog.csdn.net") {
/* 整体样式调整 */
/* 背景调整 */
body {
    background: #f8f8f8 !important;
}

/* 侧栏样式调整 */
.aside-title {
    font-size: 14px !important;
    line-height: normal !important;
    padding: 8px 16px !important;
    background: #f5f5f5 !important;
}
.aside-title::before {
    display: none !important;
}
aside ul li a{
    color: #333 !important;
}
/* 去除部分信息 */
.custom-box, 
.badge-box {
    display: none !important;
}

/*----------------*/
}

/*[[aside-mode]]*/

options.value:
@-moz-document domain("blog.csdn.net") {
/* 侧栏 */
.container main {
    width: calc(100% - 60px);
}
aside {
    left: -290px !important;
    transition: left 0.5s;
}
aside:hover {
    left: -10px !important;
}
}

@-moz-document domain("blog.csdn.net") {
/* 侧栏 */
.container main {
    width: calc(100% - 360px);
}
aside {
    left: 10px !important;
    transition: left 0.5s;
}
}
