style.css:
@-moz-document domain("www.thingiverse.com")
{
/* Homepage Boxes */
.content-box {
	position: relative;
}

.content-box .more {
	position: absolute;
	top: 10px;
	right: 10px;
	display: inline-block;
	background-color: #2A89FA;
	padding: 5px 20px;
	border-radius: 5px;
	color: #fff;
	text-transform: uppercase;
	font-size: 12px;
}

/* Round Box Corners*/
.button,
.blue-button,
.white-button,
.grey-button,
.taglist a,
.user-counts .user-count,
.profile-card,
.item-list-interactions .item-interactions,
.block_nav,
.groups-container .group-mini,
.toggle-thingiview,
.toggle-fullsize,
.thing-img,
.gallery-photo,
.gallery,
.thing-card,
.dashboard_button,
.toggle_follow, 
.preview_small,
.thing-thumb img,
.thing-mini,
.search-form,
.results-container .item,
.slides .spotlight,
.profile-user-info a.view-analytics, 
.comment-body,
.comment-form .edit,
.comment-form .attachment-assets,
.results-container .dashboard-notification,
.content-box,
.notice.info,
.user-search{
	border-radius: 7px !important;
	overflow: hidden !important;
}

/* Menu */
.site-header .login{
    border-color:#e1e1e1;
}
.site-header .login .login-box{
    border:none;
}

/* Dropdown Nav */
.dropdown-menu {
	border-radius: 0 0 5px 5px;
	-webkit-box-shadow: 0 5px 10px rgba(0, 0, 0, .2);
	box-shadow: 0 5px 10px rgba(0, 0, 0, .2);
}

.dropdown-menu * {
	border-color: #eee;
}

.dropdown-menu a {
	-webkit-transition: 100ms;
	-o-transition: 100ms;
	transition: 100ms;
	margin: 0 !important;
	padding: 8px !important;
}

.dropdown-menu a:hover {
	background-color: #2A89FA !important;
	color: #fff !important;
}

.dropdown-menu a:hover * {
	color: #fff !important;
}

.dropdown-menu .login-settings {
	padding-left: 0 !important;
}

.dropdown-menu .login-logout {
	background-color: #dbdbdb;
	color: #666 !important;
	border: none !important;
}

.dropdown-menu .login-logout:hover {
	background-color: #bb1500 !important;
	color: #eee !important;
}

.dropdown-menu .login-logout:before {
	display: none;
}

/* Thing Lists */
.thing-card, .results-container .item, .items-page .item, .result-page .item{
    border-color: #f2f2f2;
    overflow: visible;
}
.thing-card:hover .item-header, .results-container .item:hover .item-header, .items-page .item:hover .item-header, .result-page .item:hover .item-header{
    background: linear-gradient(to bottom, rgba(0,0,0,0.2) 0%, rgba(0,0,0,0) 100%);
}

.item .item-header * {
    text-shadow:0 0 3px rgba(0, 0, 0, .9);
}

/* Thing Pages */
.button {
	-webkit-transition: 200ms;
	-o-transition: 200ms;
	transition: 200ms;
}

.gallery-left-arrow {
	border-radius: 0 5px 5px 0;
}

.gallery-left-arrow:before {
	padding: 0 25px;
}

.gallery-right-arrow {
	border-radius: 5px 0 0 5px;

}

.gallery-right-arrow:before {
	padding: 0 15px;
}

.block_nav .block_nav_item {
	-webkit-transition: 200ms;
	-o-transition: 200ms;
	transition: 200ms;
}


/* User Pages */
.groups-container .group-mini {
	padding: 20px;
	display: inline-block;
	height: auto;
}

/* Activity Page */
.dashboard-analytics .read_more {
	display: inline-block;
	background-color: #2A89FA;
	padding: 5px 20px;
	border-radius: 5px;
	color: #fff;
	text-transform: uppercase;
	font-size: 12px;
	height: auto;

}

/* Dashboard Page */
.dashboard-notification {
    border-top:8px solid #2A89FA;
}

/* Profile Page */
.profile-user-info a.view-analytics {
    background-color:#2A89FA !important;
}
.profile-user-info a.view-analytics:before {
 filter: invert();
    opacity:.9
}
.profile-user-info a.view-analytics span {
    opacity:.9;
    border-color:rgba(0,0,0, .1);
    border-width:1px;
    text-align:center;
    color:#fff;
    text-shadow:1px 2px 0 rgba(0,0,0,0.4);
        transition: 200ms;
}
.profile-user-info a.view-analytics:hover span {
    opacity:1;
    color:rgba(255,255,255, .7);
}

/* Not Selectable */
.loginbox-section a, .read_more, .site-header-nav a, .thing-page-actions a, .block_nav a, .thing a, .thing .card-img {
      -webkit-user-drag: none;
  -khtml-user-drag: none;
  -moz-user-drag: none;
  -o-user-drag: none;
  user-drag: none;
}

/* Fix Analytics CSS */
#ui-datepicker-div {
    background:rgba(255,255,255, .9);
    padding:10px 20px;
    border:1px solid #ddd;
    border-top-width:8px;
    border-radius:0 0 10px 10px;
}
.ui-datepicker-header {
    padding:5px 0;
}
.ui-datepicker-prev, .ui-datepicker-next {
    cursor: pointer;
    background-color:#5BA2F7;
    padding:3px 5px;
    color:#fff;
    border-radius:5px;
}
.ui-datepicker-prev {
    float:left;
}
.ui-datepicker-next {
    float:right;
}

.ui-datepicker-title {
    text-align:center;
    font-weight:600;
    font-size:13px;
    padding:5px 0;
}

.ui-datepicker-calendar th {
    padding:6px;
    text-align:center;
}


}
