style.css:
@-moz-document domain("soundcloud.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.sc-button,
.sc-classic .commentForm__wrapper,
.sc-classic .playButton.m-stretch,
.sc-tag, .sc-tag:visited,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.playableTile__mainHeading
{
	font-size: 1.5rem!important;
}

.sc-type-small,
.systemPlaylistBannerItem__trackTitle,
.systemPlaylistBannerItem__username
{
	font-size: 1.2rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.playControlsPanel,
.searchOptions__navigationItem.active:after
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.playableTile__description {
    top: 11px!important;
}

.stationTile__description {
    top: 16px!important;
}

.sound__waveform {
    margin-bottom: 2rem!important;
}

.g-nav-item.active {
    margin-left: 1px!important;
}

.commentPopover.small.visible .commentPopover__username,
.commentPopover.small.visible .commentPopover__body {
    padding-top: 22px!important;
}

/* Margins 
_____________________________________________________________________________________________________*/

.badgeList__item,
.collection__section,
.dialog,
.l-inner-fullwidth,
.l-search .l-fixed-left,
.modal__modal,
.modularHome.lazyLoadingList,
.modularHome__item
{
	margin: 1.2rem!important;
}

.l-search .l-main
{
	margin-left: 18rem!important;
}

.search {
	margin-top: 5rem;
}

.sc-classic .sound__footer {
    margin-top: -31px!important;
}

.playableTile__artwork {
	margin: 3px 6px!important;
}

.systemPlaylistBannerItem__actions {
    right: 6rem;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.badgeList__item,
.collection__section,
.dialog,
.l-search .l-fixed-left,
.l-search .l-main,
.listenDetails__trackList,
.modal__modal,
.modularHome.lazyLoadingList,
.modularHome__item,
.search,
.soundList__item,
.stream,
.systemPlaylistBannerItem__content
{
	padding: 1.2rem!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

.systemPlaylistBannerItem {
	height: auto!important;
}

.badgeList__item {
    height: 28rem!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.sc-tag>.sc-tagContent,

body {
	max-width: none!important;
}

.sc-button-small.sc-button-icon:not(.sc-button-stretch-icon), .sc-button-small.sc-button-responsive:not(.sc-button-stretch-icon) {
    min-width: 63px!important;
}

.g-scrollable-v .g-scrollable-inner,
.sc-classic .l-container.l-fullwidth {
	width: auto!important;
}

.systemPlaylistBannerItem {
	width: 98%!important;
}

.sc-classic .playControls__soundBadge {
    width: 55rem;
}

}

@-moz-document domain("soundcloud.com") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {
	
	.sc-classic .l-container {
		width: 98%;
	}

	.l-listen__mainContent>.l-about-left {
		width: 11rem;
	}

	.sc-classic .l-listen__mainContent>.l-about-right {
		padding-left: 13rem;
	}
	
}

@media (min-width: 1080px) and (max-width: 1920px) {
		
	.sc-classic .l-container {
		width: 98%;
	}

	.l-listen__mainContent>.l-about-left {
		width: 11rem;
	}

	.sc-classic .l-listen__mainContent>.l-about-right {
		padding-left: 13rem;
	}
	
}
}

@-moz-document domain("soundcloud.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.header__navMenu>li>a.selected, .header>li>a:focus,
.sc-ministats-small,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.sc-button,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.systemPlaylistBannerItem__username,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

.sc-type-small,

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.sc-classic .playbackTimeline__duration,
.sc-classic .playbackTimeline__timePassed,

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.errorTitle,
.waveformWrapper__empty
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

selector
{
	color: #fff!important;
}


}

@-moz-document domain("soundcloud.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

.callout__bubble::before {
    background-color: #310202!important;
}

.sc-classic .tileGallery.m-hasItemsForward .tileGallery__sliderPeekForward {
    background-color: #31020298!important;
}

.sc-classic .tileGallery.m-hasItemsForward .tileGallery__sliderPeekForward:hover {
    background-color: #310202d8!important;
}

.sc-button-large.sc-button-play:before,
.sc-button-medium.sc-button-copylink:before,
.sc-button-medium.sc-button-repost:before,
.sc-button-medium.sc-button-share:before,
.sc-button-queue:before,
.sc-button-selected.sc-button-medium.sc-button-like:before,
.sc-button-small.sc-button-copylink:before,
.sc-button-small.sc-button-like:before,
.sc-button-small.sc-button-more:before,
.sc-button-small.sc-button-repost:before,
.sc-button-small.sc-button-share:before,
.sc-classic .playButton.m-stretch:before,
.sc-classic .queue__itemsHeight,
.sc-classic .repeatControl:before,
.sc-classic .shuffleControl:before
{
	background-image: none!important;
}

/* Background 
_____________________________________________________________________________________________________*/

selector
{
	background: #060505!important;
	background-color: #060505!important;
}

selector
{
    background-color: #1a1a1a!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.backgroundGradient__buffer,
.laiFvd,
.profileHeaderBackground,
.sc-button,
.sc-classic .commentForm__wrapper,
.sc-classic .playControls__bg,
.sc-classic .queueItemView.m-active,
.sc-classic .queueItemView:hover,
.sc-classic .queue__itemWrapper,
.sc-classic .selectionPlaylistBanner,
.sc-classic .soundBadge__additional,
.sc-classic .sound__soundActions,
.searchTitle,
.trackItem:not(.m-disabled).active .trackItem__additional,
.trackItem:not(.m-disabled).hover .trackItem__additional
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Orange 
_____________________________________________________________________________________________________*/

.frontHero__createAccountButton,
.itTAAq
{	
	background: radial-gradient(circle, #ca3805a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.frontHero__createAccountButton::before,
.itTAAq::before
{
	background: radial-gradient(circle, #000 0%, #ca3805 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.frontHero__createAccountButton:hover::before,
.itTAAq:hover::before
{
  opacity: 1!important;
}

/* Green 
_____________________________________________________________________________________________________*/

[aria-label="Follow"]
{	
	background: radial-gradient(circle, #228005a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

[aria-label="Follow"]::after
{
	background: radial-gradient(circle, #000 0%, #228005 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

[aria-label="Follow"]:hover::after {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

.collection__section,
.errorPage__inner,
.l-inner-fullwidth,
.l-search .l-fixed-left,
.listenDetails__trackList,
.modularHome.lazyLoadingList,
.search,
.stream,
.trendingTracks
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.backgroundGradient,
.badgeList__item,
.callout__bubble,
.dialog,
.fnCoOQ,
.modal__modal,
.modularHome__item,
.playlistConsumerSubUpsell__messageBox,
.sc-classic .queue,
.soundList__item
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.backgroundGradient:hover,
.badgeList__item:hover,
.callout__bubble:hover,
.collection__section:hover,
.dialog:hover,
.errorPage__inner:hover,
.fnCoOQ:hover,
.l-inner-fullwidth:hover,
.l-search .l-fixed-left:hover,
.listenDetails__trackList:hover,
.modal__modal:hover,
.modularHome.lazyLoadingList:hover,
.modularHome__item:hover,
.playlistConsumerSubUpsell__messageBox:hover,
.sc-classic .queue:hover,
.search:hover,
.soundList__item:hover,
.stream:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

.compactTrackListItem.clickToPlay.active,
.searchOptions__navigationItem.active,
.systemPlaylistBannerItem:not(.m-disabled).m-overlayOpen,
.systemPlaylistBannerItem:not(.m-disabled).m-playing
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

.commentsList__item:hover,
.compactTrackList__item:hover
{
	background: linear-gradient(to bottom, #470303 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/* Blue Gradient 
_____________________________________________________________________________________________________*/

.sc-classic .playbackTimeline__progressBar
{
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
}

/* Orange Gradient 
_____________________________________________________________________________________________________*/

.commentFormDisabled,
.playControls__inner
{
	background: linear-gradient(to bottom, #da3a04 0%, #000 100%)!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

.sc-classic .header
{
	background: linear-gradient(to bottom, #424040 0%, #000 100%)!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.systemPlaylistBannerItem:not(.m-disabled):focus,
.systemPlaylistBannerItem:not(.m-disabled):hover,
.trackItem.active,
.trackItem:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

.sc-tag, .sc-tag:visited
{
	background: radial-gradient(ellipse at center, #9a9d00 0%,#0e0e0e 70%)!important;
	padding: 13px 26px!important;
}

.header__navMenu>li>a.selected, .header>li>a:focus
{
	background: radial-gradient(ellipse at center, #00809d 0%,#0e0e0e00 70%)!important;
	padding: 13px 26px!important;
}

}

@-moz-document domain("soundcloud.com") {
/* Image Settings */

.sc-classic .badgeList__item,
.sc-classic .userSuggestionList__item,
.soundBadgeList__item,
.tileGallery__sliderPanelSlide,
.usersList__item
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.compactTrackListItem.clickToPlay.active,
.sc-classic .badgeList__item:hover,
.sc-classic .userSuggestionList__item:hover,
.soundBadgeList__item:hover,
.systemPlaylistBannerItem:not(.m-disabled).m-overlayOpen,
.systemPlaylistBannerItem:not(.m-disabled).m-playing,
.tileGallery__sliderPanelSlide:hover,
.usersList__item:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

.modularHome__item,

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/


img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}
}

@-moz-document domain("soundcloud.com") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

.sc-classic .repeatControl.m-none,
.sc-classic .shuffleControl,
.sc-classic .notificationIcon:before {
    top: 7px!important;
    opacity: 1;
}

.sc-classic .playbackSoundBadge:not(.m-queueVisible) .playbackSoundBadge__queueIcon {
    fill: #f6f3f3;
}

/* Add to Next up _______________________________________________________________*/

[aria-label="Add to Next up"] {
	background: url(https://i.ibb.co/mcr61Wr/add2.png) no-repeat!important;
	box-sizing: border-box;
}

[aria-label="Add to Next up"] {
    background-size: 20px!important;
    height: 21px!important;
    padding-left: 6px!important;
    width: 1px!important;
}

/* Arrows _______________________________________________________________________*/

.sc-classic .tileGallery__slideForwardButton:after {
    transform: none;
}

/* Up _________________*/

selector
{
	background: url(https://i.ibb.co/bvZmSFj/red-up.png) no-repeat!important;
	box-sizing: border-box;
}

/* Down _________________*/

selector
{
	background: url(https://i.ibb.co/z7MRv74/direction-arrow-down.png) no-repeat!important;
	box-sizing: border-box;
}

/* Left _________________*/

.tileGallery__slideBackwardButton
{
	background: url(https://i.ibb.co/mRKFvH8/direction-arrow-left.png) no-repeat!important;
	box-sizing: border-box;
}

/* Right _________________*/

.sc-classic .tileGallery__slideForwardButton
{
	background: url(https://i.ibb.co/LQDKzQT/direction-arrow-right.png) no-repeat!important;
	box-sizing: border-box;
}

.sc-classic .tileGallery__slideForwardButton,
.tileGallery__slideBackwardButton
{
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 1px!important;
}

/* Follow _______________________________________________________________________________*/

.sc-button-small.sc-button-follow:before
{
	background: url(https://i.ibb.co/xXYCdyM/user-add-icon.png) no-repeat!important;
	box-sizing: border-box;
}

.sc-button-small.sc-button-follow:before
{
    background-size: 20px!important;
    height: 21px!important;
    padding-left: 26px!important;
    width: 1px!important;
}

/* Comment _________________________________________________________________________________*/

.sc-icon-large.sc-icon-comment,
.sc-ministats-small.sc-ministats-comments:before
{
	background: url(https://i.ibb.co/98nZdCB/comment1.png) no-repeat!important;
	box-sizing: border-box;
}

.sc-icon-large.sc-icon-comment
{
    background-size: 20px!important;
    height: 21px!important;
    padding-left: 26px!important;
    width: 1px!important;
}

.sc-ministats-small.sc-ministats-comments:before
 {
    background-size: 1rem!important;
    height: 1rem!important;
    padding-left: 1rem!important;
    width: 1px!important;
}

/* Copy Link _______________________________________________________________________________*/

.sc-button-medium.sc-button-copylink:before,
.sc-button-small.sc-button-copylink:before
{
	background: url(https://i.ibb.co/drymw1D/links1.png) no-repeat!important;
	box-sizing: border-box;
}

.sc-button-medium.sc-button-copylink:before,
.sc-button-small.sc-button-copylink:before
{
    background-size: 20px!important;
    height: 21px!important;
    padding-left: 21px!important;
    width: 1px!important;
}

/* Heart ________________________________________________________________________________*/

/* On ___________________*/

.sc-ministats-medium.sc-ministats-likes:before,
.systemPlaylistBannerItem__actions [aria-label="Unlike"]
{
	background: url(https://i.ibb.co/FB9kX4W/healthy-heart.png) no-repeat!important;
	box-sizing: border-box;
}

/* Off ___________________*/

.sc-ministats-small.sc-ministats-likes:before
{
	background: url(https://i.ibb.co/cD452WP/healthy-hear-darkt-32x32.png) no-repeat!important;
	box-sizing: border-box;
}

.sc-ministats-medium.sc-ministats-likes:before,
.systemPlaylistBannerItem__actions [aria-label="Unlike"]
{
    background-size: 20px!important;
    height: 21px!important;
    padding-left: 21px!important;
    width: 1px!important;
}

.sc-ministats-medium.sc-ministats-likes:before
{
    background-size: 20px!important;
    height: 21px!important;
    padding-left: 21px!important;
    width: 1px!important;
}

.sc-ministats-small.sc-ministats-likes:before
{
    background-size: 1rem!important;
    height: 1rem!important;
    padding-left: 1rem!important;
    width: 1px!important;
}

/* Messages ________________________________________________________________________*/

.sc-button-medium.sc-button-message:before,
.sc-classic .notificationIcon.messages:before {
	background: url(https://i.ibb.co/GnWPVcz/email.png) no-repeat!important;
	box-sizing: border-box;
}

.sc-button-medium.sc-button-message:before,
.sc-classic .notificationIcon.messages:before {
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 10px!important;
}

/* More _______________________________________________________________________________*/

[aria-label="More"]
{
	background: url(https://i.ibb.co/gzwX32y/more.png) no-repeat!important;
	box-sizing: border-box;
}

[aria-label="More"]
{
    background-size: 20px!important;
    height: 21px!important;
	margin: .3rem 2rem;
    padding-left: 6px!important;
    width: 1px!important;
}

/* Notification ________________________________________________________________________*/

.sc-classic .notificationIcon.activities:before {
	background: url(https://i.ibb.co/8Px680k/Notification.png) no-repeat!important;
	box-sizing: border-box;
}

.sc-classic .notificationIcon.activities:before {
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 10px!important;
}

/* Pause ____________________________________________________________________*/

.sc-classic .playControl.playing
{
	background: url(https://i.ibb.co/vkjYYs8/red-pause.png) no-repeat!important;
	box-sizing: border-box;
}

.sc-classic .playControl.playing:hover
{
	background: url(https://i.ibb.co/D7QSSmZ/red-pause-hover.png) no-repeat!important;
	box-sizing: border-box;
}

.sc-classic .playControl.playing:hover,
.sc-classic .playControl.playing
{
	background-size: 35px!important;
	height: 36px!important;
	padding-left: 36px!important;
	width: 1px!important;
}

/* Play ______________________________________________________________________________*/

.sc-classic .playButton.m-stretch,
.sc-classic .playControl,
.sc-ministats-medium.sc-ministats-plays:before,
.sc-ministats-small.sc-ministats-plays:before
{
	background: url(https://i.ibb.co/7VB6KK3/red-play.png) no-repeat!important;
	box-sizing: border-box;
}

.sc-classic .playButton.m-stretch:hover,
.sc-classic .playControl:hover,
.sc-ministats-medium.sc-ministats-plays:hover:before,
.sc-ministats-small.sc-ministats-plays:hover:before
{
	background: url(https://i.ibb.co/qdS3kwB/red-play-hover.png) no-repeat!important;
	box-sizing: border-box;
}

.sc-classic .playButton.m-stretch,
.sc-classic .playButton.m-stretch:hover
{
    background-size: 65px!important;
    height: 66px!important;
    padding-left: 66px!important;
    width: 1px!important;
}

.sc-classic .playControl,
.sc-classic .playControl:hover
{
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 1px!important;
}

.sc-ministats-medium.sc-ministats-plays:before,
.sc-ministats-medium.sc-ministats-plays:hover:before
{
    background-size: 20px!important;
    height: 21px!important;
    padding-left: 21px!important;
