      -webkit-flex: 0 0 auto;
      -ms-flex: 0 0 auto;
      flex: 0 0 auto;
      padding-right: 1.25rem;
      padding-left: 1.25rem;
      -ms-flex-preferred-size: 25%;
      flex-basis: 25%;
      max-width: 25%
    }
  }

  .c-hamburger {
    display: block;
    position: fixed;
    overflow: hidden;
    margin: 0;
    padding: 0;
    width: 80px;
    height: 80px;
    font-size: 0;
    text-indent: -9999px;
    appearance: none;
    border-radius: none;
    border: none;
    cursor: pointer;
    transition: background 0.2s;
    z-index: 10
  }

  .c-hamburger span {
    display: block;
    position: absolute;
    top: 44px;
    left: 18px;
    right: 18px;
    height: 3px;
    background: #fcf7e4;
    border-radius: 50px
  }

  .c-hamburger span::before,
  .c-hamburger span::after {
    position: absolute;
    display: block;
    left: 0;
    width: 100%;
    height: 3px;
    border-radius: 50px;
    background-color: #fcf7e4;
    content: ""
  }

  .c-hamburger span::before {
    top: -15px
  }

  .c-hamburger span::after {
    bottom: -15px
  }

  .c-hamburger:focus {
    outline: none
  }

  .c-hamburger--htx {
    left: auto;
    right: 5px;
    top: 0px
  }

  .c-hamburger--htx span {
    transition: background 0s 0.2s
  }

  .c-hamburger--htx span::before,
  .c-hamburger--htx span::after {
    transition-duration: 0.2s, 0.2s;
    transition-delay: 0.2s, 0s
  }

  .c-hamburger--htx span::before {
    transition-property: top, transform
  }

  .c-hamburger--htx span::after {
    transition-property: bottom, transform
  }

  .c-hamburger__input:checked+.c-hamburger--htx span {
    background: none
  }

  .c-hamburger__input:checked+.c-hamburger--htx span::before {
    top: 0;
    transform: rotate(45deg)
  }

  .c-hamburger__input:checked+.c-hamburger--htx span::after {
    bottom: 0;
    transform: rotate(-45deg)
  }

  .c-hamburger__input:checked+.c-hamburger--htx span::before,
  .c-hamburger__input:checked+.c-hamburger--htx span::after {
    transition-delay: 0s, 0.2s
  }

  .modal {
    opacity: 0;
    position: fixed;
    text-align: center;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: 1;
    transition: all .2s ease-in-out;
    visibility: hidden
  }

  .modal__content {
    background-color: #524f4c;
    height: 300px;
    width: 200px;
    margin: 20px auto
  }

  .modal a {
    color: #fff
  }

  .c-hamburger__input:checked~.modal {
    opacity: 1;
    background-color: #524f4c;
    visibility: visible
  }

  .c-hamburger__input:checked~.modal .item {
    font-weight: 700
  }

  .c-hamburger__input:checked~.modal .item a {
    transform: scale(1)
  }

  .c-hamburger__input:checked~.modal .item:nth-child(1) a {
    transition-delay: 0s
  }

  .c-hamburger__input:checked~.modal .item:nth-child(2) a {
    transition-delay: 0.035s
  }

  .c-hamburger__input:checked~.modal .item:nth-child(3) a {
    transition-delay: 0.075s
  }

  .c-hamburger__input:checked~.modal .item:nth-child(4) a {
    transition-delay: 0.0995s
  }

  .c-hamburger__input:checked~.c-hamburger span::after,
  .c-hamburger__input:checked~.c-hamburger span::before {
    background-color: #e98b3d
  }

  input#hamburger-menu {
    display: none
  }

  nav.nav .navigation {
    position: absolute;
    left: 0%;
    width: 100%;
    top: 50%;
    -webkit-transform: translatey(-50%);
    -moz-transform: translatey(-50%);
    -ms-transform: translatey(-50%);
    -o-transform: translatex(-50%);
    transform: translatey(-50%)
  }

  nav.nav ul,
  nav.nav li {
    margin: 0;
    padding: 0
  }

  nav.nav .item {
    text-transform: capitalize;
    font-size: 3em;
    list-style: none;
    font-weight: 400;
    margin: .4em 0;
    transition: all .2s ease-in-out
  }

  nav.nav .item a {
    display: block;
    height: 100px;
    line-height: 100px;
    transition: all 400ms cubic-bezier(0.17, 0.67, 0.38, 1.46);
    transform: scale(0.3)
  }

  .ns-box {
    position: fixed;
    background: rgba(42, 45, 50, 0.85);
    padding: 22px;
    line-height: 1.4;
    z-index: 1000;
    pointer-events: none;
    color: rgba(250, 251, 255, 0.95);
    font-size: 90%;
    font-weight: 600
  }

  .ns-box.ns-show {
    pointer-events: auto
  }

  .ns-box a {
    color: inherit;
    opacity: 0.7;
    font-weight: 700
  }

  .ns-box a:hover,
  .ns-box a:focus {
    opacity: 1
  }

  .ns-box p {
    margin: 0
  }

  .ns-box.ns-show,
  .ns-box.ns-visible {
    pointer-events: auto
  }

  .ns-close {
    width: 20px;
    height: 20px;
    position: absolute;
    right: 4px;
    top: 4px;
    overflow: hidden;
    text-indent: 100%;
    cursor: pointer;
    -webkit-backface-visibility: hidden;
    backface-visibility: hidden
  }

  .ns-close:hover,
  .ns-close:focus {
    outline: none
  }

  .ns-close::before,
  .ns-close::after {
    content: '';
    position: absolute;
    width: 3px;
    height: 60%;
    top: 50%;
    left: 50%;
    background: #6e6e6e
  }

  .ns-close:hover::before,
  .ns-close:hover::after {
    background: #fff
  }

  .ns-close::before {
    -webkit-transform: translate(-50%, -50%) rotate(45deg);
    transform: translate(-50%, -50%) rotate(45deg)
  }

  .ns-close::after {
    -webkit-transform: translate(-50%, -50%) rotate(-45deg);
    transform: translate(-50%, -50%) rotate(-45deg)
  }

  .ns-growl {
    top: 30px;
    left: 30px;
    max-width: 300px;
    border-radius: 5px
  }

  .ns-growl p {
    margin: 0;
    line-height: 1.3
  }

  [class^="ns-effect-"].ns-growl.ns-hide,
  [class*=" ns-effect-"].ns-growl.ns-hide {
    -webkit-animation-direction: reverse;
    animation-direction: reverse
  }

  .ns-effect-scale {
    background: #524f4c;
  }

  .ns-effect-scale a:hover,
  .ns-effect-scale a:focus {
    color: #fff
  }

  .ns-effect-scale .ns-close::before,
  .ns-effect-scale .ns-close::after {
    background: #fff
  }

  .ns-effect-scale .ns-close:hover::before,
  .ns-effect-scale .ns-close:hover::after {
    background: #000
  }

  .ns-effect-scale.ns-show,
  .ns-effect-scale.ns-hide {
    -webkit-animation-name: animscale;
    animation-name: animscale;
    -webkit-animation-duration: 0.25s;
    animation-duration: 0.25s
  }

  @-webkit-keyframes animscale {
    0% {
      opacity: 0;
      -webkit-transform: translate3d(0, 40px, 0) scale3d(0.1, 0.6, 1)
    }

    100% {
      opacity: 1;
      -webkit-transform: translate3d(0, 0, 0) scale3d(1, 1, 1)
    }
  }

  @keyframes animscale {
    0% {
      opacity: 0;
      -webkit-transform: translate3d(0, 40px, 0) scale3d(0.1, 0.6, 1);
      transform: translate3d(0, 40px, 0) scale3d(0.1, 0.6, 1)
    }

    100% {
      opacity: 1;
      -webkit-transform: translate3d(0, 0, 0) scale3d(1, 1, 1);
      transform: translate3d(0, 0, 0) scale3d(1, 1, 1)
    }
  }

  @media screen and (max-width: 25em) {
    .ns-growl {
      top: 10px;
      left: 10px;
      right: 10px;
      max-width: none
    }
  }

