@-moz-document domain("fa.wikipedia.org") {
  body,
  #content h1, #content h2,
  #content #firstHeading,
  #content .mw-editsection, .mw-body .mw-editsection-like,
  #content #toc h2, #content .toc h2,
  #template-documentation, #documentation-meta-data {
      font-family: iransans;
  }

  h1, h2, h3, h4, h5, h6 {
      font-weight: 800;
  }

  .mw-body-content p {
      line-height: 1.95em;
      margin: 0.5em 0;
      font-weight: 200;
      text-indent: 1em;
  }

  blockquote.templatequote p {
      font-weight: 500;
      color: #bcbdbf;
      border-right: 5px solid #bcbdbf;
      padding-right: 10px;
      border-radius: 3px;
  }
}
