style.css:
@-moz-document url-prefix("https://www.google.com") {
/*
 * Name:  SUSTech google search
 * Author:  Alan Chou
 * Version: v0.0.1
 */

/* Background */
html, body, #cnt, .rg_l, #botabar, #hdtbSum, #appbar, .sfbg{
    background: #f1f1f1 !important;
    color:#7b7b7b
}


/* set the search result page logo to baidu logo */
.logo{
    left: -150px;
    top: -10px;
    width: 101px;
    height:53px;
    background-image: url(https://eee.sustc.edu.cn/p/sunxiaowei/images/SUSTech.png);
    background-size: 35px, 33px;
    background-repeat: no-repeat;
    background-position: center;
    opacity: 0.7;
    filter: drop-shadow(1px 1px 2px black);
    transition: all 0.5s
}

.logo:hover{
    opacity: 1; 
    filter: drop-shadow(2px 2px 3px black);
}

.logo img{
    opacity: 0;
}

/* set the index page logo to baidu logo */
#lga{
    width:150px;
    background-image: url(https://eee.sustc.edu.cn/p/sunxiaowei/images/SUSTech.png);
    background-repeat: no-repeat;
    background-size: 120px, 120px;
    background-position: center;
    opacity: 0.85;
    filter: drop-shadow(1px 1px 2px black);
    transition: all 0.5s
}

#lga:hover{
    opacity: 1;
    filter: drop-shadow(2px 2px 3px black);
}

#lga img,#hplogo{
    opacity: 0;
}

/* set top useless items to none */
#hptl,.gb_d[data-pid="23"]{
    display: none;
}

.cF4V5c,.f9UGee,.match-mod-horizontal-padding,.di3YZe, .kno-rdesc{
    color : #2e2b2b;
}

.gb_cb, .gb_ya{
    opacity: 0.8;
    transition: all 0.5s;
}
.gb_cb:hover,.gb_ya:hover{
    opacity: 1.0;
    transform: scale(1.1);
}

.sfbg{
    padding-bottom: 10px;
    box-shadow: 2px 2px 1px #e3e3e3;
}

.rc, .cv2VAd, .kp-blk{
    background: #f7f7f7 !important;
    border-radius: 10px;
    border: 8px solid #f7f7f7;
    box-shadow: 2px 2px 2px 1px #e3e3e3;
    transition: all 0.5s;
}

.rc{
    box-shadow: 2px 2px 2px 2px #bbb;
}

.rc:hover{
    box-shadow: 3px 3px 3px 4px #828080b8;
}

.fbar{
    background: #f1f1f1 !important;
}

.RNNXgb{
    border-radius: 5px !important;
    box-shadow: 0 0 0 white;
    transition: all 0.5s;
}

a.NKcBbd{
  display:none;  
}

span.st em{
  background-color:#f033336e;  
}


.e2BEnf h3{ 
    color: black;
}

/* reset origin search result index */
#nav td{
    border-radius: 50%;
    background: #003f43;
    filter: drop-shadow(2px 2px 1px #003f43);

    transition: 0.5s;
}


#nav td:hover{
    transform: rotate(360deg) scale(1.1);
    box-shadow: 2px 2px 3px #003f43;
}



#nav .fl , #nav .cur, #nav .navend{
    width: 45px;
    height: 35px;
    padding-top: 10px;

    text-align: center;
    font-size: 20px;
    transition: all 0.5s;
}

#nav .cur{
    height: 15px !important;
    padding-right: 20px;
    background: #df5b18;
}

#nav .fl span, #nav .cur span, #nav .navend .csb {
    display: none;
}

#nav .navend span{
    margin-left: 10px !important; 
    margin-right: 10px !important; 
    color: #f1f1f1;
}

#nav .navend:first-child{
    display: none;
}

/* #2bb7b3 */
#nav .navend:last-child{
    background: #2bb7b3;
    box-shadow: 2px 2px 5px #003f43;
}

#footcnt{
    display: block;
}

/* set footer under ip location detection to none */
#fbar .B4GxFc #fsl, .FPdoLc center input, #fsl, #fsr, #prm{
  display:none;  
}
}
