style.css:
@-moz-document domain("www.nationstates.net") {
.rmbforum .rmbrow {
    display: flex;
    flex-flow: column;
    min-width: 240px;
    width: 75%;
    background: #f5f5f5;
    border-radius: 4px;
    box-shadow: 2px 8px 5px #000;
    margin: 0 0 25px;
}
.rmbforum .rmbrow.even {
    margin-left: auto;
}
.rmbforum .rmbmsg2 {
    border: 0px !important;
    margin-left: 0;
    color: #2b2b2b;
    padding: 0px;
}
.rmbforum .rmbrow.even a.nlink.nameblock span.nnameblock span.nname {
    font-weight: 600;
    font-size: 12px;
    margin: 0 0 4px;
    color: #3498db;
}
.rmbforum .rmbrow.odd a.nlink.nameblock span.nnameblock span.nname {
    font-weight: 600;
    font-size: 12px;
    margin: 0 0 4px;
    color: #2ecc71;
}

.rmbforum a.nlink.nameblock span.nnameblock span.ntype {
    display: none;
}
.rmbforum .rmbauthor2 {
    width: 100%;
    box-shadow: none;
    display: flex;
    flex-flow: row nowrap;
    justify-content: space-between;
}
.rmbforum .rmbauthor2 p:first-child {
    order: -1;
}
.rmbforum .rmbauthor2 .rmbedited {
    order: -1;
}
.rmbforum .rmbdate a,
.rmbforum .rmbdate a:hover {
    font-size: 12px;
    color: #999;
}
.rmbforum fieldset.rmbquoted {
    margin: 0px;
    border-radius: 0px;
    border: 0;
    border-left: 5px solid rgba(0, 0, 0, 0.75);
    color: #2b2b2b !important;
    background: rgba(0, 0, 0, 0.075) !important;
    padding: 10px 5px 5px
}
.rmbforum fieldset.rmbquoted legend {
    background: transparent !important;
    visibility: hidden;
    padding: 0;
}
.rmbforum fieldset.rmbquoted legend a {
    position: relative;
    top: 10px;
    visibility: visible;
}
.rmbforum a.rmbbutton {
    border-radius: 10px;
    bottom: 5px;
}
.rmbforum .rmbbuttons {
    margin-bottom: 0;
}
.rmbforum .rmbtable2 p:empty,
.rmbforum .rmbnewlabel {
    display: none;
}
.rmbforum p.rmblikers,
.rmbforum p.rmblikers a{
    color: #ff00cf;
}
.rmbforum p.rmblikers {
    opacity: 0.5;
}
.rmbforum .paginate {
    margin-top: 0;
}
}

@-moz-document url-prefix("https://www.nationstates.net/page=display_region_rmb"), url-prefix("https://www.nationstates.net/region=the_south_pacific/page=display_region_rmb") {
#content {
    background: url("http://forums.crackberry.com/attachments/blackberry-10-wallpapers-f308/137432d1361639896t-z10-wallpaper-set-z10-music.jpg");
}
h1 {
    font-size: 2.75em;
}
}
