style.css:
@-moz-document domain(facebook.com) {
.UIIntentionalStory_Content,.UIRecentActivity_Stream .UIRecentActivityStory
{
   border:solid transparent !important;
   border-width: 2px 1px 1px !important;
   padding:7px !important;
   -moz-border-radius: 7px !important;
}
.UIIntentionalStory_Close,.UIRecentActivity_Hide
{
   margin-top:3px !important;
}
.UIIntentionalStory:hover .UIIntentionalStory_Content,.UIRecentActivity_Stream .UIRecentActivityStory:hover 
{
   background:#f2f2f2 !important;
   border-color:#BDC7D8 !important;
}
}
