style.css:
@-moz-document url-prefix("https://docs.google.com"), url-prefix("http://docs.google.com") {
.kix-appview-editor, input { /*inner-secondary*/
    background: #45494F !Important;
}
::-webkit-scrollbar-thumb, ::-webkit-scrollbar-track {
    background-clip: padding-box;
    border-radius: 7px;
    border-style: solid;
    border-width: 3px;
}

::-webkit-scrollbar-track-piece {
    background-color: #2f3136;
    border: 3px solid #36393f;
    border-radius: 7px;
}
.docs-gm-dialogs .modal-dialog, .docs-gm-dialogs .modal-dialog-content, .docs-gm-dialogs #docs-offline-optinpromo-description, .docs-gm-dialogs #docs-offline-optinpromo-learn-more-containe {
	color: #eee;
}
/* new */
.docs-gm-sidebars .docos-anchoredreplyview .docos-anchoredreplyview-body {
	color: #eee;
}
.docs-gm-sidebars .docos-anchoredreplyview-header .docos-anchoredreplyview-author {
	color: #eee;
}
.docs-gm-sidebars .docos-anchoredreplyview-header .docos-anchoredreplyview-timestamp {
    color: #eee;
}
/* .suggestion-text-green {
    color: #fe019a !important;
} */
.docos.docos-gm-blue.docs-gm-sidebars .jfk-button.jfk-button-standard {
    color: #1a73e8 !important;
}
/* .suggest-changes-top-colorbar {
	border-top-color: #fe019a !important;
}
.suggest-changes-bottom-colorbar {
	border-bottom-color: #fe019a !important;
}
.suggest-changes-colorbar-arrow-up-ltr {
	border-bottom-color: #fe019a !important;
}
.suggest-changes-colorbar-arrow-down-ltr {
	border-top-color: #fe019a !important;
} */
.docos-anchoredreplyview .docos-replyview-resolve-button-plus-one, .docos-anchoredreplyview .docos-accept-suggestion, .docos-anchoredreplyview .docos-reject-suggestion {
	opacity: 0.8 !important;
}
.docos-replyview-resolve-button-original {
	opacity: 0.8 !important;
}
.apps-menuitem .goog-menuitem-label {
    color: #eee !important;
}
.docs-menu-hide-mnemonics .goog-menuitem-mnemonic-hint {
    color: #eee !important;
}
.docs-gm .goog-menu {
    background: #2F3136 !important;
}
#docs-instant-button-bubble.docs-instant-button-visible {
    background-color: #eee !important;
}
#docs-instant-button-bubble.docs-instant-button-visible:before {
    background-color: #eee !important;
}
.docos-icon-img {
    background: #eee !important;
}
.docos-icon-img:before {
    background: #eee !important;
}
.docs-gm .goog-menuseparator:before {
    display: inline-block;
    content: "";
    border-top: .1em solid #eee;
    width: 100%;
    margin-top: -20px !important;
    margin-left: -10px !important;
}
.docs-gm-dialogs .docs-slidingdialog {
    background-color: #202225 !important;
}
.docs-gm-dialogs .docs-findinput-container, .docs-gm-dialogs .docs-gotoinput-container {
	background-color: #36393F !important;
}
.docs-findinput-input, .docs-findinput-input:focus {
    color: #eee !important;
}
.docs-gm-dialogs .modal-dialog button, .docs-gm-dialogs .modal-dialog .jfk-button-standard {
    color: #1a73e8 !important;
}
.docs-gm-dialogs .modal-dialog .goog-buttonset-action[disabled], .docs-gm-dialogs .modal-dialog .jfk-button-action.jfk-button-disabled {
	color: #3c4043 !important;
}
.docs-title-input:focus {
	color: #eee;
}
.docos-anchoreddocoview.docos-docoview-active .docos-replyview-first .docos-show-less {
    background: #202225;
    color: #21fc !important;
}
.docs-gm-sidebars .docos-anchoreddocoview .docos-show-more, .docs-gm-sidebars .docos-anchoreddocoview .docos-show-less, .docs-gm-sidebars .docos-anchoreddocoview.docos-docoview-active .docos-show-more, .docs-gm-sidebars .docos-anchoreddocoview.docos-docoview-active .docos-show-less, .docs-gm-sidebars .docos-replyview-first .docos-show-more, .docs-gm-sidebars .docos-anchoreddocoview.docos-docoview-tesla-conflict:hover .docos-show-more {
	background: #202225;
	color: #21fc !important;
}
.docs-gm-sidebars .docs-dictionary-titlebar {
    background-color: #2F3136;
}
.docs-link-linksuggestion-url, .docs-link-linksuggestion-title, .docs-link-linksuggestion-url, .docs-gm-dialogs .docs-link-urlinput-url, .docs-link-linksuggestiongroup, .docs-link-insertlinkbubble-suggestionholder {
	background-color: #2F3136 !important;
	color: #CACCCD !important;
}
.docs-link-linksuggestion:hover, .docs-link-linksuggestion:focus, .docs-link-linksuggestion-text:hover {
	background-color: #333 !important;
}
.docs-link-insertlinkbubble-buttonbar {
	background-color: #202225 !important;
}
.docs-gm-dialogs .modal-dialog .goog-buttonset-action, .docs-gm-dialogs .modal-dialog .jfk-button-action {
	color: #eee !important;
}
.docos-anchoreddocoview-content {
    background-color: #202225;
}

.docs-gm .docs-menubar .goog-control {
	color: #eee !important;
}

.docs-gm-sidebars .docos-anchoredreplyview, .docs-gm-sidebars .docos-showrepliesbutton, .docs-gm-sidebars .docos-anchoreddocoview-input-pane {
	background-color: #202225;
}

.docos-showrepliesbutton-collapsed {
    color: #15c !important;
}

.docs-toolbar-zoom-combobox .goog-toolbar-combo-button-input, .docs-toolbar-zoom-combobox .goog-toolbar-combo-button-caption {
    background-color: #45494F !important;
    color: #CACCCD;
}

#fontSizeSelect .goog-toolbar-combo-button-input, #docs-font-size .goog-toolbar-combo-button-input, #fontSizeSelect .goog-toolbar-combo-button-caption, #docs-font-size .goog-toolbar-combo-button-caption {
    background-color: #45494F !important;
    color: #CACCCD;
}
/* new */

.link-management-url-container {
	border-color: #45494F;
}

.link-management-copy-link-button, .link-management-copy-link-button.jfk-button-disabled {
    background-color: #2F3136;
    border-color: #45494F;
    color: #CACCCD !important;
    background-image: none !important;
}

.link-management-copy-link-button:hover, .link-management-copy-link-button.jfk-button-disabled:hover {
    background-color: #45494F;
    border-color: #45494F;
    color: #CACCCD !important;
    background-image: none !important;
}

.toggle-link {
	background-color: #2F3136;
}

.link-management-select:hover {
	background-color: #45494F;
    border-color: #45494F;
    background-image: none !important;
}

.link-management-select {
	background-color: #2F3136;
    border-color: #45494F;
    background-image: none !important;
}

.docs-ruler {
background: #2F3136 !Important;
}

.docs-ruler-background-inner {
background: #2F3136 !Important;
}

::-webkit-scrollbar-thumb {
    background-color: #202225 !Important;
    border-color: transparent;
}
 /*inner*/
.kix-page, .kix-page-content-wrapper, .docos-input-textarea, .jfk-textinput {
    background: #F3EAD9 !Important;
    border-color: transparent !important;
/*     color: #CACCCD !Important; */
}

.kix-commentoverlayrenderer-normal, .kix-commentoverlayrenderer-highlighted { /*comment highlight*/
    background: #fe019a ;
    border-radius: 3px 0 0 3px;
    opacity: 0.2;
/*     box-shadow: -4px 0px 0px 0px #faa61a; */
}

.docs-material,
.docs-titlebar-buttons,
#docs-toolbar-wrapper,
.docos-streampane-header,
.docos-anchoredreplyview,
.docos-input,
.docs-revisions-sidebar-title,
.docs-revisions-chromecover-content-exclude-sidebar-width { /*menu*/
    background: #202225 !Important;
}

.goog-menuitem-highlight, .apps-navigationwidget-item-selected {
    background: #36393F !Important;
}
.goog-menuitem {
margin: 5px;
border-radius: 3px;
}

.docos-docoview-active {
background: transparent !Important;
}

.kix-lineview-background div {
background:transparent !Important;
}

/* .kix-cursor-caret {
border-color: white !Important;
} */

.docs-material .goog-toolbar-button, .docs-material .goog-toolbar-menu-button { /*buttons*/
background: transparent;
padding:5px;
border-radius: 3px;
}

.goog-menuitem-label, .goog-menuitem-mnemonic-hint {
color: #61646B !Important;
}

.goog-menuitem-highlight .goog-menuitem-content .goog-menuitem-label,
.goog-menuitem-highlight .goog-menuitem-content .goog-menuitem-mnemonic-hint {
color: white !Important;
}


.goog-toolbar-button-checked {
background-color: rgba(79,84,92,.6) !Important;
color: white;
}
.goog-toolbar-button-hover,
.goog-toolbar-menu-button-hover,
.goog-toolbar-menu-button-open,
.docs-revisions-tile-selected {
background-color: #36393F !Important;
color:white;
}

.app-switcher-button-hover .app-switcher-button-icon-container {
background-color: #36393F !Important;
border-radius: 3px;
}

.companion-app-switcher-container,
.docs-revisions-tile {
background: #202225;
}

.app-switcher-button {
	border-radius: 5px;
	margin:5px;
	background: #2F3136;
}

.docs-gm-dialogs .modal-dialog-title-text, .docs-gm-dialogs .docs-offline-optinpromo-title-text {
	color: #CACCCD;
}

.docs-gm-sidebars .docos-anchoreddocoview-internal, .docs-gm-sidebars .docos-anchoreddocoview, .docs-gm #docs-toolbar-wrapper, #kix-horizontal-ruler, #kix-vertical-ruler, .docs-gm .goog-toolbar-separator.goog-inline-block, .docs-gm-dialogs .docs-slidingdialog, .docs-gm .goog-menuseparator {
	border-color: #2F3136;
}

div {
	color: #eee !important;
/* 	border-color: #2F3136 !Important; */
}

div > span {
	border-color: #15c !important;
}

.kix-link span {
    background-color: #F3EAD9 !Important;
    color: #7289da !Important;
    text-decoration: none !important;
}

.goog-menu-vertical, /*Dialog*/
.apps-shortcutshelpcontentimpl-container,
.docs-dialog,
.modal-dialog,
.modal-dialog-content,
.modal-dialog-title,
.docs-revisions-sidebar-date-group,
.share-client-dialog {
	background: #2F3136 !Important;   
}

.goog-menu-vertical {
box-shadow: 1px 1px 4px #00000085 !Important;
}

body { /*Prevents loading flashbang*/
background: black;
}
}
