/* Fobaz Dekanser Yandex 0.1*/




:root {
    --color1: #f55b78;
    --color2: #c45b78;
    --color3: rgba(200, 50, 100, 0.2);
    --color4: rgba(0, 0, 0, 0.7);
    --color5: rgba(0, 0, 0, 0);
    --color6: rgb(225, 211, 255);
    --color7: black;
    --color8: rgb(157, 0, 100);
    --color9: rgba(0, 0, 0, 0.3);
    --color10: rgba(92, 96, 101, 0.17);
    --color11: rgba(200, 0, 50, 0.2);
    --filt2: hue-rotate(146deg)brightness(1.5)saturate(4)contrast(1) !important;
    --filt22: hue-rotate(130deg)brightness(1)saturate(7)opacity(0.4)contrast(1) !important;
    --dots: url('http://tweak.pro/img/profile_dots_rose.png');
    --brigther_font_color: #a4abb3;
}






.suggest2-item__text {
    white-space: nowrap;
    color: #f00; /* Цвет подсказок*/
}
.popup_theme_ffffff:before {
    background: black; /* Цвет фона подсказок*/
}
.suggest2 {
    border-width: 1px;
    border-style: solid;
    border-color: #000 #2400ff #1a00ff;
    background-color: #000;
    padding: 0;
}
.suggest2_theme_large:after {
    top: 10px;
    background-color: black; /* Цвет фона подсказок*/
}
.suggest2_search2_yes .suggest2__content:before {
    border-bottom: 12px solid black; /* какой то уебанский отступ 2*/
    background-color: transparent;
}
.suggest2_theme_large:not(.suggest2_kids):after {
    background-color: black; /* Цвет фона подсказок*/
    top: 11px;
