style.css:
@-moz-document domain("freefeed.net") {
body, .create-post, .post-editor, .posts, .box, .box-body, .p-my-groups, .single-notification, .notification-list, .post-body, .single-post, .expandable, .expoanded, .comment-body,.profile, .profile-controls, .tile-list-subheader, .p-pagination-controls, .attachments  {
background-color: rgb(0, 16, 24);
color: #f7f7f7;
}

.box-body, .box-footer { border-radius: 5px; }
.box-footer { text-align: center; }

li { padding-left: 8px; }
.post-like { padding-left: 0; }

.fold-comments-sticky { background-color: #f0f040; color: black; }

.comment-textarea { color: black; }

.comment-likes-list, .user-name-wrapper, .tile-list { background-color: rgb(0, 16, 24); }
.post.timeline-post.direct-post { background-color: rgb(16, 0, 24); }

bdi { color: red; }

.radio, .checkbox { color: black; }

.col-md-9 {
background-color: rgb(0, 16, 24);
}

h1, .site-logo, .site-logo-link, .name {
color: silver !important; border-radius: 5px;
}

h3, h2 { color: black; }

.expand-button {
background-color: rgb(0, 16, 24);
color: #ffffff;
}

span.Linkify span {
color: gold;
    background-color: rgb(0, 16, 24);
}

.expand-panel {
background-color: rgb(0, 16, 24);
}

span.Linkify {
color: gold;
    background-color: rgb(0, 16, 24);
}

.Linkify {
color: gray;
}

.col-md-3.sidebar {
color: #ffffff;
}

.box-header-friends, .box-header-groups {
color: #ffffff;
}

.p-home a {
color: #ffffff;
}

.p-direct-messages a {
color: #ffffff;
}

.p-my-discussions a {
color: #ffffff;
}

.p-best-of a {
color: #ffffff;
}

li.p-invites {
color: #ffffff;
}

li.p-invites a {
color: #ffffff;
}

div.box-footer a {
color: black;
}

.box-header-search {
color: #ffffff;
}

ul li a {
color: #ffffff;
}

.post-textarea {
color: rgb(0, 16, 24);
}

.search-button {
color: #3b179c;
}

.search-input {
color: rgb(0, 16, 24);
}

.post-edit-more-trigger {
color: #9e660b;
}

.user-name-wrapper a span {
color: lime;
}

.user div a {
color: #ffffff;
}

div.box-footer {
color: black;
background-color: silver;
}

.post-author span {
color: #5f5cfa;
}

.box-header-timeline { color: white; }

a.active {color: lime; }
a { color: red; }
a:hover { background-color: #fff040; }
.post-text a { color: #80FF00 !important; }
.info a { color: #80FF00 !important; }
.post-action, a.more-comments-link { color: #ff0080 !important; }
}
