style.css:
@-moz-document domain(random.org) {
body {
    background: #333333;
    color: #e6e6e6;
}
input {
    background: #333333;
    color: #e6e6e6;
}
h1 {
    background-image: url('data:image/png;base64,');
}
p[style*="background-color:#ddffdd"] {
    background-color: #344c34 !important;
}
#navigation {
    background-color: #333333;
    color: #66f
}
#navigation ul {
    border-left: 1px solid #999;
    border-bottom: 1px solid #999;
    border-right: 1px solid #999;
    background: #333333
}
#navigation ul {
    box-shadow: 3px 3px 3px #ccc
}
#navigation a:link,
#navigation a:visited {
    color: #66f;
    background-color: #333333
}
#navigation a:link.active,
#navigation a:visited.active,
#navigation a:hover {
    color: #e6e6e6;
    background: #333333
}
h1 {
    border-bottom: 1px #9393ff solid;
}
#invisible h2 {
    color: #6f6fc4;
    background-color: #333333
}
#invisible h3 {
    color: #6f6fc4;
    background-color: #333333
}
#invisible h4 {
    color: #6f6fc4;
    background-color: #333333
}
div.ruler {
    background: #9393ff;
    color: #e6e6e6
}
div.ruler2 {
    background: #9393ff;
    color: #e6e6e6;
}
div.rowl {
    border-right: 1px #9393ff solid
}
div.rowr {
    border-left: 1px #9393ff solid;
}
.codeblock {
    border: dotted thin #e6e6e6;
}
a:link {
    color: #6f6fc4;
}
a:hover {
    color: #e6e6e6;
}
a:visited {
    color: #6f6fc4
}
.smallgraph {
    background-color: #c1c185;
    color: #e6e6e6;
}
.submenu {
    border-right-color: #9393ff;
}
#invisible .submenu a:hover {
    background-color: #333333;
    color: #e6e6e6
}
.important {
    color: red;
    background-color: #333333
}
.infobox {
    border-color: #9393ff;
    background-color: #e3e3ff;
}
.alertbox {
    border-color: #ff9393;
    background-color: #ffe3e3;
}
#search {
    color: #9393ff;
    background: #333333;
}
.draw_public {
    border-color: #e6e6e6;
    color: #e6e6e6;
    background-color: #344034
}
.draw_private {
    border: thin solid;
    border-color: #e6e6e6;
    color: #e6e6e6;
    background-color: #ffc
}
.draw_semipublic {
    border: thin solid;
    border-color: #e6e6e6;
    color: #e6e6e6;
    background-color: #eef
}
.draw_pending {
    border: thin solid;
    border-color: #e6e6e6;
    color: #e6e6e6;
    background-color: #fee
}
.draw_public_text {
    color: green;
    background-color: #344034
}
.draw_private_text {
    color: #c84;
    background-color: #ffc
}
.draw_semipublic_text {
    color: #46d;
    background-color: #eef
}
.draw_pending_text {
    color: red;
    background-color: #fee
}
.draw_public_compact {
    background-color: #344034
}
.draw_private_compact {
    background-color: #ffc
}
.draw_semipublic_compact {
    background-color: #eef
}
.draw_pending_compact {
    background-color: #fee
}
div.maingraphic1 {
    background-color: #345834
}
div.maingraphic2 {
    background-color: #343458
}
abbr {
    border-bottom: 1px #e6e6e6 dotted
}#true-random-integer-generator {
	font-family: verdana, sans;
	font-size: 9pt;
	background: #333333;
	padding: 5px;
	margin: 0px;
	width: 148px;
	color: #777777;
	
		border: 1px solid #343458;}

#true-random-integer-generator-title {
	text-align: center;
	padding: 1px;
	display: block;
	background: #343458;
	color: #e6e6e6;
	margin: -6px;
	margin-bottom: 10px;
}


#true-random-integer-generator-min-span {
	display: block;
	margin-bottom: 5px;
}

#true-random-integer-generator-min-span label {
	color: #777777;
}

#true-random-integer-generator-min-span input {
	width: 77px;
	margin-left: 10px;
}

#true-random-integer-generator-max-span {
	display: block;
	margin-bottom: 5px;
}

#true-random-integer-generator-max-span label {
	color: #777777;
}

#true-random-integer-generator-max-span input {
	width: 77px;
	margin-left: 6px;
}

#true-random-integer-generator-button {
	display: block;
}

#true-random-integer-generator-result {
	display: block;
	padding: 2px;
	margin-bottom: 10px;
	color: #e6e6e6;
	background:  #343458;
	font-size: 11pt;
}

#true-random-integer-generator-credits {
	display: block;
	font-size: 6pt;
	text-align: right;
	padding: 1px;
}

#true-random-integer-generator-credits a {
	color: #777777;
}

#true-random-integer-generator-credits a:hover {
	color: #e6e6e6;
}}
