style.css:
@-moz-document regexp("https?://(www.)?wikiwand.*") {
nav, .collapse_arrow, .title_icon, .collapse_icon, .see_also_icon, .mwe-math-fallback-image-inline
{
	filter: invert(100%);
}

.ng-binding .ng-scope
{
	color: red !important;
	background-color: yellow;
}

.collapse_title
{
	border-bottom: 1px solid #c0c0c0 !important;
}

.diff .hljs-header, .hljs-comment, .hljs-javadoc, .hljs-template_comment
{
	color: #777;
	font-style: italic;
}

.hljs-phpdoc, .hljs-string, .hljs-tag .hljs-value, .tex .hljs-formula
{
	color: #d14;
}

.css .rule .hljs-keyword, .hljs-keyword, .hljs-request, .hljs-status, .hljs-subst, .hljs-winutils, .javascript .hljs-title, .nginx .hljs-title
{
	color: #e3b600;
	font-weight: 700;
}

.django .hljs-tag .hljs-keyword, .hljs-rules .hljs-property, .hljs-tag, .hljs-tag .hljs-title
{
	color: #65d5dd;
	font-weight: 400;
}

pre
{
	border: 1px solid #333;
}

th, td, body.light .caption
{
	color: #c0c0c0 !important;
}

div#content, body, .hljs, .h2
{
	color: #c0c0c0 !important;
	background-color: #262626 !important;
}

#article_content_wrapper.narrow_column
{
	max-width: 98%;
}

body.light .infobox, body.light table.ww_ib_1
{
	color: #888888 !important;
}

#wikiwand_toc
{
	background-color: #3a3a3a !important;
}

#main_menu.navbar_opaque
{
	background-color: #554e53 !important;
}

body:not(.dark) #header.fold #main_menu.navbar_opaque, .lang_btn .drop_down li.lang_pref a
{
	background-color: #544e52 !important;
}

#read_more a
{
	background-color: #0d0a0c !important;
}

#main_menu > li.toc_folded
{
	background-color: #544e52;
}

body.light #article_content_wrapper.padded, body.light #body_wrapp
{
	color: #e0e0e0 !important;
	background-color: #262626 !important;
}

#main_menu
{
	background-color: #544e52;
}

body.light a, body.light a:visited, a
{
	color: #ffaf50 !important;
}

body.light a:hover, .wiki_alert
{
	color: #ffffa0 !important;
}

body.light .wikiwand_footer, div.ocd-content, div.ocd-content div.title
{
	background-color: #262626 !important;
	color: #eeeeee !important;
}

body.light .mw-headline
{
	color: #ffc47f !important;
	font-weight: 500 !important;
}

div.ocd-content div.text, .footer_invite_wrapper .invite_box h4
{
	color: #aaaaaa !important;
}

body.light .tooltipster-ocd
{
	border-color: #ffaf50;
}

.wiki_alert
{
	background-color: #181818 !important;
}

body.light table, body.light td, body.light tr
{
	background-color: #333333 !important;
	border-color: #666666 !important;
}

body.light th
{
	background-color: #4a403a !important;
}

body.light .collapse .navbox-abovebelow, body.light .collapse .navbox-subgroup .navbox-title, body.light .collapse th.navbox-group
{
	background: #333 !important;
}

body.light .collapse .navbox, body.light .collapse .navbox-subgroup
{
	background: #333 !important;
}

body.light .collapse .navbox th, body.light .collapse .navbox-title
{
	background: #222 !important;
}

body.light .collapse .navbox-subgroup .navbox-abovebelow, body.light .collapse .navbox-subgroup .navbox-group, body.light .collapse .navbox-even, .drop_down > li > a
{
	background: #444 !important;
}

.lang_btn .drop_down li
{
	border-top: 1px solid #555;
}
}
