style.css:
@-moz-document domain(datascience.maths.unitn.it) {

* {
    color: #ececec;
}
a{
    color: #8585d7;
}
a:visited{
    color: #8585d7;
}
img{
    border-radius: 10px;
}
body {
    background-color: #343437;
}
#header.navbar {
    background: #484862;
}
.content {
    background: transparent;
}
.container {
    background: #37373c;
}
.btn-primary {
    
    background-color: #343a40;
    border-color: #343a40;
}
.btn-primary:hover {
    
    background-color: #484862;
    border-color: #343a40;
}
.form-control{
    background: #ffffffc2;
}
.form-control:focus{
    background: #ffffffcf;
    border-color: #09010173;
    box-shadow: 0 0 0 0.2rem rgba(141, 146, 151, .25);
}
.card{
    background: #464a51;
}
.navbar-expand{
    background: #484862;
}
.loader{
    border: 16px solid #c5c5d2;
    border-top: 16px solid #7878a2;
}
#current_solution{
    background:#37373c;
    border-width:1px;
    
}
.list-group-item{
    background: #dfdede;
    text-align: center;
}
.list-group-item:hover{
    background: #cecdcd;
}
.list-group-item.active{
   background: #484862;
    border-color: #09010173;
}
#header.fluid-row{
    background-color: #484862;
    
}
#media{
    margin-top: 5%;
    background: #484862;
    text-align: center;
}
hr{
    display: none;
}
#results_table {
    background-color: #dfdede;
    border-radius: 10px;
    text-align: center;
    color: black;
}
.table th{
    color: black;
}
.table td{
    color: black;
}
#plot{
    background: transparent;
    border: 0px;
}
pre{
    color: #ececec;
}
}
