*:not(:empty):not(a):not(b):not(option):not(select):not(img):not(header):not(section):not(.s3dmBox):not(.image):not(picture):not(button):not(p):not(td):not([class*="filetype"]):not(.Thumbnail):not(.thumbnail):not([class*="thumb"]):not([id*="image"]):not([id*="Image"]):not([style*='display: block; background-image:']):not(.gdtm), 
#page .bottom, #page .top {
