section:not([class*="fixed"]), #banner, .banner, .header, header, figure, picture {
  background-color: rgba(0,0,0,0.5) !important;
  background-blend-mode: darken !important}

section p, header p, #banner p, .banner p, .header p, figure p, picture p {
    background-color: rgba(0,0,0,0.3) !important}

 /*----- IMAGE CHANGES -----*/
img:not(#s3downbar_download_box_img), input[type="image"] {
    filter: brightness(var(--imgBrightness))}

/* This resets brightness on image hover. Comment out if you don't want it to apply */
img:not(#s3downbar_download_box_img), div[style*="background-image:"]:not(.download_data_box):not(#s3downbar_download_box_progress), input[type="image"] {
    transition: filter .2s}
