/*CATEGORY TAB COLOR STRIPES*/
 .J-KU-KO.aAy:before {
    border-radius: 0px;
    margin: 0px;
}
/*SEARCH BAR*/
 .gb_Se {
    background-color: white;
    border-radius: 0px !important;
