@-moz-document url-prefix("chrome-extension://clngdbkpkpeebahjckkjfobafhncgmne") {
*,::placeholder,.entry.usercss .style-name-link::after,.frame-url::before,.entry:nth-child(-n+10):before,.entry:nth-child(11):before {
    color: #fff!important
}

*,[style="box-shadow: rgb(221, 221, 221) 2px 2px 16px;"] {
    box-shadow: none!important
}

#backup-buttons .dropdown-content,#confirm>div,#filters .filter-selection:hover,#filters label:hover,#header,#help-popup,#hotkey-info div,#manage\.newUI\.sort,#message-box-buttons,#message-box-title,#message-box.center>div,#message-box.danger #message-box-title,#message-box>div,#notes,#search,#search-replace-dialog [data-type=status],#search-replace-dialog>*,#stylus-popup,.CodeMirror,.CodeMirror-gutters,.CodeMirror-lint-tooltip,.Codemirror-hints,.active #filters-stats,.colorpicker-input-container,.colorpicker-sliders,.entry.usercss .style-name-link::after,.menu-items-wrapper,.newUI .entry,.newUI .updater-icons>:not(.check-update):after,.search-result,.search-result-meta,body:not([id="stylus-options"]),button,div#message-box-title,input,input[type=checkbox]:not(.slider),select,#options,.entry:nth-child(even),.colorpicker-popup,#options-title,[style="background: repeating-linear-gradient(-45deg, rgb(245, 249, 255), rgb(245, 249, 255) 10px, rgb(255, 255, 255) 10px, rgb(255, 255, 255) 20px);"] {
    background: #000!important
}

.newUI .entry .style-name:hover:before,#toc li:hover,#import details:not([data-id="invalid"]) div:hover,#lint tr:hover,.entry .style-name::before,.CodeMirror-line>span>span::selection,.CodeMirror-line>span::selection,.CodeMirror-line::selection {
    background: #0000!important
}

#backup-buttons .dropdown-content a:hover,.CodeMirror-activeline,.CodeMirror-activeline-background,.CodeMirror-line::selection,.CodeMirror-line>span::selection,.CodeMirror-line>span>span::selection,.newUI .entry .style-name:hover:before,:selection {
    background: #2b2b2b!important
}

#backup-buttons .dropdown-content a:hover,.warnings {
    background-color: #333!important
}

.newUI .entry:hover,button:not(:disabled):hover,#hotkey-info mark {
    background: #333!important
}

#backup-buttons .dropdown-content,#confirm>div,#filters-stats,#help-popup,#manage\.newUI\.sort,#message-box-buttons button,#message-box.center>div,#message-box>div,#search,.CodeMirror,.CodeMirror-lint-tooltip,.Codemirror-hints,.active,.disabled h2::after,.entry.usercss .style-name-link::after,.menu-items-wrapper,.newUI .entry,.newUI .updater-icons>:not(.check-update):after,.search-result,.search-result:hover,body#stylus-options,button,button:not(:disabled),input:not([type]),input[type=checkbox]:not(.slider),input[type=number],select,.colorpicker-input,[style="border: 1px dashed rgb(64, 97, 167);"],input {
    border: 1px solid #fff!important
}

body#stylus-popup {
    width: -webkit-fill-available!important;
    border: 2px solid #fff!important;
    border-bottom: 3px solid #fff!important
}

.block {
    border-bottom: 1px solid #fff!important
}

#search-replace-dialog {
    border-radius: 3px!important
}

:focus,#options-title,#header,#search-replace-dialog,.applies-to {
    border-color: #fff!important
}

.CodeMirror-cursor {
    border-left: 1px solid #fff!important;
}

.filter-selection select,#header summary h2 {
    border-color: #000!important
}

.CodeMirror-focused,:focus {
    outline: none!important;
}

textarea:focus {
    outline: 1px solid #fff!important
}

#reset-filters,.newUI .entry:hover .svg-icon:hover,.svg-icon,.svg-icon.checked,.svg-icon.select-arrow,.svg-icon:hover {
    fill: #fff!important
}

body.dirty #save-button {
    filter: grayscale(100%) brightness(40%) sepia(100%) hue-rotate(-50deg) saturate(600%) contrast(.8)!important
}

.CodeMirror-selected {
    background: #d9d9d9!important
}

.CodeMirror-focused .CodeMirror-selected {
    background: #393939!important
}

.CodeMirror-line::selection,.CodeMirror-line>span::selection,.CodeMirror-line>span>span::selection {
    background: #5e5e5e!important
}

.CodeMirror-line::-moz-selection,.CodeMirror-line>span::-moz-selection,.CodeMirror-line>span>span::-moz-selection {
    background: #444!important
}

.CodeMirror-activeline-background {
    background: #313131!important
}

#manage-text>:not(:last-child):after {
    content: ""!important
}

#search-replace-dialog:not(:focus-within):not(:hover),#hotkey-info,.CodeMirror-foldgutter-folded::after,.CodeMirror-foldgutter-open::after,.set-update-url p,#search-replace-dialog [data-type="tally"] {
    opacity: 1!important
}

::-webkit-scrollbar,::-webkit-scrollbar:horizontal,::-webkit-scrollbar:vertical {
    width: 10px!important;
    height: 10px!important;
    background-color: #000!important
}

::-webkit-scrollbar-corner {
    background-color: #000!important
}

::-webkit-scrollbar-thumb {
    background-color: #fff!important;
    border-radius: 5px!important;
    border: 3px solid #000!important
}

::-webkit-scrollbar-track-piece {
    background-color: #000!important;
    box-shadow: inset 0 0 0 #fff!important;
    border: 0 solid #fff!important;
    border-radius: 5.5px!important
}

::-webkit-media-controls-enclosure {
    filter: invert(1)!important;
    border: 1px solid #000!important
}

#manage-text>span a,#find-styles-link,.write-style-link,#usw-url,.external-support,.external-link>div>ul>li a {
    text-decoration: none!important;
}

#manage-text>span a:hover,#find-styles-link:hover,.write-style-link:hover,#toc li:hover,#import details:not([data-id="invalid"]) div:hover,#lint tr:hover,#usw-url,.external-support,.external-link>div>ul>li a:hover {
    color: #54aaff!important;
}

#search-replace-dialog svg:hover,.svg-icon.edit:hover {
    fill: #54aaff!important;
}

.cm-searching {
    background: #fff!important;
    -webkit-text-fill-color: #000!important;
}

.newUI .entry .style-info[data-type=age] {
    display: none!important;
}

.cm-comment {
    color: #AEAEAE!important
}

.cm-builtin,.cm-qualifier,.cm-tag,.cm-variable-3 {
    color: #FF6400!important
}

.cm-meta,.cm-property,.cm-variable-2 {
    color: #79A6CE!important
}

.cm-atom,.cm-callee,.cm-number,.cm-string {
    color: #D8FA3C!important
}

.cm-keyword {
    color: #FBDE2D!important
}

.onoffswitch input {
    width: calc(100% + 9px);
    filter: invert(1) grayscale(1) brightness(2)!important;
}
}
