style.css:
@-moz-document regexp("(https?:)?//(www|space|live|game|search|t|h|message|account|manga).bilibili.com.*") {
/**************************************************************************
作者：某只正在看书的
这个是公益作品，原型是站内: “百度美化-少女前线G36”，站内叫@王比利
所有页面背景: 薯妈的女儿G36，赞美薯妈
**************************************************************************/
/*各种变量*/
:root{
    --appdisplay:/*[[app]]*/;
    --footdisplay:/*[[footer]]*/;
    --blurdisplay:/*[[blurbg]]*/;
    --bgcolor:/*[[bgcolor]]*/;/*背景颜色，背景图片无法加载的时候的背景*/
    --bgimg:url(/*[[uurl]]*/);
    --bgmode:/*[[bgmode]]*/;
    --textshadow:/*[[txtshadow]]*/;
    --textshadow2:/*[[txtshadow2]]*/;
    --boxbacknone:transparent;/*透明色*/
    --boxbackt:/*[[bb1]]*/19;/*元件几乎透明的背景色 */
    --boxback:/*[[bb1]]*/66;/*元件半透明的背景色 */
    --boxbacks:/*[[bb2]]*/d9;/*元件几乎不透明的背景色 */
    --bartxt:/*[[bartxt]]*/e6;/*进度条下方文字 */
    --barimg:/*[[barimg]]*/dd;/*进度条下方图标 */
    --barnew:/*[[barnew]]*/4d;/*进度条已经缓冲部分 */
    --bar:/*[[bar]]*/33;/*进度条 */
    --barold:/*[[barold]]*/;/*进度条已经播放部分*/
    --v1:cubic-bezier(0.215, 0.61, 0.355, 1);/*速度模式1*/
}
.bili-footer,
.international-footer,
.manga-footer,
.link-footer,.link-footer-ctnr{
    /*页面底端*/
    display:var(--footdisplay,none) !important;
}
ul li.shouji,
.mobile-link-l,
.nav-menu .nav-con .nav-item.mobile,
.download-app,
a[href="//app.bilibili.com"]{
    /*去掉下载APP按钮*/
    display:var(--appdisplay,none) !important;
}
.nav-menu .blur-bg{
    /*顶栏模糊*/
    display:var(--blurdisplay,none) !important;
}
/**/
html:not([stylus-iframe]) body{
    background: no-repeat fixed top right;
    background-image:var(--bgimg) !important; /*把背景设置为老婆*/
    background-size:var(--bgmode);
    background-color: var(--bgcolor,#D9D6E1);
    text-shadow: 0px 0px 5px var(--textshadow,#ffffff),0px 0px 8px var(--textshadow2,#eeeeee);
}/**/
/*

默认图片如下
这个是裁剪过的图片↓，保存在微博图床
http://wx2.sinaimg.cn/large/005DxcwUgy1g4u2r0rh1kj30j60sjgos.jpg

*/
html:not([stylus-iframe]){
    /*去掉一些奇怪的白框*/
    background-color: transparent !important;
}
.nav-menu{
    background-color: transparent !important;
}
/*
**
**
*/
.bili-header-m,
.international-header .mini-type{
    /*所有页面的顶栏都是半透明的了*/
    background-color: transparent;
}
.z_header div ul li,.z_header div ul li a,
.nav-menu .nav-con .nav-item .t,/*一般的顶栏*/
.nav-link .nav-link-ul .nav-link-item,/*动态页左*/
.nav-user-center .user-con>div/*动态页右*/{
    transition: all 0.2s !important;
}
.z_header div ul li:hover a,
.nav-menu .nav-con .nav-item:hover .t,
.nav-link .nav-link-ul .nav-link-item:hover,
.nav-user-center .user-con>div:hover{
    /*顶栏的一些简陋特效，有点小问题但是不想解决了*/
    padding-top: 4px;
}
/*
**
**
*/
#nav_searchform,
form.searchform{
    transition: 0.3s;
    opacity: 0.4;
    position:relative;
    overflow: hidden;
}
#nav_searchform:hover,
form.searchform:hover{
    /*搜索栏，屏幕最顶上一条白条真的难受*/
    opacity: 1;
}
#nav_searchform:after,
form.searchform:after{
    content: "";
    display: block;
    position: absolute;
    width: 100%;height: 100%;top: 0;left: 0;
    pointer-events: none;
}
#nav_searchform:hover:after,
form.searchform:hover:after{
    /*搜索栏动画特效*/
    animation:shuibo .7s linear 0s 1;
}
/*
**
**
*/
::-webkit-scrollbar{
    max-width:8px;
    min-height:5px;
    min-width:5px;
    background-color:#fff8;
    transition:all 0.3s ease;
}
::-webkit-scrollbar:hover{
    background-color:#eee8;
}
::-webkit-scrollbar-thumb{
    max-width:10px;
    min-height:5px;
    min-width:5px;
    transition:all 0.3s ease;
    background-color: #AAA8;
}
::-webkit-scrollbar-thumb:hover{
    max-width:10px;
    min-height:5px;
    min-width:5px;
    background-color: #7778;
}
::-webkit-scrollbar-thumb:active{
    background-color: #3338;
}
/*
**
**
*/
@keyframes entryl{/*从左侧进入*/
    0%  {opacity:0;margin-left:-20px;}
    100%{opacity:1;}
}
@keyframes entry{/*进入*/
    0%  {opacity:0;}
    100%{opacity:1;}
}
@keyframes entryd{/*延迟进入*/
    0%  {opacity:0;}
    50% {opacity:0;}
    100%{opacity:1;}
}
@keyframes entryld{/*从左侧延迟进入*/
    0%  {opacity:0;margin-left:-40px;}
    50% {opacity:0;}
    100%{opacity:1;}
}
@keyframes shuibo{/*水波特效*/
    0%  {
            background-image: radial-gradient(circle,#00000020 0%, #00000040 5%, transparent 5.1%);
            transform: scale(0.2);
            opacity: 1;
            background-repeat: no-repeat;
            background-position: 50%;
        }
    50% {
            transform: scale(20);
            opacity: 0.1;
        }
    100%{
            transform: scale(40);
            opacity: 0;
        }
}
/* @keyframes fromleft{
    0%  {left:0;opacity:0;}
    75% {opacity:1;}
    100%{width:100%;opacity:1;left:0;}
}
@keyframes toleft{
    0%  {left:0;opacity:1;}
    75% {opacity:0;}
    100%{width:0;opacity:0;left:0;}
}
@keyframes fromright{
    0%  {right:0;opacity:0;}
    75% {opacity:1;}
    100%{width:100%;opacity:1;right:0;}
}
@keyframes toright{
    0%  {right:0;opacity:1;}
    75% {opacity:0;}
    100%{width:0;opacity:0;right:0;}
} */
/*
**
**
*/
/*
**
**
*/
.gg-floor-module{
    opacity:.001 !important;/*广告嘛，总是需要被加载出来的嘛*/
    height:0 !important;
    width:0 !important;
    padding:0 !important;
    margin:0 !important;
    overflow:hidden !important;
}/*继续加载广告，但是它已经不需要再被看见了*/
/*
**
**
*/
}

@-moz-document regexp("(https?:)?//live.bilibili.com/[\\d]{3,}.*") {
/*
**直播间多于信息去除
**还是建议使用哔哩哔哩助手
*/
/*主要作用于聊天栏*/
/***************************************
**使用方法：不想去除该栏的话，就将注释**
**那一行最后面的斜杠去掉              **
**想继续去除该栏的话，就把斜杠加回来  **
**最好不要动最后的美化部分，不然会有一**
**些细节上的奇怪的视觉效果            **
****************************************
/
/*主要作用于聊天栏*/
/*去除上方栏*/
.aside-area .rank-list-section {
    display: none !important;
    position: absolute;
    top: 0px;
}
/*全区广播*/
.chat-item.system-msg,
/*老爷头衔*/
.chat-item.danmaku-item .vip-icon,
/*进入直播间的信息*/
.chat-item.welcome-guard,
.chat-item.welcome-msg,
#activity-welcome-area-vm,
/*礼物信息*/
.chat-item.gift-item,
/*新舰长/送礼物领奖*/
.draw-bingo-cntr,
/*送礼提示*/
#gift-animation-cntr,
.chat-history-panel .penury-gift-msg,
/*用户等级*/
.chat-item.danmaku-item .user-level-icon,
/*聊天底框*/
.chat-item.danmaku-item:before,
/*粉丝勋章*/
.chat-item.danmaku-item .fans-medal-item-ctnr,
/*活动头衔*/
.chat-item.danmaku-item .title-label,
/*舰长头衔*/
.chat-item .guard-icon,
/*榜首大哥*/
.rank-icon,
/*房管标志*
.chat-item.danmaku-item .admin-icon,
/*头衔*/
.live-title-icon/*注意：要禁用最后一行的话，额外必须去掉前一行的逗号*/
{
    display: none !important;
    margin-right: 0 !important;
}
/**/
/*别的地方的花里胡哨的东西*/
/*2233娘*/
.live-haruna-ctnr,
/*小时榜*/
.v-middle.hour-rank,
/*每周榜*/
.v-middle.week-star-rank,
/*热门榜*/
.hot-rank-text,
/*礼物图标*/
/*排行榜图标*/
.svg-icon.gift,
.svg-icon.ranking-big,
.icon-label[data-v-dafab962],
/*哔哩哔哩直播的logo*/
.bilibili-live-player-video-logo,
/*全区广播*/
.bilibili-live-player-video-operable-container/*注意：要禁用最后一行的话，额外必须去掉前一行的逗号*/
{
    display: none !important;
}
/**/
/*细节美化*/
.chat-history-panel .chat-history-list {
    height: 100% !important;
}
.live-room-app .app-content .app-body .player-and-aside-area .aside-area {
    overflow: hidden;
}
/**/
.aside-area .chat-history-panel {
    height: calc(100% - 145px) !important;
    padding-top: 10px;
}
/**/
.chat-item.danmaku-item {
    padding: 4px 5px !important;
    margin: 0 !important;
}
}

@-moz-document regexp("(https?:)?//www.bilibili.com/(bangumi|blackroom|read|audio|cheese|medialist)/(play/|ban/|[ac][vum]|activity)\\S*"), regexp("(https?:)?//www.bilibili.com/(video|blackboard)/.+"), regexp("(https?:)?//(space|t|h|live|manga).bilibili.com.+") {
/*
评论区通用
*/
.bb-comment .hot-line span {
    /*就是那个：以上为热门评论，查看更多*/
    background-color: transparent;
}
.common {
    background: var(--boxbackt, rgba(255, 255, 255, 0.1));
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, .05);
    padding: 8px;
    /*边框8*/
    margin-left: -10px;
    /*间隔10*/
    border-radius: 4px;
    width: 100%;
}
.tabs span.common {
    /*标签栏选中“评论”区时，tab里面有个span.common*/
    background: none;
    box-shadow: none;
    margin-left: 0;
    padding: 0;
}
.bb-comment {
    background: none;
    box-shadow: none;
    width: 100%;
}
.bb-comment .comment-list .list-item .reply-box .reply-item,
.reply-wrap {
    transition: all 0.3s;
    padding: 5px !important;
    /*边框5*/
    padding-bottom: 0 !important;
    border-radius: 2px;
}
.bb-comment .comment-list .list-item .reply-box .reply-item:hover,
.reply-wrap:hover {
    background: var(--boxback, rgba(255, 255, 255, 0.35));
    box-shadow: 0 0 5px 2px rgba(0, 0, 0, .1);
}
.bb-comment .comment-list .list-item .reply-box .reply-item .reply-con, .comment-bilibili-fold .comment-list .list-item .reply-box .reply-item .reply-con{
    width: calc(100% - 79px);
}
.comment-list .list-item .con {
    /*修正楼层分割线的位置*/
    margin-top: -5px;
}
.paging-box-big .page-jump {
    /*页面跳转的字体颜色*/
    color: rgb(100, 100, 100)
}
.bb-comment .comment-send-lite {
    background: var(--boxback, rgba(255, 255, 255, 0.1));
    opacity: .5;
    transition: all 0.3s;
}
.bb-comment .comment-send-lite:hover {
    background: var(--boxbacks, rgba(255, 255, 255, 0.85));
    opacity: 1;
}
/* 装饰品 */
.user .sailing {
    position: relative;
    min-height: 28px;
}
.sailing img.sailing-img {
    position: absolute;
    pointer-events: none;
}
}

@-moz-document regexp("(https?:)?//www.bilibili.com/(video|bangumi|blackboard|audio|medialist)/(play/|[acbB][vVu]|activity)\\S*") {
/*播放器的样式改变*/
.bui-bar-wrap {
    /*进度条底色*/
    background: var(--bar, #ffffff33) !important;
}
.bui-bar-wrap .bui-bar-buffer {
    /*进度条已缓冲底色*/
    background: var(--barnew, #ffffff4d) !important;
}
.bui-bar-wrap .bui-bar-normal {
    /*进度条已播放底色*/
    background: var(--barold, #00a1d6) !important;
}
.bilibili-player-video-btn {
    /*进度条下文字颜色*/
    fill: var(--barimg, #ffffffdd) !important;
}
.bilibili-player-video-time-wrap,
.bilibili-player-video-btn {
    /*进度条下按钮颜色*/
    color: var(--bartxt, #ffffffe6) !important;
}
#bilibili_pbp svg g {
    /*高能进度条透明度*/
    fill-opacity: /*[[gnt]]*/;
}
#bilibili_pbp svg g rect:first-child {
    /*高能进度条底色*/
    fill: /*[[gntds]]*/;
}
#bilibili_pbp svg g rect:nth-child(2) {
    /*高能进度条已播放底色*/
    fill: /*[[gntbf]]*/;
}
#bilibili_pbp svg g line {
    /*高能进度条线透明度*/
    opacity: /*[[gntx]]*/;
}
.bilibili-player-video-popup {
    /*不知道哪里冒出来的关注按钮*/
    display: /*[[popup]]*/
    !important;
}
.bilibili-player-video-wrap {
    /*去除不必要的文字发光*/
    text-shadow: none;
}
/**/
.bilibili-player.mode-fullscreen .bilibili-player-video-control-wrap,
.bilibili-player .bilibili-player-area .bilibili-player-video-wrap .bilibili-player-video-top {
    opacity: 0 !important;
    transition: all .4s var(--v1);
}
.bilibili-player.mode-fullscreen .bilibili-player-video-control-wrap:hover,
.bilibili-player .bilibili-player-area .bilibili-player-video-wrap .bilibili-player-video-top:hover {
    opacity: 1 !important;
}
}

@-moz-document regexp("(https?:)?//www.bilibili.com/?(?=$|v/|guochuang/|anime/|ranking/|documentary/|cinema/|watchlater/|movie/|tv/|\\?(spm_id_)?from).*") {
/*
作者：某只正在看书的
公益作品
*/
/****************************************************

新版主页的代码，实在不想改旧版的代码了

****************************************************/
/*顶部搜索框*/
.international-header .nav-search-box {
    transition: 0.3s;
    opacity: 0.4;
}
.international-header .nav-search-box:hover {
    opacity: 0.9;
}
#nav_searchform {
    overflow: hidden;
    position: relative;
    opacity: 1 !important;
}
/*顶部导航栏*/
.international-header {
    background: var(--boxback, rgba(255, 255, 255, 0.4));
}
.page-tab .con li,
.channel-menu-itnl .item,
.friendship-link .item {
    transition: all 0.3s;
    padding: 4px;
    border-radius: 2px;
    border: none;
}
.page-tab .con li:hover,
.channel-menu-itnl .item:hover,
.friendship-link .item:hover {
    background: var(--boxbacks, rgba(255, 255, 255, 0.85));
    box-shadow: 0 0 3px 1px rgba(0, 0, 0, .2);
}
.channel-menu-itnl .item .name {
    margin: 0 auto;
}
/*分区导航部分居中*/
/*主体部分*/
.b-wrap {
    /*宽一点总是没错的*/
    width: 1670px;
    margin: 0 auto;
}
.elevator {
    /*右侧栏需要再靠右一些才行*/
    margin-left: 853px;
}
@media screen and (max-width: 1870px) {
    .b-wrap {
        width: 1454px;
    }
}
@media screen and (max-width: 1654px) {
    .b-wrap {
        width: 1238px;
    }
    .b-wrap .elevator {
        margin-left: 625px;
    }
}
/*适配屏幕宽度*/
@media screen and (max-width: 1438px) {
    .b-wrap {
        width: 1030px;
    }
    .b-wrap .elevator {
        margin-left: 545px;
    }
}
/*适配屏幕宽度*/
.space-between {
    transition: background 0.4s, box-shadow 0.3s;
    background: var(--boxback, rgba(255, 255, 255, 0.4));
    box-shadow: 5px 5px 10px 5px rgba(0, 0, 0, .1);
    border-radius: 4px;
    box-sizing: border-box;
    padding: 15px 10px 15px 10px;
    /*左20，下10*/
    margin: 15px 0px 8px 0px;
    /*分区之间间隔15*/
    width: 100%;
    position: relative;
    animation: entryld 1.6s ease 0s 1;
}
.space-between:hover {
    background: var(--boxbacks, rgba(255, 255, 255, 0.85));
    box-shadow: 0 0 3px 1px rgba(0, 0, 0, .2);
}
#bili_report_spe_rec {
    height: 280px;
}
/*特别推荐*/
#bili_guochuang .guochuang,
#bili_anime .anime {
    width: 100%;
}
/*排行榜动画效果*/
.rank-wrap,
.pgc-rank-wrap,
.manga-rank-item,
.rank-wrap > a > p,
.rank-wrap .preview .txt > a > p,
.pgc-rank-wrap > a > p,
.pgc-rank-wrap .preview .txt > a > p,
.manga-rank-item > a > p,
.manga-rank-item .preview .preview-desc .title {
    transition: all 0.3s, border-bottom 0.5s;
}
.rank-wrap,
.pgc-rank-wrap,
.manga-rank-item {
    position: relative;
}
.manga-rank-item {
    padding-bottom: 0;
    margin-bottom: 9px;
}
.rank-wrap:hover,
.pgc-rank-wrap:hover,
.manga-rank-item:hover {
    background: var(--boxbacks, rgba(255, 255, 255, 0.85));
    /*box-shadow: 5px 5px 10px 10px rgba(255, 255, 255, .1);*/
    padding-left: 6px;
    margin-right: -6px;
    /*     margin-top:-2px; */
}
.rank-wrap > a:after,
.rank-wrap .preview .txt > a:after,
.pgc-rank-wrap > a:after,
.pgc-rank-wrap .preview .txt > a:after,
.manga-rank-item > a .title:after,
.manga-rank-item .preview .preview-desc .title:after {
    content: "";
    display: block;
    position: absolute;
    height: 100%;
    width: 0;
    top: 0;
    left: 100%;
    transition: all .45s var(--v1);
    opacity: .5;
    /*clear:both;*/
    border-bottom: 2px solid;
    /*直接画线会导致块变大qwq，所以提前画好，透明就看不见了*/
    /*animation:toright .75s ease 0s 1;视觉效果是底部线向右收缩*/
}
.rank-wrap:hover > a:after,
.rank-wrap:hover .preview .txt > a:after,
.pgc-rank-wrap:hover > a:after,
.pgc-rank-wrap:hover .preview .txt > a:after,
.manga-rank-item:hover > a .title:after,
.manga-rank-item:hover .preview .preview-desc .title:after {
    width: 100%;
    opacity: 1;
    left: 0;
    transition: all .45s var(--v1), left 0s;
    /*时间曲线来源于https://easings.net/#easeOutCubic*/
    /*animation:fromleft .75s ease 0s 1;视觉效果是从左出现底部线*/
}
.manga-rank-item:hover .other-link .update {
    /*     margin-top:4px;/*对齐底部线*/
    margin-right: 6px;
    /*容易挤到列表外*/
}
/*广告屏蔽*/
.banner-card {
    display: none !important;
}
.elevator-module .app-download/*下载客户端的按钮*/
{
    display: none !important;
}
/*感觉自己有点缺德*/
#home_popularize .adpos,
#reportFirst2 .gg-window > a {
    opacity: 1;
    background-image: url(https://s2.ax1x.com/2019/11/22/M7cC6g.jpg);
    background-size: cover;
}
/*保留广告链接、看不见，但是可以正常点击广告，也许这就是我最后一点良心了吧*/
.adpos .lazy-img,
.adpos a img,
.gg-window a img {
    opacity: 0;
}
.home-match .adpos a img,
#reportFirst3 .adpos a img {
    opacity: 1;
}
/*原图：来自于P站，
擅自上传到图床了
https://s2.ax1x.com/2019/05/16/EHwbTg.png
https://s2.ax1x.com/2019/11/22/M7cC6g.jpg <--这个来源于微博上薯妈的画
*/
/*右侧栏半透明*/
.elevator {
    transition: 0.3s;
    opacity: 0.4;
}
.elevator:hover {
    opacity: 0.9;
}
/****************************************************

旧版主页的代码，实在不想改了

****************************************************/
/*顶部搜索框*/
.bili-header-m .search .searchform,
.bili-header-m .search .link-ranking {
    background: none !important;
}
.bili-header-m .search {
    transition: all 0.4s;
    background: var(--boxback, rgba(255, 255, 255, 0.4));
    box-shadow: 0 0 10px 1px var(--boxback, rgba(255, 255, 255, 0.4));
}
.bili-header-m .search:hover {
    background: var(--boxbacks, rgba(255, 255, 255, 0.85));
