    box-shadow: 0 0 3px 1px rgba(0, 0, 0, .15);
}
/* 排行榜左侧栏 */
.sidebar-item .dp-i-block:not([class*="bg-no-repeat"]) {
    transition: all 0.3s;
}
.sidebar-item .dp-i-block:not([class*="bg-no-repeat"]):hover {
    margin-left: -10%;
    padding: 0 10%;
    background-color: var(--boxback, rgba(255, 255, 255, 0.4)) !important;
}
/* 详情页 */
.header-info.t-no-wrap,
/*漫画简介框*/
.manga-detail .section > .right-side > div,
/*右侧推荐与排行*/
.manga-detail .section > .section-list/*详细信息（章节列表之类）*/
{
    transition: all 0.4s;
    background-color: var(--boxback, rgba(255, 255, 255, 0.4)) !important;
    box-shadow: 3px 3px 6px 4px rgba(0, 0, 0, .1);
    border-radius: 4px !important;
}
.manga-detail .section > .right-side > div:hover,
.manga-detail .section > .section-list:hover {
    background-color: var(--boxbacks, rgba(255, 255, 255, 0.9)) !important;
    box-shadow: 0 0 3px 1px rgba(0, 0, 0, .15);
}
/* 右侧推荐 */
.recommendation .comic-info-panel {
    transition: all 0.4s;
    padding: 5px;
    margin-top: 6px;
}
.recommendation .comic-info-panel:hover {
    background-color: var(--boxbacks, rgba(255, 255, 255, 0.9)) !important;
    box-shadow: 0 0 3px 1px rgba(0, 0, 0, .15);
}
/* 更新推荐 */
.date-selector .date-item {
    /*顶部时间线*/
    transition: all 0.4s;
}
.date-selector .date-item:hover {
    margin-top: -5px;
    margin-bottom: -5px;
    padding-top: 5px;
    padding-bottom: 5px;
    background-color: var(--boxback, rgba(255, 255, 255, 0.4)) !important;
}
.schedule-list .data-list .data-item {
    /*     margin-top: -5px;margin-bottom: -5px; */
    /*     padding-top: 5px;padding-bottom: 5px; */
    transition: all 0.4s;
    /*     background-color:var(--boxback,rgba(255, 255, 255, 0.4)) !important; */
}
.schedule-list .data-list .data-item:hover {
    background-color: var(--boxback, rgba(255, 255, 255, 0.4)) !important;
}
/* 分类 */
.data-list .manga-card-vertical {
    padding: 5px;
    box-sizing: border-box;
    transition: all 0.4s;
}
.data-list .manga-card-vertical:hover {
    background-color: var(--boxback, rgba(255, 255, 255, 0.4)) !important;
    box-shadow: 0 0 3px 1px rgba(0, 0, 0, .15);
}
.style-section .tag-block .tag-containers .style-tag {
    transition: all 0.3s;
}
.style-section .tag-block .tag-containers .style-tag:hover {
    margin-top: -5px;
    margin-bottom: -5px;
    padding-top: 5px;
    padding-bottom: 5px;
    background-color: var(--boxback, rgba(255, 255, 255, 0.4)) !important;
}
}

@-moz-document domain("game.bilibili.com") {
/***********
 *   游戏  *
 ***********/
#mainly > .game {
    background-color: transparent;
}
#mainly > .game > .grid {
    transition: 0.4s ease;
    background-color: var(--boxback, rgba(255, 255, 255, 0.4));
    box-shadow: 3px 3px 6px 4px rgba(0, 0, 0, .1);
}
#mainly > .game > .grid:hover {
    background-color: var(--boxbacks, rgba(255, 255, 255, 0.9));
    box-shadow: 0 0 3px 1px rgba(0, 0, 0, .15);
}
}
