style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fandom.com") {
  
#WikiaBar, #WikiaNotifications {display:none!important;}
  .wds-global-navigation-wrapper, #globalNavigation {position:static!important;}
.article-with-rail {display:block!important;}
  #WikiaMainContent {width:100%!important;clear:both!important;}
#WikiaRailWrapper {display:block;position:relative!important;width:100%!important;}
  #WikiaRail {column-count:3;column-fill:balance;column-width:auto;}
  #WikiaRail > section, #WikiaRail > div {box-sizing:border-box!important;padding:10px!important;border-width:0px!important;display:inline-block;max-width:100%;}
  div#NATIVE_TABOOLA_RAIL, #RECIRCULATION_RAIL, #top-right-boxad-wrapper {display:none!important;}
  .wikia-ad {display:none!important;}
  body::after {content:none!important;}
  body::before {z-index:-2!important;}
  div.background-image-gradient {display:none!important;}
  div#WikiaArticle {font-size:100%!important;line-height:normal!important;min-height:0px;}
  div#WikiaMainContentContainer div[itemprop="video"]:first-child {display:none;}
  div.WikiaPageContentWrapper {box-shadow:0px 0px 0px 0px rgba(0, 0, 0, 0)!important;}
  nav#toc {background-color:rgba(255, 255, 255, 0.5);}
  body {padding-top:0%!important;}
  /* PAGE REORGANIZATION */
  header.wds-community-header {display:table-column!important;float:left;width:15%!important;height:auto;background-color:rgba(255, 255, 255, 0.9)!important;border-top-style:solid;border-right-style:solid;border-bottom-style:solid;border-top-width:1px;border-right-width:1px;border-bottom-width:1px;border-color:#d0d0d0;box-shadow:10px 10px 5px 3px rgba(0, 0, 0, 0.3);background-image:none!important;}
  section#WikiaPage {display:block;float:right;width:80%!important;border-style:solid;border-width:1px;border-color:#d0d0d0;box-shadow:10px 10px 5px 3px rgba(0, 0, 0, 0.3);z-index:-1!important;}
div.WikiaSiteWrapper {width:100%!important;max-width:100%!important;min-width:100%!important;}
div.WikiaPage div.WikiaPageContentWrapper {display:block;width:80%;float:right;}
  footer.wds-global-footer {display:table-row!important;float:right;width:100%;}
  header.wds-community-header>div {display:table-cell!important;}
  header.wds-community-header>nav {display:table-cell!important;}
  nav.wds-community-header__local-navigation>ul {display:table-cell!important;}
  .wds-community-header nav div.wds-dropdown__content {display:block;position:static;width:14vw;background-color:rgba(0,0,0,0)!important;}
  nav .wds-dropdown .wds-dropdown__content, nav .wds-list li, nav .wds-list li a, nav .wds-list li a span {border:0px;background-color:transparent;font-size:8pt;line-height:1;}
  nav .wds-dropdown .wds-dropdown__content ul.wds-list li {height:auto;padding:2px 0px 2px 0px!important;}
  nav .wds-dropdown .wds-dropdown__content ul.wds-list li a {color:black!important;white-space:normal;}
  nav.wds-community-header__local-navigation .wds-dropdown:hover {max-width:14vw;}
  nav.wds-community-header__local-navigation li.wds-tabs__tab:hover {max-width:14vw;}
  nav.wds-community-header__local-navigation div.wds-dropdown-level-2__content {width:200px!important;background-color:white!important;}
  nav.wds-community-header__local-navigation div.wds-dropdown-level-2__content ul {max-width:200px!important;}
  nav.wds-community-header__local-navigation div.wds-dropdown-level-2__content ul li {max-width:200px!important;}
  nav.wds-community-header__local-navigation div.wds-dropdown-level-2__content ul li a {max-width:200px!important;}
  .wds-community-header__sitename a, .wds-community-header__counter span, .wds-tabs__tab-label a, .wds-tabs__tab-label span {color:black!important;}
  .wds-community-header__counter {float:left;max-width:90%;}
  ul.wds-list>li a {padding:0px 0px 0px 0px!important;}
  div#global-navigation-user-sign-out button {padding:0px 0px 0px 0px!important;height:auto;}
  .wds-community-header__sitename {max-width:90%!important;display:block!important;float:left;}
  .wds-community-header__sitename a {text-shadow:3px 3px 3px rgba(0, 0, 0, 0.3);white-space:normal;}
  header.wds-community-header div.wds-community-header__top-container {max-width:15vw!important;display:block!important;width:100%;height:auto;}
  header.wds-community-header a.wds-button {max-height:1em;}
  header.wds-community-header div.wds-button-group {float:left!important;width:90%;margin-bottom:10px;align-items:none;display:block!important;}
  header.wds-community-header div.wds-button-group a.wds-button {position:static;display:block!important;float:left!important;clear:right;}
  header.wds-community-header div.wds-button-group div.wds-dropdown {position:static;display:block!important;float:left!important;clear:both;width:100%;margin-top:5px;}
  header.wds-community-header div.wds-button-group div.wds-dropdown div.wds-button {display:none;}
  header.wds-community-header div.wds-button-group div.wds-dropdown div.wds-dropdown__content {position:static;display:contents;float:left!important;clear:both;}
  header.wds-community-header div.wds-button-group div.wds-dropdown div.wds-dropdown__content:hover {background-color:rgba(0,0,0,0);border-width:0px;padding:0px;width:100%;}
  header.wds-community-header div.wds-button-group div.wds-dropdown div.wds-dropdown__content ul li {margin-left:0px;height:auto;padding:2px 0px 2px 0px!important;}
  header.wds-community-header div.wds-button-group div.wds-dropdown div.wds-dropdown__content ul li a {height:auto;font-size:8pt;line-height:1;font-weight:bold;color:black;}
  header.wds-community-header div.wds-button-group div.wds-dropdown div.wds-dropdown__content ul li a:hover {color:black;}
  .wds-dropdown__toggle-chevron {display:none!important;visibility:hidden!important;height:0px!important;width:0px!important;opacity:0!important;}
  nav.wds-community-header__local-navigation div.wds-tabs__tab-label svg {display:none!important;visibility:hidden!important;height:0px!important;width:0px!important;opacity:0!important;fill:rgba(0,0,0,0)!important;}
  .wds-dropdown::after,.wds-dropdown::before {content:none!important;display:none!important;}
  nav.wds-community-header__local-navigation {height:auto!important;}
  ul[id*="Autocomplete"] li {font-size:10pt;height:auto;line-height:0.8;}
  ul[id*="Autocomplete"] li a {font-size:10pt;height:auto;line-height:0.8;}
  ul[id*="Autocomplete"] li a strong {font-size:10pt;height:auto;line-height:0.8;}
  h2.has-icon {max-height:32px!important;overflow-y:scroll;}
  h2.has-icon svg {fill:rgba(255,255,255,0)!important;max-height:16px!important;max-width:16px!important;}
  div#mixed-content-footer {height:350px;overflow:auto;}
  div.wds-fandom-content-well {width:100%;}
  div.post-details-desktop {display:table-column!important;float:right;width:83%;border-top-right-radius:0px;border-bottom-right-radius:0px;border-style:solid;border-width:1px;border-color:#d0d0d0;box-shadow:10px 10px 5px 3px rgba(0, 0, 0, 0.3);z-index:-1!important;}
  div.post-details-desktop div.post-details-desktop__main {width:70%;display:table-column!important;float:left;}
  div.wds-fandom-content-well aside.post-details-desktop__right {display:table-column!important;float:right;width:30%;}
  div.wds-fandom-content-well div.home-page-desktop {width:83%;float:right;}
  /* MINOR STYLE FIXES */
  span.headnote {margin-top:0px!important;}
  div#catlinks {background-color:rgba(0,0,0,0);}
  /* STYLE CHANGES */
  nav.WikiaArticleInterlang {border-style:solid;border-width:1px;border-color:rgba(0,0,0,0.3);border-radius:3px;background-color:#f8f8f8;padding-bottom:4px;}
  div.category-page__members {column-count:4!important;column-width:25%!important;}
  nav#articleCategories {border-style:solid!important;border-width:1px!important;border-color:rgba(0,0,0,0.3)!important;background-color:#f8f8f8;}

}
