style.css:
@-moz-document domain("oj.dcs.upd.edu.ph") {
body, .case-info, .user-points {
    color: #f0f0f0 !important;
}

h1, h2, h3, h4, h5, h6 {
    color: gray !important;
}

.tabs h2 {
    color: #f0f0f0 !important;
}

.content-description h4 {
    border-bottom: 1px solid #555 !important;
}

span {
    color: #ddd;
}

body, #page-container {
    background: #1e1e1e !important;
    border: none !important;
}

.problem-title h2 {
    color: white !important;
}

i:not(.solved-problem-color).fa:not(.fa-rss) {
    color: gray !important;
}

.alert {
    border: none;
}

.alert-warning {
    background: #ffe393;
}

.sidebox-content {
    border: none;
    box-shadow: 0 10px 20px rgba(0, 0, 0, .4);
}

.content-description pre {
    color: #ccc;
    background-color: #111;
    border: none;
    border-radius: 8px;
    box-shadow: 0 7.5px 10px rgba(0, 0, 0, .3);
}

.content-description code {
    background-color: #111 !important;
    color: inherit !important;
    border: 1px solid #444;
}

.btn-clipboard {
    color: #f0f0f0;
    background: #333;
    border: none;
}

a,
a:hover,
a:active {
    color: #64a8d5;
}

.tabs .tab a {
    color: #f0f0f0 !important;
}

.tabs .tab.active a {
    background: #111 !important;
    border-top-color: #f0f0f0 !important;
}

hr {
    color: rgba(0,0,0,0) !important;
    border-bottom: 1px solid #444 !important;
}

tr {
    background: none !important;
}

tr:not(.case-row) td {
    border: 1px solid #444 !important;
}

#contest-info {
    background: rgba(0, 0, 0, 0.85);
    border: none !important;
    padding: 15px 25px;
    border-radius: 8px;
    box-shadow: 0 10px 20px rgba(0, 0, 0, .4);
}

input[type="text"],
input[type="search"],
input[type="password"] {
    background: #141414;
    border: none;
    color: inherit;
}

input[type="text"]::placeholder,
input[type="search"]::placeholder {
    color: #eee !important;
}

[class^="select2"] {
    background-color: #141414 !important;
    border: none !important;
    border-radius: 8px !important;
}

.noUi-target {
    background: transparent;
}

.noUi-connect {
    background: #777;
}

.contest-tag {
    background-color: #111 !important;
}

.contest-tag > a {
    color: #999 !important;
}

.sub-result .state {
    color: black !important;
}

.sub-result span {
    color: inherit !important;
}

.sub-result.AC {
    background: #2ecc71 !important;
}

.sub-result.RTE {
    background: #f09e1d !important;
}

.case-row {
    display: block;
    padding-bottom: 4px;
}

.case-row  span.case-AC {
    display: inline-block;
    color: #2ecc71;
    animation: jump 0.6s infinite;
}

.case-row span.case-AC::after {
    content: '❤️';
    display: inline-block;
    margin-left: 8px;
    animation: beat 0.6s infinite;
}

.case-row span[class^="case-"]:not(.case-AC)::after {
    content: 'but u can do it fam ❤️';
    display: inline-block;
    margin-left: 12px;
    animation: beat 0.6s infinite;
}

@keyframes jump {
    33.3%  { transform: translateY(-4px); }
}

@keyframes beat {
    50%  { transform: translateY(-4px) scale(1.1); }
}

footer {
    background: #111 !important;
    border-top: none !important;
}

#footer-content a {
    margin-right: 10px;
}

#footer-content form {
    margin-left: 10px;
}

#footer-content form select {
    border: none;
    outline: none;
    background: #444;
    color: #d7d7d7;
}

#problem_submit {
    background: none;
    border: none;
}

.django-ace-toolbar {
    background: #444 !important;
    border: none;
}

.ace-github {
    color: #f0f0f0 !important;
    background: #111 !important;
}

.ace_gutter {
    color: #999 !important;
    background: #333 !important;
}

.ace_text-input {
    background: red !important;
}

#login-panel .form-area {
    background: #111;
    border: none;
    border-radius: 8px;
    box-shadow: 0 10px 20px rgba(0, 0, 0, .4);
}

