style.css:
@-moz-document domain("yandex.com"), domain("yandex.ru") {
/***** MAIN CHANGES *****/

* {
  background-color: transparent !important;
  color: #999 !important;
  border-color: #444 !important}
*, *:focus {outline: none !important}
html {background: /* url("http://abload.de/img/sony_experience_flow_doqi8.jpg") */ #333 bottom fixed no-repeat !important;
background-size: cover !important}

::-moz-selection {background-color: rgba(45,45,45,.6) !important; color: #08ADCF !important}
::selection {background-color: rgba(45,45,45,.6) !important; color: #08ADCF !important}

h1, h2, h3, h4, h5, h6, h1 *, h2 *, h3 *, em, .serp-block__head, .other-sizes__title, .related2__title, .slideshow-controls__title, .MMSidebar-SectionTitle, .MMSidebar-SectionHeader, .CbirItem-Title {color: #DDD !important}
.button_teaser_card .button__label {color: #999 !important}
.button_teaser_card:hover .button__label, .dropdown_applied_yes button *, .check-button_theme_light.check-button_checked_yes *, .collections-menu__menu-item_cur .link__inner, .collections-menu-list__item_cur {color: #FFF !important}

nothing {color: #444 !important; text-shadow: 0 1px #000 !important}

/*** RED ***/
.under-menu button:hover *, .under-menu .check-button.check-button_theme_light:hover *, .under-menu .link:hover, .under-menu .link:hover * {color: #C00 !important; text-shadow: none !important}
/*** GREEN ***/
[class$="_cur"] .tabs-head__link {color: #AC0 !important}
/*** YELLOW ***/
.a, .cite, cite, cite a, cite b, .other-sites__outer-link, .polaroid2__link, .snippet2__link-text, .other-sites__snippet-site-link {color: #ECEA00 !important}
/*** PINK ***/
.ya-login_card-teaser-source .ya-login__name {color: #E8287E !important}
/*** ORANGE ***/
.dot-list_teaser-source .link, .Path .Link * {color: #F80 !important}
/*** BLUE ***/
.other-sites__title-link, .snippet2__title, .suggest2-item:hover * {color: #08ADCF !important}
/*** GREY ***/
.other-sites__snippet-desc {color: #7A7A7A !important}
/*** DARK GREY ***/
.cl-layout__desc__date, .comment__date, .cbir-other-sizes__column-title {color: #666 !important}
/*** FONT STYLE & SHADDOW ***/
nothing {font-weight: bold !important}
nothing {text-shadow: 0 1px rgba(0,0,0,0.5) !important}
nothing {text-shadow: 0 1px #000 !important}
nothing {text-shadow: 0 0 2px #000 !important}
.other-sizes__column-title {color: #BBB !important; text-shadow: 0 0 4px rgba(255, 255, 255, 0.3) !important}
nothing {border-bottom: 1px dotted #444 !important}
nothing {border-style: dashed !important}

nothing:hover {border-color: #FA0 !important}
nothing {border-color: #F80 !important}


a:hover, a:hover *, .link:hover, .Path .Link:hover * {
  color: #FFF !important;
  text-shadow: 0 0 6px rgba(255,255,200,0.8) !important}
nothing:hover {text-shadow: none !important; color: #999 !important}
menuitem:hover {
  color: #BBB !important;
  background: rgba(50,50,50,0.6) !important;
  box-shadow: inset 0 0 3px rgba(0,0,0,0.5) !important}
menuitem-disabled {opacity: 0.3 !important}

.tags__tag {background: rgba(255,255,255,.1) !important;}

nothing {border-bottom: 1px dotted #666 !important}
nothing a:visited * {color: #777 !important}
nothing img {transition: border-color, opacity, 0.4s}

.pane2_fullscreen_no .snippet2__title::after, .polaroid2__title::after, .similar-offers-item__title::after, .snippet2__text::after, .cl-teaser_card .cl-teaser__article-text::before, .tabs-head_more .tabs-head__wrap::before {background: none !important}
nothing {background-image: none !important}
nothing {border: none !important}
nothing {border-color: transparent !important}
.pane2__column > .pane2__direct-wrapper_type_stripe.blocks-grid__item.blocks-grid__item_static {display: none !important}


.logo {
  filter: invert(100%) hue-rotate(180deg) saturate(300%) !important}
.MMIcon,
.icon_type_cbir,
.cbir-intent__close, .cbir-panel__close-icon, .complain2_icon-theme_outlined.complain2_color_black.complain2_layout_button .button2__icon, .complain2_icon-theme_outlined.complain2_color_black.complain2_layout_icon .button2__icon, .disk-logo__icon.disk-logo__icon_theme_gray, .head-filter_type_family .head-filter__icon, .head-filter_type_moderate .head-filter__icon, .head-filter_type_unlimited .head-filter__icon, .icon_type_share.icon_color_black, .pane2__close_color_white .pane2__close-icon, .popup_filter_unlimited .popup__content-icon,
.icon_type_input-cbir, .icon_type_user, .icon_type_arrow, .input_theme_websearch2 .input__clear,
.button2 .button2__icon, a.button_icon-only, button.button_icon-only, .button__icon, .Icon.Icon_tone_black,
.button_card-action_check .button__label::before,
.cl-teaser__imageholder {
  filter: invert(100%) !important}


/***** BUTTONS & CO *****/

button, input, textarea, select {-moz-appearance: none !important; -webkit-appearance: none !important}

input[type="submit"], input[type="button"], input[type="checkbox"], input[type="radio"], button, a[role="button"], a.button2, a.Button2, .serp-header button.websearch-button {
  background: rgba(35,35,35,.7) !important;
  border: 1px solid #292929 !important;
  box-shadow: 0 0 5px #000 !important;
  border-radius: 0 !important;
  color: #999 !important;
  text-shadow: 0 1px #000 !important;
  transition: 0.3s !important;}
button::before, .button2_theme_action.button2::before, .input_theme_normal .input__box::before, a.button2:before, a.Button2:before {background: none !important;}
button.button2_theme_clear, button.button2_theme_clear:hover, button.button_theme_light, button.button_theme_light:hover, .check-button_theme_pseudo::before, button.button_icon-only, button.button_icon-only:hover {background: none !important; border: none !important; box-shadow: none !important;}
input[type="submit"]:hover, input[type="button"]:hover, input[type="checkbox"]:hover, input[type="radio"]:hover, button:hover, a[role="button"]:hover, a.button2:hover, a.Button2:hover, .serp-header button.websearch-button:hover {
  box-shadow: 0 0 5px #666 !important;
  color: #CCC !important;
  text-shadow: 0 1px #000 !important}
input[type="submit"]:hover *, input[type="button"]:hover *, input[type="checkbox"]:hover *, input[type="radio"]:hover *, button:hover *, a[role="button"]:hover *, a.button2:hover *, a.Button2:hover *, .serp-header button.websearch-button:hover {
  color: #CCC !important;
  text-shadow: 0 1px #000 !important}

select button {
  box-shadow: 0 0 12px #000 !important;
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAFCAYAAACJmvbYAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAadEVYdFNvZnR3YXJlAFBhaW50Lk5FVCB2My41LjEwMPRyoQAAADRJREFUGFdjmDlz5n8gYEDHIHEGdnb2/+gKQHyQOFgHsgKYBEgcbhxMAYiGWYFiF7IESAEA1TZZ1AUcPVYAAAAASUVORK5CYII=) rgba(35,35,35,.7) no-repeat center !important;
  padding-right: 5px !important}

input[type="checkbox"], input[type="radio"] {min-width: 6px; min-height: 6px}
input[type="checkbox"]:active:hover, input[type="radio"]:active:hover {
  background-color: rgba(35,35,35,.7) !important;
  border: 1px solid #292929 !important}
input[type="checkbox"]:checked, input[type="radio"]:checked {color: #AC0 !important}


@media screen and (-webkit-min-device-pixel-ratio:0) {
input[type="checkbox"] {margin: 3px 3px 2px 4px !important}
input[type="radio"] {margin: 3px 6px 2px 4px !important}
input[type="checkbox"]:checked, input[type="radio"]:checked {border-color: #F80 !important}
select {
  padding-right: 12px !important;
  background: url(http://www.abload.de/img/dropkupg.png) rgba(35,35,35,.7) no-repeat !important;
  background-position: right center !important}
}

input:disabled, button:disabled, select:disabled {
  color: #666 !important;
  opacity: 0.6 !important;
  cursor: default !important}

textarea, select, input, .serp-header .search2__input .input_theme_websearch2, .CbirOcr-Content {
  background: rgba(35,35,35,.7) !important;
  border: 2px solid !important;
  border-color: rgba(200,200,200,.1) rgba(200,200,200,.11) !important;
  border-radius: 0 !important;
  box-shadow: inset 0 0 3px #000 !important;
  transition: 0.4s}
textarea:hover, select:hover, input:hover, .serp-header .search2__input .input_theme_websearch2:hover {border-color: rgba(200,200,200,.2) !important}
textarea:focus, select:focus, input:focus, .serp-header .search2__input .input_theme_websearch2.input_focused_yes {border-color: rgba(200,200,200,.3) !important}

option, optgroup {
  background: #222 !important;
  color: #666 !important}


/***** MENUS, POPUPS & CO *****/

.pane2__paranja, .paranja_theme_dark, .cl-layout.cl-layout_show-suggest {background-color: rgba(0,0,0,.8) !important}
.pane2_disabled-info-column .pane2__info-wrapper::after {background-color: rgba(30,30,30,.8) !important}
.modal_theme_normal.modal_visible_yes {background: radial-gradient(circle, rgba(10,10,10,1) 10%, rgba(20,20,20,.3)) !important}

.popup2_theme_normal::before, .kebab .popup-menu__main {
  background-color: rgba(35, 35, 35, .97) !important;
  border: 2px solid !important;
  border-color: rgba(200, 200, 200, .1) !important;
  box-shadow: 1px 3px 8px #000 !important}
.popup_theme_ffffff {
  background-clip: padding-box !important;
  border: 3px solid !important;
  border-color: rgba(52,52,52,248) !important;
  box-shadow: 1px 3px 8px #000 !important}
.popup__content {background: #222 !important}

.popup2_theme_normal .popup2__tail, .popup_theme_ffffff .popup__tail {background-image: linear-gradient(to top right, rgba(52,52,52,248) 50%,transparent 50%) !important}
.popup2_theme_normal .popup2__tail::after, .popup_theme_ffffff .popup__tail::after {
  background-image: linear-gradient(to top right,rgba(35, 35, 35, .97) 50%,transparent 50%) !important;
  bottom: 3px !important;
  left: 3px !important}
.popup_theme_ffffff .popup__tail::after, .cbir-no-results__tail::before {background: linear-gradient(to top right,#222 50%,transparent 50%) !important}


.modal__content {
  background: rgba(35, 35, 35, .97) !important;
  border: 8px solid rgba(99,99,99,0.5) !important;
  background-clip: padding-box !important;
  box-shadow: 0 0 9px #000, inset 0 0 2px #C0C0C0 !important}


/***** HEADER *****/

.serp-header_slide-auto-hide .serp-header__wrapper, .serp-header_slide-auto-hide .serp-header__under, .serp-header__wrapper, .serp-header .navigation {
  background: rgba(30,30,30,0.9) !important}
.serp-header_slide-auto-hide .header__slide, .header__slide > .under-menu {
  background: rgba(50,50,50,0.9) !important;
  box-shadow: inset 0 0 5px #000 !important}
.header__slide {
  padding-left: 0 !important}
.header__slide > .under-menu {
  height: 40px !important;
  padding-left: 116px !important}
.serp-header_slide-hidden_yes .header__slide {
  opacity: 0 !important}

.cbir-panel {background: #333 !important;}
.cbir-panel__wrap form:hover, .cbir-panel__wrap_hover_yes .cbir-panel__dragzone, .cbir-panel__close:hover {background: #444 !important;}


.serp-header .search2 *, .suggest2_theme_large::before {background-color: transparent !important; border: none !important; box-shadow: none !important}
.websearch-button__text::before, .websearch-button__text::after, .suggest2_theme_large::after {background: none !important}
.serp-header:not(.serp-header_kids) .search2__input::after, .suggest2_search2_yes .suggest2__content::before {border: none !important}
.serp-header .search2__input .input_theme_websearch2 {padding-left: 8px !important}
.suggest2_theme_large {background: linear-gradient(rgba(30,30,30,0), rgba(30,30,30,1) 8px) !important;}


/***** BOXES *****/

.original-image, .other-sizes, ul.similar__thumbs, .cbir-no-results, .MediaViewer-LayoutContainer {
  background: rgba(30,30,30,.9) !important;
  border: none !important;
  box-shadow: 0 0 8px rgba(0,0,0,.6) !important}
.polaroid2__content, .pane2__column_type_main, .cl-teaser_card .cl-teaser__inner, .cl-layout__wrap__i, .serp-item .serp-item__link {background: #222 !important;}
.pane2__column_type_info, .cl-layout__side, .MediaViewer-LayoutSideblock {background: #333 !important;}

.serp-item__preview {box-shadow: 0 0 1px rgba(255,255,255,.3) !important;}
.serp-item__preview:hover {box-shadow: none !important}
.cl-teaser__wrap {box-shadow: 0 0 8px rgba(0,0,0,.6) !important}



/***** FOOTER *****/

.footer {
  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAIAAAAmkwkpAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAadEVYdFNvZnR3YXJlAFBhaW50Lk5FVCB2My41LjEwMPRyoQAAAB1JREFUGFdjEIQBNTU1BggbyAKSIA6EBeLAWUAOAGysBC0kLJbtAAAAAElFTkSuQmCC") !important;
  border-color: #2C2C2C !important;
  box-shadow: inset 0 0 9px #000 !important}


/* other */

.serp-item__meta, a .serp-item__meta, .other-sites__meta {
  background: rgba(255,255,255,.4) !important;
  box-shadow: 0 0 1px rgba(0,0,0,.5) !important;
  color: #000 !important;
  text-shadow: 0 0 2px #FFF !important}
a .serp-item__meta * {color: #000 !important;}
.similar__more-link {background: rgba(0,0,0,.5) !important;}
.similar__more-title {color: #DDD !important;}


.radiobox__radio_color_red .radiobox__text {
 color:red !important;
 border:1px solid #c61f00 !important;
 background-color:red !important;
}
.radiobox__radio_color_orange .radiobox__text {
 color:orange !important;
 border:1px solid #e5b71f !important;
 background-color:orange !important;
}
.radiobox__radio_color_yellow .radiobox__text {
 color:#ffd600 !important;
 border:1px solid #e5dd56 !important;
 background-color:#ffd600 !important;
}
.radiobox__radio_color_cyan .radiobox__text {
 color:#68cdff !important;
 border:1px solid #00c7f2 !important;
 background-color:#68cdff !important;
}
.radiobox__radio_color_green .radiobox__text {
 color:#6dc065 !important;
 border:1px solid #5bcb00 !important;
 background-color:#6dc065 !important;
}
.radiobox__radio_color_blue .radiobox__text {
 color:#2a4fd7 !important;
 border:1px solid #0056e5 !important;
 background-color:#2a4fd7 !important;
}
.radiobox__radio_color_violet .radiobox__text {
 color:#a51bae !important;
 border:1px solid #9100e5 !important;
 background-color:#a51bae !important;
}
.radiobox__radio_color_brown .radiobox__text {
 color:#7e5000 !important;
 border:1px solid #714800 !important;
 background-color:#7e5000 !important;
}
.radiobox__radio_color_white .radiobox__text {
 color:#fff !important;
 border:1px solid #d9d9d9 !important;
 background-color:#fff !important;
}
.radiobox__radio_color_black .radiobox__text {
 color:#000 !important;
 border:1px solid #000 !important;
 background-color:#000 !important;
}

.Tags.Tags_type_simple, .CbirOcr-ControlsScroller {padding: 5px !important}


/***** IMAGE CHANGES *****/

.advanced-search_light_yes .dropdown_applied_yes:not(:hover) .button__text::after {
  background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="8" height="5"><path fill="white" d="M8 .7L7.3 0 4 3.3.7 0 0 .7l4 4 4-4z"/></svg>') !important;
  background-position: 0 0 !important}

.tooltip_view_classic.tooltip_tone_default.tooltip_theme_promo.popup2_theme_normal .popup2__tail {
  background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 22 10"><path d="M20 10H0c5 0 7.949-3.872 10-9 2.051 5.128 5 9 10 9z" fill="black"/></svg>') !important;
}

.spin2 {
  border-color: transparent !important;
  width: 100px !important;
  height: 100px !important;
  background: url(data:image/svg+xml;base64,) !important}
}
