style.css:
@-moz-document url(http://www.phoenixjp.net) {
*,
::after,
::before {
	box-sizing: border-box;
}

body {
	font-family: helvetica, arial, verdana, sans-serif;
	background-color: #666666;
}

div.titre {
	background-color: inherit;
}

div.titre span.logo a img {
	display: none;
}
div.titre span.logo a:before {
	content: "PJP";
	color: #BFBFBF;
	font-size: 3em;
	vertical-align: middle;
}

div.titre span.menu_site a img {
	display: none;
}
div.titre span.menu_site a:before {
	color: #BFBFBF;
	display: inline-block;
	background-color: #666666;
	border: 1px solid #010101;
	min-width: 50px;
	padding: 5px;
	text-align: center;
	transition: all .3s;
	box-shadow: 0 1px 5px rgba(12, 13, 14, 0.3);
}
div.titre span.menu_site a:hover:before {
	color: #000000;
	background-color: #BFBFBF;
}
div.titre span.menu_site a.options:nth-child(1):before {
	content: "Mobile";
}
div.titre span.menu_site a.options:nth-child(2):before {
	content: "FR";
}
div.titre span.menu_site a.options:nth-child(3):before {
	content: "US";
}
div.titre span.menu_site a.options:nth-child(4):before {
	content: "Perso";
}
div.titre span.menu_site a.options:nth-child(5):before {
	content: "Facebook";
}
div.titre span.menu_site a.options:nth-child(6):before {
	content: "Twitter";
}
div.titre span.menu_site a.options:nth-child(7):before {
	content: "Insta";
}
div.titre span.menu_site a.options:nth-child(8):before {
	content: "Gurushots";
}
div.titre span.menu_site a.options:nth-child(9):before {
	content: "RSS";
}
div.titre span.menu_site a.options:nth-child(10):before {
	content: "Leetchi";
}

div.titre span.menu_site a.generalbox {
	color: #BFBFBF;
}

div.entete {
	border-bottom: 0;
}
div.entete div.generalbox {
	color: #BFBFBF;
}

div#bordermenunews {
	box-shadow: none;
}

div.menuprincipal {
	background-color: #BFBFBF;
	border: 0;
	color: #000000;
}
div#menunews {
	box-shadow: none;
}

a.suboptions:link,
a.suboptions:visited {
	color: #666666;
}

div#presentation,
div#presentation.normal,
div#presentation.fixed {
	height: auto !important;
	padding: 10px;
	margin: 0;
	margin-bottom: 70px;
}
#presentation.fixed {
	margin-top: 70px !important;
}

div#presentation div.lefttitle {
	border-bottom: none;
	float: none;
	width: auto;
	margin: 0;
	padding-bottom: 10px;
	color: #BFBFBF;
}

div#presentation div.bordergeneral {
	padding: 0;
}
div#presentationdiv.bordergeneral table.tableauGeneral {
	border-collapse: collapse;
}

div#presentation div.bordergeneral table.tableauGeneral td.tableauGeneralLeft {
	padding: 0;
}

div#presentation div.bordergeneral table.tableauGeneral div.generalContent {
	padding: 10px;
	box-shadow: none;
}

div#presentation div.bordergeneral table.tableauGeneral div.generalContent div.newsUrl {
	padding: 0;
}

div#presentation table.allnewsTab {
	width: 100%;
}

div#presentation table.allnewsTab a {
	color: inherit;
}
div#presentation table.allnewsTab a:hover {
	text-decoration: underline;
}
div#presentation table.allnewsTab a:visited {
	color: #666666;
}

div#presentation table.allnewsTab tr:hover td {
	background-color: #808080;
	color: #BFBFBF !important;
}

div#presentation table.allnewsTab td.allnewsTabTd {
	padding: 5px 10px;
	border-bottom: 1px solid #808080;
}
div#presentation table.allnewsTab td.allnewsTabTd:nth-child(1) {
	width: 130px;
}
div#presentation table.allnewsTab td.allnewsTabTd:nth-child(3) {
	padding: 5px 0;
	width: 15px;
}
div#presentation table.allnewsTab td.allnewsTabTd:nth-child(4) {
	width: 180px;
}
div#presentation table.allnewsTab td.allnewsTabTd:nth-child(2),
div#presentation table.allnewsTab td.allnewsTabTd:nth-child(5),
div#presentation table.allnewsTab td:last-of-type {
	display: none;
}

div#presentation table.allnewsTab td.allnewsTabTd:nth-child(6) {
	padding: 0;
}
div#presentation table.allnewsTab td.allnewsTabTd:nth-child(6) a {
	width: 100%;
	height: 100%;
	display: block;
	padding: 5px 10px;
}


br {
	display: none;
}

center {
	padding-top: 15px;
	background-color: #BFBFBF;
}

div.copyright {
	padding-bottom: 10px;
	background-color: #BFBFBF;
}
div.copyright br {
	display: inline;
}

div.statistiques {
	display: none;
}
}
