style.css:
@-moz-document domain("phonandroid.com")
{
    body,
    .featured-grid,
    .container,
    #header_ad,
    .single-post-container {
        background: rgb(25, 25, 35);
        color: #ddd;
    }

    #header_ad {
        background: rgb(25, 25, 35) !important;
    }

    #header {
        background: rgba(45, 45, 55);
    }

    .menu-area .accordion li .toggle-container {
        background: rgba(65, 65, 75);
    }

    #onesignal-popover-container {pointer-events: none;}

    #onesignal-popover-container.slide-down #onesignal-popover-dialog {pointer-events: all;}

    #onesignal-popover-container.slide-down #onesignal-popover-dialog,
    #onesignal-popover-container #onesignal-popover-dialog .popover-button.secondary {
        background: rgba(65, 65, 75) !important;
    }

    #onesignal-popover-container #onesignal-popover-dialog .popover-body-message {
        color: #fff !important;
    }

    /* Style for the side bar */
    /* - Search bar */
    .widget-newsletter input[type=search] {
        background: rgb(45, 45, 55);
        color: #fff;
        border: none;
    }
    .widget-newsletter input[type=search]:focus {
        background: rgb(65, 65, 75);
        color: #fff;
        outline: 0;
    }
    /* - Trending */
    .widget-trend-categories {
        background: rgb(45, 45, 55);
    }

    /* Style for reading an article */
    /* - Share buttons */
    .share-box {
        background: rgb(25, 25, 35);
    }

    .share-box ul li a[data-href^="#comments"]:before,
    .share-box ul li a[href^="#comments"]:before,
    .share-box ul li button[data-href^="#comments"]:before,
    .share-box ul li button[href^="#comments"]:before {
        border-color: rgb(25, 25, 35) transparent transparent;
    }

    /* - Title into text */
    .text-block h1,
    .text-block h2,
    .text-block h3,
    .text-block h4,
    .text-block h5,
    .text-block h6 {
        color: #fff;
    }

    /* - Content */
    .wp-caption {
        background: rgb(45, 45, 55);
        border-color: rgb(45, 45, 55);
    }

    /* - Reactions block */
    #taboola-below-main-column .trc_rbox_header .heading-text,
    #taboola-below-main-column .trc_rbox_header .trc_rbox_header_span,
    .comment-section .heading-text,
    .comment-section .trc_rbox_header_span,
    .crp_related .heading-text,
    .crp_related .trc_rbox_header_span,
    .yarpp-related-widget .heading-text,
    .yarpp-related-widget .trc_rbox_header_span {
        color: rgba(255, 255, 255, .7);
    }
    .comment-section .plugin-holder:before {
        background: linear-gradient(rgba(255, 255, 255, 0), rgb(25, 25, 35));
    }
    .widget-newsletter.widget-mode-expanded {
        background: rgb(45, 45, 55);
    }

    /* - Bon plans/Dossiers */
    .fx-toc .fx-toc-title,
    .product-widget {
        background: rgb(45, 45, 55);
    }

    /* - Polls */
    .fx-toc {
        background: rgb(65, 65, 75);
    }
    .fx-toc .fx-toc-list li a {
        color: #ccc;
    }
    .fx-toc .fx-toc-list li a:hover {
        color: #fff;
    }
    #wrapper .weblator-poll-container .panel-default > .panel-heading {
        border: none;
        background: rgb(45, 45, 55);
    }
    .weblator-poll-container .panel-default {
        border-color: rgba(255, 255, 255, .1);
        background: rgb(65, 65, 75);
    }
    .weblator-poll-container {
        color: #fff;
    }
    .weblator-poll-container .panel-footer {
        background: rgb(55, 55, 65);
        border-color: rgba(255, 255, 255, .2);
    }
    .weblator-poll-container .btn-default {
        color: #fff;
        background: rgba(255, 255, 255, .1);
    }
    #wrapper .weblator-poll-container .btn {
        box-shadow: inset 0 -3px 0 rgba(0, 0, 0, .3);
    }

    /* - Table */
    .text-block .mce-item-table tbody tr:nth-child(odd) td,
    .text-block .mce-item-table tbody tr:nth-child(odd) th,
    .text-block table tbody tr:nth-child(odd) td,
    .text-block table tbody tr:nth-child(odd) th {
        background: rgb(55, 55, 65);
        border-color: rgba(255, 255, 255, .2);
    }
    .tablepress .odd td {
        background: rgb(45, 45, 55);
        border-color: rgba(255, 255, 255, .2);
    }
    .tablepress .row-hover tr:hover td {
        background: rgba(255, 255, 255, .2);
    }
    .text-block .tablepress tfoot th {
        background: rgba(255, 255, 255, .3);
        border-color: rgba(255, 255, 255, .2);
    }
    .product-test,
    #header-resume-test,
    #header-resume-test .head,
    #header-resume-test:after,
    #header-resume-test:before {
        background: rgb(45, 45, 55);
        color: #ddd;
    }




    /* Style for the home page */
    /* - Header */
    #sub-nav {
        background: rgb(55, 55, 65);
    }
    #sub-nav ul li a {
        color: #fff;
    }

    .h2 a,
    h2 a {
        color: #fff;
    }
    .post {
        border-color: rgba(255, 255, 255, .2);
    }
    .post-list .post h2 + p,
    .post-section .post h2 + p {
        color: #eee;
    }
    .post .post-metas {
        color: rgba(255, 255, 255, .5);
    }

    /* Grid post (at the bottom) */
    .grid-posts .title {
        color: #fff;
    }
    .bonsplans-container a.button-inverted {
        border-color: #fff;
        color: #fff;
    }
    .folder-container {
        background: rgb(45, 45, 55);
    }
}

