        background: rgb(25, 25, 35);
        color: #eee;
    }

    .tab-conversation.active > a {
        color: #fff !important;
    }
    .nav-tab > a {
        color: #ddd;
    }
    .newsletter-box {
        background: rgba(255, 255, 255, .2);
    }
}
